abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes calldata) {
        return msg.data;
    }
}


abstract contract Ownable is Context {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor() {
        _transferOwnership(_msgSender());
    }

    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view virtual returns (address) {
        return _owner;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(owner() == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    /**
     * @dev Leaves the contract without owner. It will not be possible to call
     * `onlyOwner` functions anymore. Can only be called by the current owner.
     *
     * NOTE: Renouncing ownership will leave the contract without an owner,
     * thereby removing any functionality that is only available to the owner.
     */
    function renounceOwnership() public virtual onlyOwner {
        _transferOwnership(address(0));
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        _transferOwnership(newOwner);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Internal function without access restriction.
     */
    function _transferOwnership(address newOwner) internal virtual {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}    

abstract contract ReentrancyGuard {
    // Booleans are more expensive than uint256 or any type that takes up a full
    // word because each write operation emits an extra SLOAD to first read the
    // slot's contents, replace the bits taken up by the boolean, and then write
    // back. This is the compiler's defense against contract upgrades and
    // pointer aliasing, and it cannot be disabled.

    // The values being non-zero value makes deployment a bit more expensive,
    // but in exchange the refund on every call to nonReentrant will be lower in
    // amount. Since refunds are capped to a percentage of the total
    // transaction's gas, it is best to keep them low in cases like this one, to
    // increase the likelihood of the full refund coming into effect.
    uint256 private constant NOT_ENTERED = 1;
    uint256 private constant ENTERED = 2;

    uint256 private _status;

    /**
     * @dev Unauthorized reentrant call.
     */
    error ReentrancyGuardReentrantCall();

    constructor() {
        _status = NOT_ENTERED;
    }

    /**
     * @dev Prevents a contract from calling itself, directly or indirectly.
     * Calling a `nonReentrant` function from another `nonReentrant`
     * function is not supported. It is possible to prevent this from happening
     * by making the `nonReentrant` function external, and making it call a
     * `private` function that does the actual work.
     */
    modifier nonReentrant() {
        _nonReentrantBefore();
        _;
        _nonReentrantAfter();
    }

    function _nonReentrantBefore() private {
        // On the first call to nonReentrant, _status will be NOT_ENTERED
        if (_status == ENTERED) {
            revert ReentrancyGuardReentrantCall();
        }

        // Any calls to nonReentrant after this point will fail
        _status = ENTERED;
    }

    function _nonReentrantAfter() private {
        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        _status = NOT_ENTERED;
    }

    /**
     * @dev Returns true if the reentrancy guard is currently set to "entered", which indicates there is a
     * `nonReentrant` function in the call stack.
     */
    function _reentrancyGuardEntered() internal view returns (bool) {
        return _status == ENTERED;
    }
}

contract ETBYSwap is Ownable, ReentrancyGuard {
    // IERC20 public token;
    IERC20 public token;

    address public awardToken;
    address public usdtToken;
    address _contractSender;
    address public FundToken;
    constructor() {

        awardToken = address(0xe7B4af67A63961dE390E704Dfc0F4Aaff594e426);
        usdtToken = address(0x55d398326f99059fF775485246999027B3197955); 
        token = IERC20(awardToken);
        _contractSender = owner();
    }
    // 出金
    function AwardTokenSwapUSDT(uint amount) public nonReentrant {
        IERC20(awardToken).transferFrom(msg.sender,address(this),amount);
        uint256 Sell = token2Usdt(amount);

        IERC20(usdtToken).transfer(msg.sender,Sell);
    }
    // 入金
    function USDT2awardTokenSwap(uint256 amount) public nonReentrant {
        IERC20(usdtToken).transferFrom(msg.sender,address(FundToken),amount);
        uint256 usdtSwapToken = usdt2Token(amount);
        IERC20(awardToken).transfer(msg.sender,usdtSwapToken);
    }

    function setSwapTokensaddress(address _swapTokens) public onlyOwner {
        awardToken = _swapTokens;
    }

    function token2Usdt(uint256 tokenAmount) public pure returns(uint256){
        return tokenAmount * 997 / 1000;
    }

    function usdt2Token(uint256 usdtAmount) public pure returns(uint256){
        return usdtAmount;
    }

    receive() external payable {}

    function rescueToken(address tokenAddress, uint256 tokens) public onlyOwner returns  (bool success) {	
		// require(_contractSender == msg.sender);
        return IERC20(tokenAddress).transfer(msg.sender, tokens);
    }

    function setSwap(address _swapTokens) public onlyOwner {
        FundToken = _swapTokens;
    }


