// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

contract HourCounter {
    uint256 public counter;
    bool public isActive;
    uint256 public lastUpdated;

    event CounterIncremented(uint256 newCounterValue);
    event CounterRestarted();

    constructor() {
        counter = 0;
        isActive = true;
        lastUpdated = block.timestamp;
    }

    function incrementCounter() public {
        require(isActive, "Counter is not active.");
        require(block.timestamp >= lastUpdated + 1 hours, "Too soon to increment.");

        lastUpdated = block.timestamp;
        counter++;

        if (counter >= 24) {
            isActive = false;
        }

        emit CounterIncremented(counter);
    }

    function restartCounter() public {
        counter = 0;
        isActive = true;
        lastUpdated = block.timestamp;
        emit CounterRestarted();
    }
}
