    "contracts/prize-pots/PixelSweeperERC20PlayerPot.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ERC165Checker} from "@openzeppelin/contracts/utils/introspection/ERC165Checker.sol";
import {ERC165, IERC165} from "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC721Enumerable} from "@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol";
import {IERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import {Clones} from "@openzeppelin/contracts/proxy/Clones.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {Pausable} from "@openzeppelin/contracts/security/Pausable.sol";

import {IControlledBurnableToken} from "./token-upgradeable/IControlledBurnableToken.sol";
import {BurnableTicketFactory} from "./token-upgradeable/BurnableTicketFactory.sol";
import {BurnableTicket} from "./token-upgradeable/BurnableTicket.sol";
import {ITokenController} from "./token/ITokenController.sol";
import {IPeriodicPrizeStrategy} from "./prize-strategy/IPeriodicPrizeStrategy.sol";
import {IPeriodicPrizeStrategyListener} from "./prize-strategy/IPeriodicPrizeStrategyListener.sol";
import {EmergencyWithdrawable} from "../sweeper/utils/EmergencyWithdrawable.sol";
import {IUniswapV2Router01} from "../interfaces/IUniswapV2Router01.sol";
import {ITicket} from "./token/ITicket.sol";

contract PixelSweeperERC20PlayerPot is
    EmergencyWithdrawable,
    ReentrancyGuard,
    ERC165,
    ITokenController,
    IPeriodicPrizeStrategyListener,
    Pausable
{
    using ERC165Checker for address;
    using SafeERC20 for IERC20;
    using Strings for uint256;
    using Address for address payable;

    struct Round {
        uint256 roundId;
        BurnableTicket ticket;
        uint256 ticketsSold;
    }

    /// @dev Event emitted when the Prize Strategy is set
    event PrizeStrategySet(address indexed prizeStrategy);

    /// @dev Event emitted when fund receivers are changed
    event FundReceiversSet(address bonus, address team);

    address public immutable PRIZE_TOKEN;
    string private TICKET_NAME_PREFIX;
    string private TICKET_SYMBOL_PREFIX;
    address public immutable ROUTER;
    address private immutable WETH;

    uint256 public constant FEE_BONUS = 5;
    uint256 public constant FEE_TEAM = 5;
    uint256 public constant FEE_BASE = 100;

    /// @dev The implementation for tickets, cloned new round.
    BurnableTicketFactory public ticketFactory;

    /// @dev The Prize Strategy that this Prize Pool is bound to.
    IPeriodicPrizeStrategy public prizeStrategy;

    /// @dev current round id
    uint256 public roundId;

    /// @dev Record for all past and current rounds
    mapping(uint256 => Round) rounds;

    /// @dev Receiver addresses for ticket sale funds
    address payable public fundReceiverBonus;
    address payable public fundReceiverTeam;

    constructor(
        address _ticketFactory,
        address _prizeToken,
        address _router,
        address payable _fundReceiverBonus,
        address payable _fundReceiverTeam,
        string memory _ticketNamePrefix,
        string memory _ticketSymbolPrefix
    ) {
        PRIZE_TOKEN = _prizeToken;
        TICKET_NAME_PREFIX = _ticketNamePrefix;
        TICKET_SYMBOL_PREFIX = _ticketSymbolPrefix;
        ROUTER = _router;
        WETH = IUniswapV2Router01(_router).WETH();

        fundReceiverBonus = _fundReceiverBonus;
        fundReceiverTeam = _fundReceiverTeam;

        ticketFactory = BurnableTicketFactory(_ticketFactory);

        _pause();

        _prepareNextRound();
    }

    /// @dev Buy tickes with BNB.
    function buyTickets(
        uint256 mintAmountReceived
    ) external payable nonReentrant whenNotPaused {
        address from = _msgSender();
        uint256 amountTickets_ = msg.value;

        uint256 feeBonus = (amountTickets_ * FEE_BONUS) / FEE_BASE;
        uint256 feeTeam = (amountTickets_ * FEE_TEAM) / FEE_BASE;
        amountTickets_ = amountTickets_ - feeBonus - feeTeam;

        BurnableTicket ticket_ = _ticket();

        _buyPrizeTokens(
            amountTickets_,
            mintAmountReceived,
            address(prizeStrategy)
        );

        ticket_.controllerMint(from, amountTickets_);

        fundReceiverTeam.sendValue(feeTeam);
        fundReceiverBonus.sendValue(feeBonus);
    }

    /// @dev set after constructor since we need to set this contract as controller
    function setTicketFactory(address _ticketFactory) external onlyOwner {
        ticketFactory = BurnableTicketFactory(_ticketFactory);
    }

    /// @dev hook used by tickets
    function beforeTokenTransfer(
        address from,
        address to,
        uint256
    ) external view override {
        require(from != to, "Self transfer");
        require(
            from == address(0) || to == address(0),
            "Tickets non transferable"
        );
        require(
            address(prizeStrategy) == address(0) ||
                prizeStrategy.awardNotInProgress(),
            "Award in progress"
        );
    }

    /// @dev hook to get notified once prize was awarded
    function afterPrizePoolAwarded(uint256, uint256) external override {
        require(_msgSender() == address(prizeStrategy), "only prize strategy");
        _prepareNextRound();
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(ERC165, IERC165) returns (bool) {
        return
            interfaceId == type(IPeriodicPrizeStrategyListener).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    /// @notice Set the receivers of ticket sale funds
    function setFundReceivers(
        address payable _bonus,
        address payable _team
    ) external onlyOwner {
        fundReceiverBonus = _bonus;
        fundReceiverTeam = _team;

        emit FundReceiversSet(fundReceiverBonus, fundReceiverTeam);
    }

    /// @notice Sets the prize strategy of the prize pool.  Only callable by the owner.
    /// @param _prizeStrategy The new prize strategy
    function setPrizeStrategy(
        IPeriodicPrizeStrategy _prizeStrategy
    ) external onlyOwner {
        _setPrizeStrategy(_prizeStrategy);
    }

    /// @notice Pauses ticket sale
    /// @dev Callable by owner, pausing the contract is used to complete the setup and for migration purposes.
    function pause() external onlyOwner {
        _pause();
    }

    /// @notice Resume normal operations
    /// @dev Callable by owner
    function unpause() external onlyOwner {
        _unpause();
    }

    /// @notice Sets the prize strategy of the prize pool.  Only callable by the owner.
    /// @param _prizeStrategy The new prize strategy
    function _setPrizeStrategy(
        IPeriodicPrizeStrategy _prizeStrategy
    ) internal {
        require(
            address(_prizeStrategy) != address(0),
            "prizeStrategy-not-zero"
        );
        require(
            address(_prizeStrategy).supportsInterface(
                type(IPeriodicPrizeStrategy).interfaceId
            ),
            "prizeStrategy-invalid"
        );
        prizeStrategy = _prizeStrategy;

        emit PrizeStrategySet(address(_prizeStrategy));
    }

    function _prepareNextRound() internal {
        BurnableTicket lastTicket = _ticket();
        if (address(lastTicket) != address(0)) {
            rounds[roundId].ticketsSold = lastTicket.totalSupply();
            lastTicket.setBurnable(true);
        }

        Round storage round = rounds[++roundId];

        string memory id = roundId.toString();
        string memory name = string(abi.encodePacked(TICKET_NAME_PREFIX, id));
        string memory symbol = string(
            abi.encodePacked(TICKET_SYMBOL_PREFIX, id)
        );
        BurnableTicket ticket_ = ticketFactory.create(
            name,
            symbol,
            address(this)
        );

        round.roundId = roundId;
        round.ticket = ticket_;
    }

    function amountTickets(
        address _user
    ) external view returns (uint256) {
        BurnableTicket ticket_ = _ticket();
        return ticket_.balanceOf(_user);
    }

    function ticket() external view returns (ITicket) {
        return _ticket();
    }

    function currentRoundTicketsSold() external view returns (uint256) {
        return _ticket().totalSupply();
    }

    function _ticket() internal view returns (BurnableTicket) {
        return rounds[roundId].ticket;
    }

    function _buyPrizeTokens(
        uint256 _bnbIn,
        uint256 _minTokensOut,
        address to
    ) internal {
        address[] memory path = new address[](2);
        path[0] = WETH;
        path[1] = PRIZE_TOKEN;

        IUniswapV2Router01(ROUTER).swapExactETHForTokens{value: _bnbIn}(
            _minTokensOut,
            path,
            to,
            block.timestamp + 500
        );
    }

    function getMintTokensOut(uint256 tickets) external view returns (uint256) {
        address[] memory path = new address[](2);
        path[0] = WETH;
        path[1] = PRIZE_TOKEN;

        uint256[] memory amountsOut = IUniswapV2Router01(ROUTER).getAmountsOut(
            tickets,
            path
        );

        return amountsOut[1] * (FEE_BASE - FEE_BONUS - FEE_TEAM) / FEE_BASE;
    }
}
"
    },
