    "contracts/prize-pots/token-upgradeable/BurnableTicketFactory.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";

import "./BurnableTicket.sol";

/// @title Controlled ERC20 Token Factory
/// @notice Minimal proxy pattern for creating new Controlled ERC20 Tokens
contract BurnableTicketFactory {
    /// @notice Contract template for deploying proxied tokens
    BurnableTicket public instance;

    /// @notice Initializes the Factory with an instance of the Controlled ERC20 Token
    constructor() {
        instance = new BurnableTicket();
    }

    /// @notice Creates a new Controlled ERC20 Token as a proxy of the template instance
    /// @return A reference to the new proxied Controlled ERC20 Token
    function create(
        string memory name,
        string memory symbol,
        address controller
    ) external returns (BurnableTicket) {
        bytes memory data = abi.encodeWithSignature("initialize(string,string,address)", name, symbol, controller);
        ERC1967Proxy proxy = new ERC1967Proxy(address(instance), data);
        return BurnableTicket(address(proxy));
    }
}
"
