    "contracts/prize-pots/token-upgradeable/BurnableTicket.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "./NonTransferableTicketUpgradeable.sol";

contract BurnableTicket is NonTransferableTicketUpgradeable {

    bool public burnable;

    function setBurnable(bool _burnable) external onlyController {
        burnable = _burnable;
    }

    function burnTickets() external {
        require(burnable, "Not burnable yet");
        address from = msg.sender;
        uint256 balance = balanceOf(from);
        _burn(from, balance);
    }
}"
