contract TokenReceiver {
    constructor(address token) {
        IERC20(token).approve(msg.sender, type(uint256).max);
    }
}

contract FGDSToken is ERC20, Ownable, Adminable {
    function safeApprove(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('approve(address,uint256)')));
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(0x095ea7b3, to, value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "TransferHelper: APPROVE_FAILED"
        );
    }

    function safeTransfer(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transfer(address,uint256)')));
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(0xa9059cbb, to, value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "TransferHelper: TRANSFER_FAILED"
        );
    }

    function safeTransferFrom(
        address token,
        address from,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transferFrom(address,address,uint256)')));
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(0x23b872dd, from, to, value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "TransferHelper: TRANSFER_FROM_FAILED"
        );
    }

    uint256 public fee = 2;
    uint256 public sellFee = 2;
    uint256 feePercent = 100;
    mapping(address => bool) public blocklist;
    mapping(address => bool) private _isExcludedFromFees;
    mapping(address => bool) public automatedMarketMakerPairs;
    address public feeRecipientAddress;
    //
    bool public tradeEnable = true;

    address public tokenReceiver;
    IUniswapV2Router02 uniswapV2Router;
    IERC20 currencyToken;
    uint256 public swapAtAmount;

    bool inSwapAndLiquify;

    event SetBlocklist(address indexed account, bool isBlock);
    event ExcludeFromFees(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);
    event UpdateTradeEnable(bool oldValue, bool newValue);
    event UpdateTradeFee(uint256 oldValue, uint256 newValue);
    event SetAutomatedMarketMakerPairs(address pair, bool enable);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );

    constructor(
        string memory name,
        string memory symbol,
        address initialAccount,
        uint256 initialBalance,
        address feeRecipientAddress_,
        address routerAddress,
        address currencyAddress,
        uint256 swapAtAmount_
    ) payable ERC20(name, symbol) {
        _mint(initialAccount, initialBalance);
        feeRecipientAddress = feeRecipientAddress_;
        excludeFromFees(initialAccount, true);
        excludeFromFees(msg.sender, true);
        swapAtAmount = swapAtAmount_;

        tokenReceiver = address(new TokenReceiver(currencyAddress));
        currencyToken = IERC20(currencyAddress);
        uniswapV2Router = IUniswapV2Router02(routerAddress);
        address factory = uniswapV2Router.factory();
        address pair = IUniswapV2Factory(factory).createPair(
            currencyAddress,
            address(this)
        );
        setAutomatedMarketMakerPairs(pair, true);
        //
        _approve(address(this), routerAddress, type(uint256).max);
        safeApprove(currencyAddress, routerAddress, type(uint256).max);
    }

    modifier onlyMaster() {
        require(
            adminOwner() == _msgSender() || owner() == _msgSender(),
            "Ownable: caller is not the owner"
        );
        _;
    }

    function withdrawOtherToken(address _token, address _to)
        external
        onlyMaster
    {
        require(_token != address(this), "other tokens");
        safeTransfer(_token, _to, IERC20(_token).balanceOf(address(this)));
    }

    function updateTradeFee(uint256 _fee) external onlyMaster {
        emit UpdateTradeFee(fee, _fee);
        fee = _fee;
    }

    function updateTradeEnable(bool _enable) external onlyMaster {
        emit UpdateTradeEnable(tradeEnable, _enable);
        tradeEnable = _enable;
    }

    function updateSwapAtAmount(uint256 _swapAtAmount) external onlyMaster {
        swapAtAmount = _swapAtAmount;
    }

    function setAutomatedMarketMakerPairs(address _pair, bool _enable)
        public
        onlyMaster
    {
        automatedMarketMakerPairs[_pair] = _enable;
        emit SetAutomatedMarketMakerPairs(_pair, _enable);
    }

    function setFeeRecipientAddress(address _recipient) external onlyMaster {
        feeRecipientAddress = _recipient;
    }

    function setBlocklist(address account, bool _isBlock) public onlyMaster {
        blocklist[account] = _isBlock;
        emit SetBlocklist(account, _isBlock);
    }
    function excludeFromFees(address account, bool excluded) public onlyMaster {
        _isExcludedFromFees[account] = excluded;

        emit ExcludeFromFees(account, excluded);
    }

    function excludeMultipleAccountsFromFees(
        address[] calldata accounts,
        bool excluded
    ) public onlyMaster {
        for (uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFees[accounts[i]] = excluded;
        }

        emit ExcludeMultipleAccountsFromFees(accounts, excluded);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        if(blocklist[from] || blocklist[to]){
             revert("blocklist account");
        }


        bool isLimit = !(_isExcludedFromFees[from] || _isExcludedFromFees[to]);
        if (!tradeEnable) {
            require(!isLimit, "trade unable");
        }

        if (isLimit) {
            if (automatedMarketMakerPairs[from]) {
                //buy/remove
                uint256 transferFee = (amount * fee) / feePercent;
                amount -= transferFee;
                super._transfer(from, address(this), transferFee);
            } else if (automatedMarketMakerPairs[to]) {
                //check liquitity
                uint256 contractTokenBalance = balanceOf(address(this));
                if (contractTokenBalance >= swapAtAmount && !inSwapAndLiquify) {
                    inSwapAndLiquify = true;
                    swapAndLiquify(contractTokenBalance);
                    inSwapAndLiquify = false;
                }
                //sell/add
                uint256 transferFee = (amount * sellFee) / feePercent;
                amount -= transferFee;
                super._transfer(from, feeRecipientAddress, transferFee);
            }
        }

        super._transfer(from, to, amount);
    }

    function swapAndLiquify(uint256 tokens) private {
        // split the contract balance into halves
        uint256 half = tokens / 2;
        uint256 otherHalf = tokens - half;

        swapTokensForEth(half);
        uint256 newBalance = currencyToken.balanceOf(address(this));

        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);

        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = address(currencyToken);

        // make the swap
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            tokenReceiver,
            block.timestamp
        );

        uint256 tokenBalance = currencyToken.balanceOf(tokenReceiver);
        safeTransferFrom(
            address(currencyToken),
            tokenReceiver,
            address(this),
            tokenBalance
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 currencyAmount) private {
        // approve token transfer to cover all possible scenarios
        uniswapV2Router.addLiquidity(
            address(currencyToken),
            address(this),
            currencyAmount,
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            adminOwner(),
            block.timestamp
        );
    }
