    "contracts/core/StableFarmTreasury_CX.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.2;




import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../interfaces/iStableFarmXUSDToken.sol";
import "../interfaces/iStableFarmInsuranceToken.sol";

contract StableFarmTreasury_CX is AccessControl , Pausable, ReentrancyGuard {
    using SafeMath for uint256;

    IERC20 public BUSD;

    iStableFarmXUSDToken public XUSD;

    iStableFarmInsuranceToken public INSURE;

    bytes32 public constant INTERFACE_ROLE = keccak256("INTERFACE_ROLE");

    mapping(uint256 => ilevelLimits) public iLevels;

    struct ilevelLimits {
        uint256 individualSalesLimit;// Self purchase
        uint256 individualCommission;
    }

    uint256 public MaxLevelI;

    uint256 public MinPurchase;

    uint256 public MaxPurchase;

    // Ratios are set at 1000000 as 100 %
    struct Ratios{
        uint256 InsurancePercent;
        address InsurancePool;
        uint256 TreasuryPercent;
        address TreasuryPool;
        uint256 InfinityPercent;
        address InfinityPool;

    }

    Ratios public MainRatios;

    address public masterAddress;

    mapping(address => userData) public Users;

    struct userData {
        address parent;
        uint256 individualPurchase;
        uint256 teamPurchase;
        uint256 teamLevel;
        uint256 alloted;
    }

    uint256 public incomeLimitFactor;

    enum AlotStatus { nil , grantIndividual , grantTeam , missIndividual , missTeam }

    modifier isValidReferrer ( address _addr ) {
        require((((Users[_addr].parent != address(0) ) || _addr == masterAddress)) , "StableFarmTreasury: Invalid Referrer");
        _;
    }

    modifier isValidAmt( uint256 _amt ) {
        require(_amt >= MinPurchase , "StableFarmTreasury: out of limit");
        require(_amt <= MaxPurchase , "StableFarmTreasury: out of limit");
        _;
    }


    event purchaseEvent( address indexed _buyer , address indexed _referrer , uint256 _amt , address _interface );

    event allotmentEvent(  address indexed _buyer, address indexed _alloted , uint256 indexed _level , uint256 _amt , uint256 _share , AlotStatus  _allot );

    event claimEvent ( address indexed _master ,  uint256 _mastershare);

    constructor ( ) AccessControl()  Pausable() ReentrancyGuard() {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _pause();
    }

    function Setup(
        Ratios memory _ratios,
        uint256 _minPurchase,
        uint256 _maxPurchase,
        address _BUSD,
        address _XUSD,
        address _XINSURE,
        ilevelLimits[] memory _ilimits,
        uint256 _incomeLimitFactor
    ) external onlyRole(DEFAULT_ADMIN_ROLE) whenPaused  {
        MinPurchase = _minPurchase;
        MaxPurchase = _maxPurchase;
        MaxLevelI = _ilimits.length;
        MainRatios = _ratios;
        BUSD = IERC20(_BUSD);
        XUSD = iStableFarmXUSDToken(_XUSD);
        INSURE = iStableFarmInsuranceToken(_XINSURE);
        incomeLimitFactor = _incomeLimitFactor;
        for ( uint256 i = 0; i < _ilimits.length; i = i+1 ) {
            iLevels[i+1] = _ilimits[i];
        }

        _unpause();
    }

    function pauseContract() external onlyRole(DEFAULT_ADMIN_ROLE) returns (bool) {
        _pause();
        return true;
    }

    function unpauseContract() external onlyRole(DEFAULT_ADMIN_ROLE) returns (bool) {
        _unpause();
        return true;
    }


    function setMasterAddress( address _addr ) external onlyRole(DEFAULT_ADMIN_ROLE){
        masterAddress = _addr;
    }

    function setUser( address _addr , userData memory _data ) external onlyRole(DEFAULT_ADMIN_ROLE){
        Users[_addr] = _data;
    }

    function setLevels(
        ilevelLimits[] memory _ilimits
    ) external onlyRole(DEFAULT_ADMIN_ROLE){
        MaxLevelI = _ilimits.length;
        for ( uint256 i = 0; i < _ilimits.length; i = i+1 ) {
            iLevels[i+1] = _ilimits[i];
        }
    }

    function purchase( address _referrer , uint256 _amt ) external   {
        iPurchase( msg.sender , _referrer , _amt , msg.sender );
    }

    function interfacePurchase( address _sender , address _referrer , uint256 _amt ) external onlyRole(INTERFACE_ROLE) {
        iPurchase( _sender , _referrer , _amt , msg.sender );
    }

    function iPurchase( address _sender , address _referrer , uint256 _amt , address _interface ) internal whenNotPaused isValidReferrer(_referrer) isValidAmt( _amt) nonReentrant {

        BUSD.transferFrom(_interface , address(this) , _amt);

        XUSD.mint(_sender , _amt );
        INSURE.mint( _sender , _amt );

        iSetRatio( _amt );

        emit purchaseEvent( _sender , _referrer , _amt , _interface );

        address parent;

        if ( Users[_sender].parent == address(0)){
            parent = _referrer;
            Users[_sender].parent = _referrer;
        }else{
            parent = Users[_sender].parent;
        }

        Users[_sender].individualPurchase = Users[_sender].individualPurchase + _amt;
        uint256 share;
        for ( uint256 i = 1; i <= MaxLevelI; i = i+1 ) {

            share = ( _amt * iLevels[i].individualCommission ) / 1000000;

            bool isdist = distributeIndividual( _sender , parent , i , _amt , share );

            if ( isdist ){

                Users[parent].teamPurchase = Users[parent].teamPurchase + _amt;

//                distributeTeam( _sender , parent , _amt );
            }

            parent = Users[parent].parent;

            if ( parent == address(0)){
                break;
            }
        }
    }

    function distributeIndividual( address _sender , address _addr , uint256 _level , uint256 _amt , uint256 share ) internal returns ( bool ) {
        bool isdist = false;
        if ( Users[_addr].individualPurchase >= iLevels[_level].individualSalesLimit ){
            if ( ( ( Users[_addr].individualPurchase * incomeLimitFactor ) / 100 ) >= Users[_addr].alloted ){
                BUSD.transfer( _addr , share);
                Users[_addr].alloted = Users[_addr].alloted + share;
                emit allotmentEvent( _sender ,  _addr , _level , _amt , share , AlotStatus.grantIndividual );
                isdist = true;
            }else{
                emit allotmentEvent( _sender ,  _addr , _level , _amt , share , AlotStatus.missIndividual );
                isdist = false;
            }
        }else{
            emit allotmentEvent( _sender ,  _addr , _level , _amt , share , AlotStatus.missIndividual );
            isdist = false;
        }
        return isdist;
    }

//    function distributeTeam( address _sender , address _addr , uint256 _amt ) internal {
//
//        // Level Check
//        uint256 userLevel = 0;
//        for ( uint256 i = 1; i <= MaxLevelT; i = i+1 ) {
//            if ( Users[_addr].teamPurchase >= tLevels[i].teamSalesLimit ){
//                userLevel = i;
//            }else{
//                break;
//            }
//        }
//
//        Users[_addr].teamLevel = userLevel;
//        // Level Check
//
//        uint256 teamShare = ( _amt * tLevels[userLevel].teamCommission ) / 1000000;
//
//        XUSD.mint( _addr , teamShare );
//        Users[_addr].alloted = Users[_addr].alloted + teamShare;
//        emit allotmentEvent( _sender ,  _addr , Users[_addr].teamLevel , _amt , teamShare , AlotStatus.grantTeam );
//    }


    function iSetRatio( uint256 _amt ) internal {
        uint256 insuranceAmt = ( _amt * MainRatios.InsurancePercent ) / 1000000;
        uint256 treasuryAmt = ( _amt * MainRatios.TreasuryPercent ) / 1000000;
        uint256 infinityAmt = ( _amt * MainRatios.InfinityPercent ) / 1000000;
        BUSD.transfer( MainRatios.InsurancePool , insuranceAmt );
        BUSD.transfer( MainRatios.TreasuryPool , treasuryAmt );
        BUSD.transfer( MainRatios.InfinityPool , infinityAmt );
    }


    function adminCall ( address _addr , bytes memory _data , uint256 _val ) external onlyRole(DEFAULT_ADMIN_ROLE) nonReentrant whenPaused returns ( bool , bytes memory ){
        return payable(_addr).call{value : _val }(_data);
    }

    function CX () external onlyRole(DEFAULT_ADMIN_ROLE) nonReentrant whenPaused {
        uint256 bal = BUSD.balanceOf(address(this));
        BUSD.transfer(msg.sender , bal);
    }


}

