        bytes32 role,
        address account
    ) public view override returns (bool) {
        return _roles[role].members[account];
    }

    function _grantRole(bytes32 role, address account) private {
        if (!hasRole(role, account)) {
            _roles[role].members[account] = true;
            emit RoleGranted(role, account, _msgSender());
        }
    }

    function _setupRole(bytes32 role, address account) internal virtual {
        _grantRole(role, account);
    }

    function _revokeRole(bytes32 role, address account) private {
        if (hasRole(role, account)) {
            _roles[role].members[account] = false;
            emit RoleRevoked(role, account, _msgSender());
        }
    }

    modifier isOperatorOrOwner() {
        address _sender = _msgSender();
        require(
            isOperator(_sender) || owner() == _sender,
            "OperatorAccessControl: caller is not operator or owner"
        );
        _;
    }

    modifier onlyOperator() {
        require(
            isOperator(_msgSender()),
            "OperatorAccessControl: caller is not operator"
        );
        _;
    }

    function isOperator(address account) public view override returns (bool) {
        return hasRole(OPERATOR_ROLE, account);
    }

    function _addOperator(address account) internal virtual {
        _grantRole(OPERATOR_ROLE, account);
    }

    function addOperator(address account) public override onlyOperator {
        _grantRole(OPERATOR_ROLE, account);
    }

    function revokeOperator(address account) public override onlyOperator {
        _revokeRole(OPERATOR_ROLE, account);
    }
}

/**
 * https://github.com/maticnetwork/pos-portal/blob/master/contracts/common/Initializable.sol
 */
contract Initializable {
    bool inited = false;

    modifier initializer() {
        require(!inited, "already inited");
        _;
        inited = true;
    }
}

/**
 * https://github.com/maticnetwork/pos-portal/blob/master/contracts/common/EIP712Base.sol
 */
contract EIP712Base is Initializable {
    struct EIP712Domain {
        string name;
        string version;
        address verifyingContract;
        bytes32 salt;
    }

    string public constant ERC712_VERSION = "1";

    bytes32 internal constant EIP712_DOMAIN_TYPEHASH =
        keccak256(
            bytes(
                "EIP712Domain(string name,string version,address verifyingContract,bytes32 salt)"
            )
        );
    bytes32 internal domainSeperator;

    // supposed to be called once while initializing.
    // one of the contractsa that inherits this contract follows proxy pattern
    // so it is not possible to do this in a constructor
    function _initializeEIP712(string memory name) internal initializer {
        _setDomainSeperator(name);
    }

    function _setDomainSeperator(string memory name) internal {
        domainSeperator = keccak256(
            abi.encode(
                EIP712_DOMAIN_TYPEHASH,
                keccak256(bytes(name)),
                keccak256(bytes(ERC712_VERSION)),
                address(this),
                bytes32(getChainId())
            )
        );
    }

    function getDomainSeperator() public view returns (bytes32) {
        return domainSeperator;
    }

    function getChainId() public view returns (uint256) {
        uint256 id;
        assembly {
            id := chainid()
        }
        return id;
    }

    /**
     * Accept message hash and returns hash message in EIP712 compatible form
     * So that it can be used to recover signer from signature signed using EIP712 formatted data
     * https://eips.ethereum.org/EIPS/eip-712
     * "\\x19" makes the encoding deterministic
     * "\\x01" is the version byte to make it compatible to EIP-191
     */
    function toTypedMessageHash(
        bytes32 messageHash
    ) internal view returns (bytes32) {
        return
            keccak256(
                abi.encodePacked("\x19\x01", getDomainSeperator(), messageHash)
            );
    }
}

contract GSNPRICE is OperatorAccessControl {
    constructor() Ownable() {
        _addOperator(_msgSender());
    }

    uint256[] public indexs;
    uint256[] public amounts;

    function setPrice(
        uint256[] memory _indexs,
        uint256[] memory _amounts
    ) public onlyOperator {
        require(
            _indexs.length == _amounts.length + 1,
            "Indexs length does not match amounts length"
        );
        indexs = _indexs;
        amounts = _amounts;
    }

    function getPrice(uint256 index) public view returns (uint256) {
        for (uint256 _i = 0; _i < indexs.length - 1; _i++) {
            if (index >= indexs[_i] && index < indexs[_i + 1]) {
                return amounts[_i];
            }
        }
        return 0;
    }
}

contract GSN is EIP712Base, OperatorAccessControl {
    using SafeERC20 for IERC20;

    IAddressesProvider private addressesProvider;

    uint256 public index = 1;

    mapping(address => uint256) private stakingMap;

    mapping(address => bool) private alreadyStakingMap;

    GSNPRICE public priceContract;

    address public payContractAddress;

    mapping(address => bool) private removeIndexMap;

    mapping(bytes32 => bool) private signHashMap;

    constructor(address _addressesProvider) Ownable() {
        addressesProvider = IAddressesProvider(_addressesProvider);
        _addOperator(_msgSender());
        _initializeEIP712("MARKET");
    }

    function setPriceContractAddress(
        address _priceContractAddress
    ) public onlyOperator {
        priceContract = GSNPRICE(_priceContractAddress);
    }

    function setPayContractAddress(
        address _payContractAddress
    ) public onlyOperator {
        payContractAddress = _payContractAddress;
    }

    bytes32 constant MARKET_REQUEST_TYPEHASH =
        keccak256("MarketRequest(address userAddress,uint256 opType)");

    struct MarketRequest {
        address userAddress;
        uint256 opType;
    }

    function hashMarketRequest(
        MarketRequest memory request
    ) internal pure returns (bytes32) {
        return
            keccak256(
                abi.encode(
                    MARKET_REQUEST_TYPEHASH,
                    request.userAddress,
                    request.opType
                )
            );
    }

    function verifyRSV(
        bytes32 messageHash,
        bytes32 sigR,
        bytes32 sigS,
        uint8 sigV
    ) internal view returns (bool) {
        address rsvAddress = ecrecover(messageHash, sigV, sigR, sigS);
        return addressesProvider.isSigner(rsvAddress);
    }

    event Stake(
        address userAddress,
        address payContractAddress,
        uint256 index,
        uint256 amount
    );
    event Redeem(
        address userAddress,
        address payContractAddress,
        uint256 amount
    );

    function staking(bytes32 sigR, bytes32 sigS, uint8 sigV) public {
        address _userAddress = _msgSender();

        MarketRequest memory request = MarketRequest({
            userAddress: _userAddress,
            opType: 1
        });
        bytes32 messageHash = toTypedMessageHash(hashMarketRequest(request));

        require(verifyRSV(messageHash, sigR, sigS, sigV), "Invalid signature");

        require(!signHashMap[messageHash], "Duplicate staking");

        require(stakingMap[_userAddress] == 0, "Duplicate staking");
        require(!alreadyStakingMap[_userAddress], "Already staking");

        uint256 amount = priceContract.getPrice(index);

        require(amount > 0, "Price not obtained");

        IERC20 _erc20 = IERC20(payContractAddress);
        require(
            _erc20.balanceOf(_userAddress) >= amount,
            "erc20 payment is insufficient"
        );
        require(
            _erc20.allowance(_userAddress, address(this)) >= amount,
            "erc20 payment allowance is insufficient"
        );
        _erc20.safeTransferFrom(_userAddress, address(this), amount);

        index = index + 1;
        stakingMap[_userAddress] = amount;
        alreadyStakingMap[_userAddress] = true;
        signHashMap[messageHash] = true;

        emit Stake(_userAddress, payContractAddress, index, amount);
    }

    function redeem(bytes32 sigR, bytes32 sigS, uint8 sigV) public {
        address _userAddress = _msgSender();

        require(removeIndexMap[_userAddress], "Not removed");

        MarketRequest memory request = MarketRequest({
            userAddress: _userAddress,
            opType: 2
        });
        bytes32 messageHash = toTypedMessageHash(hashMarketRequest(request));

        require(verifyRSV(messageHash, sigR, sigS, sigV), "Invalid signature");

        require(!signHashMap[messageHash], "Duplicate redeem");

        uint256 amount = stakingMap[_userAddress];

        require(amount > 0, "No staking");

        IERC20 _erc20 = IERC20(payContractAddress);
        require(
            _erc20.balanceOf(address(this)) >= amount,
            "erc20 payment is insufficient"
        );

        stakingMap[_userAddress] = 0;
        signHashMap[messageHash] = true;

        _erc20.safeTransfer(_userAddress, amount);

        emit Redeem(_userAddress, payContractAddress, amount);
    }

    bytes32 constant REMOVE_INDEX_REQUEST_TYPEHASH =
        keccak256("RemoveIndexRequest(address userAddress)");

    struct RemoveIndexRequest {
        address userAddress;
    }

    function hashRemoveIndexRequest(
        RemoveIndexRequest memory request
    ) internal pure returns (bytes32) {
        return
            keccak256(
                abi.encode(REMOVE_INDEX_REQUEST_TYPEHASH, request.userAddress)
            );
    }

    function verifyRemoveIndexRSV(
        bytes32 messageHash,
        bytes32 sigR,
        bytes32 sigS,
        uint8 sigV
    ) internal view returns (bool) {
        address rsvAddress = ecrecover(messageHash, sigV, sigR, sigS);
        return addressesProvider.isSigner(rsvAddress);
    }

    event RemoveIndex(address userAddress, address opeatorAddress);

    function removeIndex(
        address _userAddress,
        bytes32 sigR,
        bytes32 sigS,
        uint8 sigV
    ) public {
        RemoveIndexRequest memory request = RemoveIndexRequest({
            userAddress: _userAddress
        });
        bytes32 messageHash = toTypedMessageHash(
            hashRemoveIndexRequest(request)
        );

        require(
            verifyRemoveIndexRSV(messageHash, sigR, sigS, sigV),
            "Invalid signature"
        );

        require(!signHashMap[messageHash], "Duplicate removeIndex");

        require(!removeIndexMap[_userAddress], "removed");

        require(stakingMap[_userAddress] > 0, "No staking");

        index = index - 1;

        removeIndexMap[_userAddress] = true;
        signHashMap[messageHash] = true;

        emit RemoveIndex(_userAddress, _msgSender());
    }
}
