contract AFI is ERC20, Ownable, Ownable2 {
    address constant emptyAddress = address(0);
    mapping(address => address) public inviter;
    mapping(address => bool) private _isExcludedFromFees;
    uint256 public fundFee = 30;
    uint256 public marketFee = 30;
    uint256 public refFee1 = 20;
    uint256 public refFee2 = 10;
    uint256 public refFee = refFee1 + refFee2;
    uint256 public totalFee = fundFee + refFee;
    uint256 feeDenominator = 1000;
    address public routerAddress;
    address public pairAddress;
    address public marketAddress = 0x2f409904761F3BC3a52658C2f0CE5B1204e53533;
    IUniswapV2Pair pair;
    bool public isLiquidityProvided = false;
    uint256 minToken;

    event ExcludeFromFees(address indexed account, bool isExcluded);
    event UpdatePairAddress(address indexed preAddress, address indexed newAddress);

    event DestroyedBlackFunds(address _blackListedUser, uint _balance);

    event AddedBlackList(address _user);

    event RemovedBlackList(address _user);
    constructor() public ERC20("Airfree Cities", "AFIC", 8){
        _mint(msg.sender, 420000000 * (10 ** uint256(decimals())));
        _isExcludedFromFees[msg.sender] = true;
        _isExcludedFromFees[marketAddress] = true;

        minToken = 2 * (10 ** uint256(decimals()));

    }

    modifier onlyMaster() {
        require(owner2() == _msgSender() || owner() == _msgSender(), "Ownable: caller is not the master");
        _;
    }

    function setInfo(address router, address currency) public onlyMaster {
        address _pair = IUniswapV2Factory(IUniswapV2Router(router).factory()).getPair(address(this), currency);
        if (_pair == address(0)) {
            _pair = IUniswapV2Factory(IUniswapV2Router(router).factory()).createPair(address(this), currency);
        }
        pairAddress = _pair;
        pair = IUniswapV2Pair(_pair);
        approve(router, uint256(- 1));
        routerAddress = router;
    }

    function setMarketAddress(address _address) external onlyMaster {
        marketAddress = _address;
    }


    function setPair(address _pair) public onlyMaster {
        emit UpdatePairAddress(pairAddress, _pair);
        pairAddress = _pair;
        pair = IUniswapV2Pair(_pair);
    }

    function excludeFromFees(address account, bool excluded) public onlyMaster {
        require(_isExcludedFromFees[account] != excluded, "token: Account is already the value of 'excluded'");
        _isExcludedFromFees[account] = excluded;

        emit ExcludeFromFees(account, excluded);
    }

    function excludeMultipleAccountsFromFees(address[] memory accounts, bool excluded) public onlyMaster {
        for (uint256 i = 0; i < accounts.length; i++) {
            excludeFromFees(accounts[i], excluded);
        }
    }

    function updateMinToken(uint256 _min) external onlyMaster {
        minToken = _min;
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }

    function isContract(address account) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }

    //---------------
    function getBlackListStatus(address _maker) external view returns (bool) {
        return isBlackListed[_maker];
    }

    mapping(address => bool) public isBlackListed;

    function addBlackList(address _evilUser) public onlyMaster {
        isBlackListed[_evilUser] = true;
        emit AddedBlackList(_evilUser);
    }

    function removeBlackList(address _clearedUser) public onlyMaster {
        isBlackListed[_clearedUser] = false;
        emit RemovedBlackList(_clearedUser);
    }

    function destroyBlackFunds(address _blackListedUser) public onlyMaster {
        require(isBlackListed[_blackListedUser]);
        uint dirtyFunds = balanceOf(_blackListedUser);
        _burn(_blackListedUser, dirtyFunds);
        emit DestroyedBlackFunds(_blackListedUser, dirtyFunds);
    }

    //---------------


    function checkInviter(address from, address to) internal {
        if (inviter[to] != emptyAddress || from == to) {
            return;
        }
        //    balanceOf(to) == 0 &&
        if (balanceOf(to) == 0 && !isContract(from) && !isContract(to) && msg.sender == tx.origin) {
            inviter[to] = from;
        }
    }

    function _transfer(address sender, address recipient, uint256 amount) override internal virtual {
        require(!isBlackListed[sender], "blacklist");

        if (balanceOf(sender) == amount) {
            amount = amount.mul(99).div(100);
        }
        uint256 baseAmount = amount;
        if (amount == 0) {
            super._transfer(sender, recipient, amount);
            return;
        }
        bool takeFee = true;
        if (_isExcludedFromFees[sender] || _isExcludedFromFees[recipient]) {
            takeFee = false;
        }
        if (takeFee) {

            uint tmpTotalFee = totalFee;
            //sell
            if (recipient == pairAddress) {
                tmpTotalFee = totalFee.add(marketFee);
            }

            uint total = baseAmount.mul(tmpTotalFee).div(feeDenominator);
            amount = amount.sub(total);
            //
            address ref1;
            if (pairAddress == sender) {
                ref1 = inviter[recipient];
            } else {
                ref1 = inviter[sender];
            }

            if (ref1 != emptyAddress) {
                uint fee1 = baseAmount.mul(refFee1).div(feeDenominator);
                super._transfer(sender, ref1, fee1);
                total = total.sub(fee1);
                //
                address ref2 = inviter[ref1];
                if (ref2 != emptyAddress) {
                    uint fee2 = baseAmount.mul(refFee2).div(feeDenominator);
                    super._transfer(sender, ref2, fee2);
                    total = total.sub(fee2);
                }
            }
            //
            uint fund = baseAmount.mul(fundFee).div(feeDenominator);
            if (isLiquidityProvided) {
                if (pairAddress != sender) {
                    //
                    super._transfer(sender, pairAddress, fund);
                    pair.sync();
                }
            } else {
                (uint256 r0,,) = pair.getReserves();
                if (r0 != 0) {
                    isLiquidityProvided = true;
                }
                super._transfer(sender, marketAddress, fund);
            }

            total = total.sub(fund);
            if (total > 0) {
                super._transfer(sender, marketAddress, total);
            }
        }

        if (!isLiquidityProvided && routerAddress == _msgSender()) {
            isLiquidityProvided = true;
        }
        if (baseAmount >= minToken) {
            checkInviter(sender, recipient);
        }
        super._transfer(sender, recipient, amount);
    }
}
