contract BaseSale is Ownable, Pausable, ERC721Holder, ERC1155Holder {
    using SafeERC20 for IERC20;

    // InterfaceID of NFT 721
    bytes4 constant IERC721_ID = 0x80ac58cd;
    // InterfaceID of NFT 1155
    bytes4 constant IERC1155_ID = 0xd9b67a26;

    // Total Percentage
    uint256 public constant TOTAL_PERCENTAGES = 10000;

    // Array holding list of accepting ERC20 token for payment
    address[] public acceptingERC20Tokens;

    // Address of marketplace fee collector
    address marketFeeCollector;

    // Mapping each type of token to the amount of total fee
    mapping(address => uint256) totalFeeColected;

    /**********************************/
    /*╔═════════════════════════════╗
      ║          MODIFIERS          ║
      ╚═════════════════════════════╝*/

    /**
     * @dev modifier check sender having enough money for payment
     * @param _ERC20Token - Address of ERC20 token for payment
     * @param _amount - Required money for payment
     */
    modifier checkSenderBalance(address _ERC20Token, uint256 _amount) {
        // Check paying the right price
        if (_isERC20Sale(_ERC20Token)) {
            // Check having enough tokens
            require(
                IERC20(_ERC20Token).balanceOf(msg.sender) >= _amount,
                "Not having enough amount of tokens"
            );
            // Check approved enough amount of token
            require(
                IERC20(_ERC20Token).allowance(msg.sender, address(this)) >=
                    _amount,
                "Have not approved enough amount of tokens"
            );
        } else {
            // Check sending transaction with right amount of native crypto
            require(msg.value >= _amount, "Not paying enough amount value");
        }
        _;
    }

    /**********************************/
    /*╔═════════════════════════════╗
      ║         END MODIFIERS       ║
      ╚═════════════════════════════╝*/

    /**********************************/
    /*╔═════════════════════════════╗
      ║        CHECK FUNCTIONS      ║
      ╚═════════════════════════════╝*/

    function _checkSellerOwnership(
        address _smcAddress,
        uint256 _tokenID,
        uint256 _amount,
        address _seller
    ) internal returns (bool) {
        if (IERC165(_smcAddress).supportsInterface(IERC721_ID)) {
            return IERC721(_smcAddress).ownerOf(_tokenID) == _seller;
        } else if (IERC165(_smcAddress).supportsInterface(IERC1155_ID)) {
            return
                IERC1155(_smcAddress).balanceOf(_seller, _tokenID) >= _amount;
        } else {
            // Call ownerOf function to check ownership for ERC721 NFT
            (bool callERC721Result, bytes memory returnDataERC721) = _smcAddress
                .call(abi.encodeWithSignature("ownerOf(uint256)", _tokenID));
            if (callERC721Result) {
                if (returnDataERC721.length > 0) {
                    return (abi.decode(returnDataERC721, (address)) == _seller);
                }
            } else {
                (
                    bool callERC1155Result,
                    bytes memory returnDataERC1155
                ) = _smcAddress.call(
                        abi.encodeWithSignature(
                            "balanceOf(address,uint256)",
                            _seller,
                            _tokenID
                        )
                    );
                if (!callERC1155Result) {
                    return false;
                } else {
                    return (abi.decode(returnDataERC1155, (uint256)) >=
                        _amount);
                }
            }
        }
        return false;
    }

    function _checkAcceptingAddress(
        address _addressToVerify,
        address[] memory listAcceptingAddress
    ) internal pure returns (bool) {
        for (uint256 i = 0; i < listAcceptingAddress.length; i++) {
            if (_addressToVerify == listAcceptingAddress[i]) {
                return true;
            }
        }
        return false;
    }

    function _isERC20Sale(address _ERC20Token) internal pure returns (bool) {
        return _ERC20Token != address(0);
    }

    /*╔═════════════════════════════╗
      ║     END CHECK FUNCTIONS     ║
      ╚═════════════════════════════╝*/
    /**********************************/

    /**********************************/
    /*╔═════════════════════════════╗
      ║     INTERNAL FUNCTIONS      ║
      ╚═════════════════════════════╝*/

    /**
     * @dev Transfer NFT ownership from this smc to "_to"
     * @param _to - Receiver address
     * @param _smcAddress - Address of ERC1155 SMC holding the NFT
     * @param _tokenID - Token ID of NFT to transfer
     * @param _amount - Amount of tokens to transfer (applicable to ERC1155)
     */
    function _transferNFT(
        address _to,
        address _smcAddress,
        uint256 _tokenID,
        uint256 _amount
    ) internal {
        if (IERC165(_smcAddress).supportsInterface(IERC721_ID)) {
            IERC721(_smcAddress).safeTransferFrom(
                address(this),
                _to,
                _tokenID,
                ""
            );
        } else if (IERC165(_smcAddress).supportsInterface(IERC1155_ID)) {
            IERC1155(_smcAddress).safeTransferFrom(
                address(this),
                _to,
                _tokenID,
                _amount,
                ""
            );
        } else {
            // Call optional return transfer function
            (bool callResult, bytes memory returnData) = _smcAddress.call(
                abi.encodeWithSignature(
                    "transfer(address,uint256)",
                    _to,
                    _tokenID
                )
            );
            require(callResult, "Transfer NFT failed");
            if (returnData.length > 0) {
                require(abi.decode(returnData, (bool)), "Transfer NFT failed");
            }
        }
    }

    /**
     * @dev Transfer NFT ownership from "_from" to "_to" address
     * @param _from - Sender address
     * @param _to - Receiver address
     * @param _smcAddress - Address of ERC1155 SMC holding the NFT
     * @param _tokenID - Token ID of NFT to transfer
     * @param _amount - Amount of tokens to transfer (applicable to ERC1155)
     */
    function _transferFromNFT(
        address _from,
        address _to,
        address _smcAddress,
        uint256 _tokenID,
        uint256 _amount
    ) internal {
        if (IERC165(_smcAddress).supportsInterface(IERC721_ID)) {
            IERC721(_smcAddress).safeTransferFrom(_from, _to, _tokenID, "");
        } else if (IERC165(_smcAddress).supportsInterface(IERC1155_ID)) {
            IERC1155(_smcAddress).safeTransferFrom(
                _from,
                _to,
                _tokenID,
                _amount,
                ""
            );
        } else {
            // Call optional return transferFrom function
            (bool callResult, bytes memory returnData) = _smcAddress.call(
                abi.encodeWithSignature(
                    "transferFrom(address,address,uint256)",
                    _from,
                    _to,
                    _tokenID
                )
            );
            require(callResult, "Transfer from NFT failed");
            if (returnData.length > 0) {
                require(
                    abi.decode(returnData, (bool)),
                    "Transfer from NFT failed"
                );
            }
        }
    }

    /**
     * @dev Returns the percentage of the total price (used to calculate fee payments)
     * @param _totalPrice - The total price to divide
     * @param _percentage - The corresponding percentage
     */
    function _getPortionOfPrice(uint256 _totalPrice, uint256 _percentage)
        internal
        pure
        returns (uint256)
    {
        return (_totalPrice * _percentage) / TOTAL_PERCENTAGES;
    }

    /**
     * @dev Share sold NFT fee to seller and fee recipient
     * @param _nftSeller - The address of seller
     * @param _totalPrice - The price NFT sold
     * @param _ERC20Token - The address of ERC20 token specified for payment
     */
    //---------------------------------------------------------------------------
    function _payFeesAndSeller(
        address _nftSeller,
        uint256 _totalPrice,
        address _ERC20Token,
        uint256 _marketFeePercentage
    ) internal {
        // Charging market fee
        uint256 marketFee = _getPortionOfPrice(
            _totalPrice,
            _marketFeePercentage
        );
        // Transfer market fee to marketplace smc
        _transferMoneyFrom(msg.sender, address(this), marketFee, _ERC20Token);
        // Add to total fee collected
        totalFeeColected[_ERC20Token] += marketFee;

        // Send remaining amount to seller account
        _transferMoneyFrom(
            msg.sender,
            _nftSeller,
            (_totalPrice - marketFee),
            _ERC20Token
        );
    }

    /**
     * @dev Send amount to recipient
     * @param _recipient - The receiver address
     * @param _amount - The amount of ETH to send
     * @param _ERC20Token - Address of ERC20 use for payments
     */
    function _transferMoney(
        address _recipient,
        uint256 _amount,
        address _ERC20Token
    ) internal {
        if (_isERC20Sale(_ERC20Token)) {
            // Send fund to recipient by ERC20 tokens
            IERC20(_ERC20Token).safeTransfer(_recipient, _amount);
        } else {
            // Send fund to recipient by native crypto
            // If recipient is this smc itself, do not need to execute transfer (it already keep this money)
            if (_recipient != address(this)) {
                require(
                    payable(_recipient).send(_amount),
                    "Cannot send fee to recipient"
                );
            }
        }
    }

    /**
     * @dev Send amount to recipient
     * @param _sender - The sender address
     * @param _recipient - The receiver address
     * @param _amount - The amount of ETH to send
     * @param _ERC20Token - Address of ERC20 use for payments
     */
    function _transferMoneyFrom(
        address _sender,
        address _recipient,
        uint256 _amount,
        address _ERC20Token
    ) internal {
        if (_isERC20Sale(_ERC20Token)) {
            IERC20 erc20Token = IERC20(_ERC20Token);

            // Send fund to recipient by ERC20 tokens
            erc20Token.safeTransferFrom(_sender, _recipient, _amount);
        } else {
            // Send fund to recipient by native crypto
            // If recipient is this smc itself, do not need to execute transfer (it already keep this money)
            if (_recipient != address(this)) {
                require(
                    payable(_recipient).send(_amount),
                    "Cannot send fee to recipient"
                );
            }
        }
    }

    /**
     * @dev Refund native token if buyer buy NFT with value higher sale price
     * @param _salePrice - The sale price of NFT Item
     * @param _ERC20Token - Address of ERC20 use for payments
     */
    function _refundMoneyToBuyer(uint256 _salePrice, address _ERC20Token)
        internal
    {
        if (!_isERC20Sale(_ERC20Token) && msg.value > _salePrice) {
            // Refund value to buyer by native crypto
            require(
                payable(msg.sender).send(msg.value - _salePrice),
                "Cannot refund value to buyer"
            );
        }
    }

    /**
     * @dev Set the market fee collector to new account (`_newFeeCollector`).
     * @param _newFeeCollector - New fee collector
     */
    function _setMarketFeeCollector(address _newFeeCollector) internal {
        require(
            _newFeeCollector != address(0),
            "The fee collector must not be the zero address"
        );

        marketFeeCollector = _newFeeCollector;
    }

    /*╔═════════════════════════════╗
      ║    END INTERNAL FUNCTIONS   ║
      ╚═════════════════════════════╝*/
    /**********************************/

    /**********************************/
    /*╔═════════════════════════════╗
      ║       OWNER FUNCTIONS       ║
      ╚═════════════════════════════╝*/

    /**
     * @dev Owner or fee collector call to withdraw all fee collected from sales to fee collector wallet
     * Loop over supported ERC20 tokens list and call transfer to fee collector address
     * Reset total fee colected to 0
     */
    function withdrawAllFee() external {
        require(
            msg.sender == owner() || msg.sender == marketFeeCollector,
            "No permission to withdraw fee"
        );
        for (uint256 i = 0; i < acceptingERC20Tokens.length; i++) {
            uint256 amount = totalFeeColected[acceptingERC20Tokens[i]];
            // Reset total fee collected
            totalFeeColected[acceptingERC20Tokens[i]] = 0;
            // Transfer tokens to fee collector address
            _transferMoney(marketFeeCollector, amount, acceptingERC20Tokens[i]);
        }
    }

    /**
     * @dev Transfers the market fee collector to new account (`_newFeeCollector`).
