    function transferMarketFeeCollector(address _newFeeCollector) external {
        require(
            msg.sender == marketFeeCollector,
            "No permission to transfer market fee collector"
        );

        _setMarketFeeCollector(_newFeeCollector);
    }

    /**
     * @dev Pause all user external functions: sell, buy, update price, cancel sale
