    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause all user external functions: sell, buy, update price, cancel sale
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /*╔═════════════════════════════╗
      ║      END OWNER FUNCTIONS    ║
      ╚═════════════════════════════╝*/
    /**********************************/
}
