// File: contracts/NFTSale/FixedPriceSale.sol

pragma solidity ^0.8.4;




// TODO: allow sell any number of NFT(s) (ERC1155)
// TODO: sell bundle of NFT(s)

/// @title FixedPriceSale core
contract FixedPriceSale is BaseSale, ReentrancyGuard {
    /*Struct collection
        - Array erc20token accept for payment
        - MarketFee Percentage
        - map minPrice to erc20token
    */
    struct Collection {
        address[] acceptingERC20Tokens;
        mapping(address => uint256) minPriceOfERC20Tokens;
        uint256 marketFeePercentage;
    }
    // Mapping Collection ( key: smartcontract collection address, value: struct collection)
    //Default collection is address 0
    mapping(address => Collection) public collections;
    // array save list collection
    address[] public listCollections;

    // Represent and fixed price sale on an NFT
    struct FixedPriceItem {
        // Address of SMC holding the NFT
        address smcAddress;
        // ID of NFT token
        uint256 tokenID;
        // Current onwer of NFT
        address seller;
        // Fixed price (in wei) for buying the nft
        uint256 price;
        // The seller can specify an ERC20 token that can be used purchase the NFT.
        // Set to address(0) to use native crypto
        address ERC20Token;
        // Is sale item active or not (can be bought or not)
        bool status;
        // Marketfee percentage
        uint256 marketFeePercent;
    }

    // Increment variable counting number of sales
    uint256 public saleIDCounter;

    // Last saleID is on sale
    uint256 public lastSaleOnRunning;
    // Storage saleID array for cancel patch event
    uint256[] public cancelledSaleID;

    // Map from saleID to their corresponding sale item.
    mapping(uint256 => FixedPriceItem) public fixedPriceSales;

    constructor(address _marketFeeCollector, uint256 _marketFeePercentage) {
        _setMarketFeeCollector(_marketFeeCollector);
        // Accepting native token as payment
        acceptingERC20Tokens.push(address(0));
        // Init default collection
        collections[address(0)].acceptingERC20Tokens.push(address(0));
        collections[address(0)].minPriceOfERC20Tokens[address(0)] = 0;
        collections[address(0)].marketFeePercentage = _marketFeePercentage;
        // add default collection (address 0) to list collection
        listCollections.push(address(0));
        lastSaleOnRunning = 1;
    }

    /**********************************/
    /*╔═════════════════════════════╗
      ║            EVENTS           ║
      ╚═════════════════════════════╝*/

    event NFTFixedPriceSaleCreated(
        uint256 saleID,
        address smcAddress,
        uint256 tokenID,
        address seller,
        uint256 price,
        address ERC20Token,
        uint256 marketFeePercent
    );

    event NFTFixedPriceSaleCancelledPatch(
        uint256[] cancelledSaleID
    );

    event NFTFixedPriceSaleSold(uint256 saleID, address buyer, uint256 price);

    event NFTFixedPriceSaleCancelled(uint256 saleID);

    event NFTFixedPriceSalePriceUpdated(uint256 saleID, uint256 price);

    /**********************************/
    /*╔═════════════════════════════╗
      ║          END EVENTS         ║
      ╚═════════════════════════════╝*/

    /**********************************/
    /*╔═════════════════════════════╗
      ║          MODIFIERS          ║
      ╚═════════════════════════════╝*/

    /**
     * @dev modifier check if an NFT on sale
     * @param _saleID - ID of sale to check
     */
    //for downsize, change modifier to function private view
    function _isOnSale(uint256 _saleID) private view {
        require(fixedPriceSales[_saleID].status == true, "Not on sale");
    }
    modifier isOnSale(uint256 _saleID) {
        _isOnSale(_saleID);
        _;
    }

    /**
     * @dev modifier check if sender own NFT sale
     * @param _saleID - ID of sale to check
     */
    modifier onlySeller(uint256 _saleID) {
        require(
            fixedPriceSales[_saleID].seller == msg.sender,
            "Sender does not own this NFT sale"
        );
        _;
    }

    /**
     * @dev modifier enforce the selling price greater or equal zero (free)
     * @param _price - Price submitted for sale
     */
    modifier priceGreaterOrEqualZero(uint256 _price) {
        require(_price >= 0, "Price must not be negative");
        _;
    }

    /**********************************/
    /*╔═════════════════════════════╗
      ║         END MODIFIERS       ║
      ╚═════════════════════════════╝*/

    /**********************************/
    /*╔═════════════════════════════╗
      ║        CHECK FUNCTIONS      ║
      ╚═════════════════════════════╝*/

    /*╔═════════════════════════════╗
      ║     END CHECK FUNCTIONS     ║
      ╚═════════════════════════════╝*/
    /**********************************/

    /**********************************/
    /*╔═════════════════════════════╗
      ║     INTERNAL FUNCTIONS      ║
      ╚═════════════════════════════╝*/

    /**
     * @dev Construct FixedPriceItem storing all information relating to NFT sale
     * @param _saleID - ID of the sale
     * @param _smcAddress - Address of SMC holding the NFT
     * @param _tokenID - Token ID of NFT to sell
     * @param _price - The price to sell (in wei)
     * @param _ERC20Token - The ERC20 token use for payments
     */
    function _setupSale(
        uint256 _saleID,
        address _smcAddress,
        uint256 _tokenID,
        uint256 _price,
        uint256 _marketFeePercentage,
        address _ERC20Token
    ) internal {
        FixedPriceItem storage saleItem = fixedPriceSales[_saleID];

        saleItem.smcAddress = _smcAddress;
        saleItem.tokenID = _tokenID;
        saleItem.seller = msg.sender;
        saleItem.price = _price;
        saleItem.ERC20Token = _ERC20Token;
        saleItem.status = true;
        saleItem.marketFeePercent = _marketFeePercentage;
    }

    /*╔═════════════════════════════╗
      ║    END INTERNAL FUNCTIONS   ║
      ╚═════════════════════════════╝*/
    /**********************************/

    /**********************************/
    /*╔═════════════════════════════╗
      ║     EXTERNAL FUNCTIONS      ║
      ╚═════════════════════════════╝*/

    function getCollectionInfo(address collection) external view returns(address[] memory, uint256){
        //Get collection 
        Collection storage nftCollection = _checkAcceptingAddress(
            collection,
            listCollections
        )
            ? collections[collection]
            : collections[address(0)];
        return (nftCollection.acceptingERC20Tokens, nftCollection.marketFeePercentage);
    }

    function getMinPrice(address collection, address ERC20Token) external view returns(uint256){
        return (collections[collection].minPriceOfERC20Tokens[ERC20Token]);
    }
    

    /**
     * @dev Function seller call to sell an NFT
     * Will contruct fixed price sale item, escrow NFT, and emit new sale event
     * Check for item not on sale yet
     * Check for price greater or equal zero
     * Check for seller ownership
     * @param _smcAddress - Address of ERC1155 SMC holding the NFT
     * @param _tokenID - Token ID of NFT to sell
     * @param _price - The price to sell (in wei)
     * @param _ERC20Token - The ERC20 token use for payments
     */
    function createSale(
        address _smcAddress,
        uint256 _tokenID,
        uint256 _price,
        address _ERC20Token
    ) external whenNotPaused priceGreaterOrEqualZero(_price) {
        // Check if the seller own the NFT
        require(
            _checkSellerOwnership(_smcAddress, _tokenID, 1, msg.sender),
            "Sender does not own this NFT"
        );

        //Get collection rule
        Collection storage nftCollection = _checkAcceptingAddress(
            _smcAddress,
            listCollections
        )
            ? collections[_smcAddress]
            : collections[address(0)];

        require(
            _checkAcceptingAddress(
                _ERC20Token,
                nftCollection.acceptingERC20Tokens
            ),
            "Erc20 Token does not accept for this collection NFT"
        );
    
        require(_price >= nftCollection.minPriceOfERC20Tokens[_ERC20Token],
            "Price must be greater than or equal to min price"
        );

        // Transfer NFT to this FixedPriceSale SMC
        _transferFromNFT(msg.sender, address(this), _smcAddress, _tokenID, 1);

        // Increase saleID
        ++saleIDCounter;

        // Construct and store fixed price sale item information
        _setupSale(
            saleIDCounter,
            _smcAddress,
            _tokenID,
            _price,
            nftCollection.marketFeePercentage,
            _ERC20Token
        );

        // Emit new NFT sale event
        emit NFTFixedPriceSaleCreated(
            saleIDCounter,
            _smcAddress,
            _tokenID,
            msg.sender,
            _price,
            _ERC20Token,
            nftCollection.marketFeePercentage
        );
    }

    /**
     * @dev Function buyer call to buy an NFT with specific price
     * Will contruct fixed price sale item, escrow NFT, and emit new sale event
     * Check for item currently on sale
     * Check sent value exactly the price
     * Update item sale status
     * Transfer NFT to buyer
     * Transfer token to seller and fee recipients
     * @param _saleID - ID of NFT sale
     * @param _buyerPrice -  The price buyer submit (to prevent changing price)
     */
    function buy(uint256 _saleID, uint256 _buyerPrice)
        external
        payable
        whenNotPaused
        isOnSale(_saleID)
        checkSenderBalance(
            fixedPriceSales[_saleID].ERC20Token,
            fixedPriceSales[_saleID].price
        )
        nonReentrant
    {
        FixedPriceItem storage saleItem = fixedPriceSales[_saleID];

        require(
            _buyerPrice >= saleItem.price,
            "Sale item price is higher than buyer's price"
        );


        // Set sale status to finish before transfering coin and NFT
        // to prevent reentrancy attack
        saleItem.status = false;

        // Charge market fee and transfer money to seller
        _payFeesAndSeller(
            saleItem.seller,
            saleItem.price,
            saleItem.ERC20Token,
            saleItem.marketFeePercent
        );

        // Refund momey to buyer if buy NFT via native token
        _refundMoneyToBuyer(saleItem.price, saleItem.ERC20Token);

        // Transfer NFT to buyer
        _transferNFT(msg.sender, saleItem.smcAddress, saleItem.tokenID, 1);

        // Emit NFT sold event
        emit NFTFixedPriceSaleSold(
            _saleID,
            msg.sender,
            fixedPriceSales[_saleID].price
        );
    }

    /**
     * @dev Function seller call to cancel an ongoing NFT sale
     * Check for item currently on sale
     * Check NFT ownership of sender
     * Update item sale price
     * Emit update price event
     * @param _saleID - Token ID of NFT to update
     * @param _price - New price for that NFT
     */
    function updateSalePrice(uint256 _saleID, uint256 _price)
        external
        whenNotPaused
        isOnSale(_saleID)
        priceGreaterOrEqualZero(_price)
        onlySeller(_saleID)
    {
        FixedPriceItem storage saleItem = fixedPriceSales[_saleID];

        // Update sale status to become not on sale
        saleItem.price = _price;

        // Emit cancel sale event
        emit NFTFixedPriceSalePriceUpdated(_saleID, saleItem.price);
    }

    /**
     * @dev Function seller call to cancel an ongoing NFT sale
     * Can cancel when pause	
     * Check for item currently on sale
     * Check NFT ownership of sender
     * Update item sale status
     * Transfer NFT back to seller
     * Emit cancel sale event
     * @param _saleID - Token ID of NFT to sell
     */
    function cancelSale(uint256 _saleID)
        external
        isOnSale(_saleID)
        onlySeller(_saleID)
        nonReentrant
    {
        FixedPriceItem storage saleItem = fixedPriceSales[_saleID];

        // Update sale status to become not on sale
        saleItem.status = false;

        // Transfer NFT back to seller
        _transferNFT(msg.sender, saleItem.smcAddress, saleItem.tokenID, 1);

        // Emit cancel sale event
        emit NFTFixedPriceSaleCancelled(_saleID);
    }

    /*╔═════════════════════════════╗
      ║    END EXTERNAL FUNCTIONS   ║
      ╚═════════════════════════════╝*/
    /**********************************/

    /**********************************/
    /*╔═════════════════════════════╗
      ║       OWNER FUNCTIONS       ║
      ╚═════════════════════════════╝*/

    /** function register collection
     * @dev Create new collection
     * @param _nftAddress - New smc address of NFT
     * @param _acceptingERC20Tokens - Array Erc20 token accept for payment
     * @param _marketFeePercentage - Market Fee Percentage
     */
    function createCollection(
        address _nftAddress,
        address[] memory _acceptingERC20Tokens,
        uint256 _marketFeePercentage
    ) external whenNotPaused onlyOwner {
        require(
            _nftAddress != address(0)
            ,"Address of NFT collection Smartcontract must not equal 0"
        );
    
        require(
            !_checkAcceptingAddress(_nftAddress, listCollections),
            "Collection NFT was already registed"
        );

        listCollections.push(_nftAddress);

        Collection storage newCollection = collections[_nftAddress];
        newCollection.marketFeePercentage = _marketFeePercentage;

        for (uint256 i = 0; i < _acceptingERC20Tokens.length; ++i) {
            newCollection.acceptingERC20Tokens.push(_acceptingERC20Tokens[i]);
            //default min price is 0
            //newCollection.minPriceOfERC20Tokens[_acceptingERC20Tokens[i]] = 0;
            if (
                !_checkAcceptingAddress(
                    _acceptingERC20Tokens[i],
                    acceptingERC20Tokens
                )
            ) {
                acceptingERC20Tokens.push(_acceptingERC20Tokens[i]);
            }
        }
    }

    /** function update market fee percentage collection 
     * @dev Update collection info
     * @param _nftAddress - New smc address of NFT
     * @param _marketFeePercentage - Market Fee Percentage
     */
    function updateMarketFeePercentageOfCollection(
        address _nftAddress,
        uint256 _marketFeePercentage
    ) external whenNotPaused onlyOwner {
        require(
            _checkAcceptingAddress(_nftAddress, listCollections),
            "Collection NFT is not registed"
        );
        collections[_nftAddress].marketFeePercentage = _marketFeePercentage;
    }

    /** function update minPrice collection
     * @dev Update collection info
     * @param _nftAddress - New smc address of NFT
     * @param _acceptingERC20Tokens - Array Erc20 token accept for payment
     * @param _minPriceArrayOfERC20Tokens - Array of min price of Erc20 token accept for payment
     */
    function updateMinPriceOfCollection(
        address _nftAddress,
        address[] memory _acceptingERC20Tokens,
        uint256[] memory _minPriceArrayOfERC20Tokens
    ) external whenNotPaused onlyOwner {
        require(_minPriceArrayOfERC20Tokens.length == _acceptingERC20Tokens.length,
            "Min price of all accepted tokens must be set"
        );
        require(
            _checkAcceptingAddress(_nftAddress, listCollections),
            "Collection NFT is not registed"
        );

        //Collection storage updateCollection = collections[_nftAddress];
        for (uint256 ERCToken = 0; ERCToken < _minPriceArrayOfERC20Tokens.length; ++ERCToken){
            collections[_nftAddress].minPriceOfERC20Tokens[_acceptingERC20Tokens[ERCToken]] = _minPriceArrayOfERC20Tokens[ERCToken];
        }
    }

    /** function add accept payment token
     * @dev Add new token into accepted payment token list
     * @param _collectionAddress - New smc address of NFT
     * @param _acceptingERC20Tokens - Array new Erc
     20 token accept for payment
     * @param _minPriceArrayOfERC20Tokens - Array min price of ERC20 token accept for payment
     */
    function addERC20TokenAccept(
        address _collectionAddress,
        address[] memory _acceptingERC20Tokens,
        uint256[] memory _minPriceArrayOfERC20Tokens
    ) external whenNotPaused onlyOwner {
        require(_minPriceArrayOfERC20Tokens.length == _acceptingERC20Tokens.length,
            "Min price of all accepted tokens must be set"
        );
        require(
            _checkAcceptingAddress(_collectionAddress, listCollections),
            "Collection NFT is not registed"
        );

        Collection storage updateCollection = collections[_collectionAddress];

        for (uint256 i = 0; i < _acceptingERC20Tokens.length; ++i) {
            updateCollection.acceptingERC20Tokens.push(
                _acceptingERC20Tokens[i]
            );
            updateCollection.minPriceOfERC20Tokens[_acceptingERC20Tokens[i]] = _minPriceArrayOfERC20Tokens[i];
            if (
                !_checkAcceptingAddress(
                    _acceptingERC20Tokens[i],
                    acceptingERC20Tokens
                )
            ) {
                acceptingERC20Tokens.push(_acceptingERC20Tokens[i]);
            }
        }
    }

    /**
     * @dev Function cancel patch, transfer NFT return back to seller
     * Cancel sale by id in range [from, to]
     * Estimate Warning: should cancel < 300 sales per transactions 
     * @param from: must be equal to lastSaleOnRunning - the last sale ID cancel before
     * @param to: the last sale ID will cancel
     */
    function cancelSalePatch(uint256 from, uint256 to)
        public
        onlyOwner
    {   
        cancelledSaleID = new uint256[](0);

        if (to > saleIDCounter) to = saleIDCounter;
        // if (from < lastSaleOnRunning) from = lastSaleOnRunning;
        require(from == lastSaleOnRunning
        , "Must cancel from lastSaleOnRunning"
        );
        for (uint256 saleID = from; saleID <= to; ++saleID){
            //if saleID is on sale
            if (fixedPriceSales[saleID].status == true){
                FixedPriceItem storage saleItem = fixedPriceSales[saleID];
                // Set sale status to finish before transfering coin and NFT
                // to prevent reentrancy attack
                saleItem.status = false;

                // Transfer NFT to seller
                _transferNFT(saleItem.seller, saleItem.smcAddress, saleItem.tokenID, 1);
                cancelledSaleID.push(saleID);
            }
        }
        // Emit cancel sale event
        emit NFTFixedPriceSaleCancelledPatch(
            cancelledSaleID
        );
        lastSaleOnRunning = to + 1;
    }

    /*╔═════════════════════════════╗
      ║    END OWNER FUNCTIONS      ║
      ╚═════════════════════════════╝*/
    /**********************************/
}
