    "contracts/TheLegendOfFuriax.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.14;

// Openzeppelin
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Supply.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

// Minteeble
import "@minteeble/smart-contracts/contracts/misc/MinteebleRandomizationConsumer.sol";
import "@minteeble/smart-contracts/contracts/misc/AccountActionTracker.sol";
import "@minteeble/smart-contracts/contracts/token/ERC721/extensions/SimpleMultiWhitelistExtension.sol";

// Chainlink
import "@chainlink/contracts/src/v0.8/VRFV2WrapperConsumerBase.sol";

contract TheLegendOfFuriax is
    ERC1155Supply,
    AccessControlEnumerable,
    ReentrancyGuard,
    MinteebleRandomizationConsumer,
    SimpleMultiWhitelistExtension,
    AccountActionTracker
{
    bool public paused;
    string public name;
    string public symbol;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    uint256 public constant FURIAX_COMMON_ID = 1;
    uint256 public constant ANNA_COMMON_ID = 2;
    uint256 public constant FURIAX_RARE_ID = 3;
    uint256 public constant ANNA_RARE_ID = 4;
    uint256 public constant FURIAX_EPIC_ID = 5;
    uint256 public constant ANNA_EPIC_ID = 6;
    uint256 public constant FURIAX_LEGENDARY_ID = 7;
    uint256 public constant ANNA_LEGENDARY_ID = 8;

    uint256 public constant COMMON_BOX_ID = 0;
    uint256 public constant RARE_BOX_ID = 1;
    uint256 public constant EPIC_BOX_ID = 2;

    struct IdInfo {
        uint256 id;
        uint256 amount;
    }

    struct BoxInfo {
        IdInfo[] idAmounts;
        uint256 boxSupply;
        uint256 price;
    }

    mapping(uint256 => BoxInfo) public boxInfo;

    struct BoxRequestInfo {
        address account;
        uint256 boxId;
    }

    // Request ID to request info
    mapping(uint256 => BoxRequestInfo) public boxRequestInfo;

    mapping(uint256 => uint256) public whitelistIdToBoxId;

    // Gets last minted ID,
    mapping(address => uint256) public lastMintedId;

    constructor(address _linkAddress, address _wrapperAddress)
        ERC1155(
            "ipfs://bafybeibrallpr4a65plvq73wzdfi4wjl32ddlptfa7us7yu3dk327sa7em/{id}.json"
        )
        MinteebleRandomizationConsumer(
            _linkAddress != address(0x0) ? _linkAddress : linkAddress,
            _wrapperAddress != address(0x0) ? _wrapperAddress : wrapperAddress
        )
    {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        name = "The Legend Of Furiax";
        symbol = "TLOF";
        paused = true;

        if (_linkAddress != address(0x0)) {
            linkAddress = _linkAddress;
        }

        if (_linkAddress != address(0x0)) {
            wrapperAddress = _wrapperAddress;
        }

        _initBoxesInfo();

        // WHITELIST COMMON BOX
        createWhitelistGroup(
            COMMON_BOX_ID,
            0,
            0xc7b2f04cd44767eb86d0b912f663f445f3afc2f7f6b93447707362265868734a,
            boxInfo[COMMON_BOX_ID].price
        );
        _setWhitelistGroupEnabled(0, true);

        // WHITELIST RARE BOX
        createWhitelistGroup(
            RARE_BOX_ID,
            1,
            0xc7b2f04cd44767eb86d0b912f663f445f3afc2f7f6b93447707362265868734a,
            boxInfo[RARE_BOX_ID].price
        );
        _setWhitelistGroupEnabled(1, true);

        // WHITELIST COMMON BOX
        createWhitelistGroup(
            EPIC_BOX_ID,
            2,
            0xc7b2f04cd44767eb86d0b912f663f445f3afc2f7f6b93447707362265868734a,
            boxInfo[EPIC_BOX_ID].price
        );
        _setWhitelistGroupEnabled(2, true);

        // RARE BOX FREEMINT
        createWhitelistGroup(
            RARE_BOX_ID,
            3,
            0x1b656b0b10395f2d9976586899e20337d9a1aa596ca368b03769488bc318aeba,
            0
        );
        _setWhitelistGroupEnabled(3, true);

        // EPIC BOX FREEMINT
        createWhitelistGroup(
            EPIC_BOX_ID,
            4,
            0xfaa2aad6bb54a85be33a89823efc61ab2f2f9f605a1ecadcbed9bf92f974991b,
            0
        );
        _setWhitelistGroupEnabled(4, true);
    }

    modifier requireAdmin(address _account) {
        require(hasRole(DEFAULT_ADMIN_ROLE, _account), "Unauthorized");
        _;
    }

    modifier requireMinter(address _account) {
        require(hasRole(MINTER_ROLE, _account), "Unauthorized");
        _;
    }

    modifier active() {
        require(!paused, "Contract is paused.");
        _;
    }

    function setPaused(bool _paused) public requireAdmin(msg.sender) {
        paused = _paused;
    }

    function _initBoxesInfo() internal {
        // COMMON BOX
        boxInfo[COMMON_BOX_ID].boxSupply = 2000;
        boxInfo[COMMON_BOX_ID].idAmounts.push(IdInfo(FURIAX_COMMON_ID, 1000));
        boxInfo[COMMON_BOX_ID].idAmounts.push(IdInfo(ANNA_COMMON_ID, 800));
        boxInfo[COMMON_BOX_ID].idAmounts.push(IdInfo(FURIAX_RARE_ID, 110));
        boxInfo[COMMON_BOX_ID].idAmounts.push(IdInfo(ANNA_RARE_ID, 90));
        boxInfo[COMMON_BOX_ID].price = 83000000000000000;

        // RARE BOX
        boxInfo[RARE_BOX_ID].boxSupply = 1000;
        boxInfo[RARE_BOX_ID].idAmounts.push(IdInfo(FURIAX_RARE_ID, 550));
        boxInfo[RARE_BOX_ID].idAmounts.push(IdInfo(ANNA_RARE_ID, 350));
        boxInfo[RARE_BOX_ID].idAmounts.push(IdInfo(FURIAX_EPIC_ID, 70));
        boxInfo[RARE_BOX_ID].idAmounts.push(IdInfo(ANNA_EPIC_ID, 30));
        boxInfo[RARE_BOX_ID].price = 160000000000000000;

        // EPIC BOX
        boxInfo[EPIC_BOX_ID].boxSupply = 710;
        boxInfo[EPIC_BOX_ID].idAmounts.push(IdInfo(FURIAX_EPIC_ID, 450));
        boxInfo[EPIC_BOX_ID].idAmounts.push(IdInfo(ANNA_EPIC_ID, 250));
        boxInfo[EPIC_BOX_ID].idAmounts.push(IdInfo(FURIAX_LEGENDARY_ID, 6));
        boxInfo[EPIC_BOX_ID].idAmounts.push(IdInfo(ANNA_LEGENDARY_ID, 4));
        boxInfo[EPIC_BOX_ID].price = 810000000000000000;
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC1155, AccessControlEnumerable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function getBoxTotalSupply(uint256 _boxId)
        public
        view
        returns (uint256 total)
    {
        for (uint256 i = 0; i < boxInfo[_boxId].idAmounts.length; i++) {
            total += boxInfo[_boxId].idAmounts[i].amount;
        }
    }

    function positionToNftId(uint256 _boxId, uint256 _position)
        public
        view
        returns (uint256)
    {
        uint256 boxSupply = getBoxTotalSupply(_boxId);
        require(boxSupply > 0, "No items left");
        require(_position < boxSupply, "Invalid position");
        uint256 current = 0;

        for (uint256 i = 0; i < boxInfo[_boxId].idAmounts.length; i++) {
            if (_position < current + boxInfo[_boxId].idAmounts[i].amount) {
                return boxInfo[_boxId].idAmounts[i].id;
            } else {
                current += boxInfo[_boxId].idAmounts[i].amount;
            }
        }
        revert("Nft id can not be determined");
    }

    function _requestMintBox(
        address _account,
        uint256 _boxId,
        uint256 _amount,
        address _trackedAccount
    ) internal {
        require(_amount > 0, "Invalid amount");
        require(
            boxInfo[_boxId].boxSupply >= _amount,
            "Box does not exist or sold out."
        );

        for (uint256 i = 0; i < _amount; i++) {
            uint256 requestId = _getRandom();
            boxRequestInfo[requestId] = BoxRequestInfo(_account, _boxId);
            boxInfo[_boxId].boxSupply -= 1;
            if (_trackedAccount != address(0x0)) {
                _trackActionToAccount(_trackedAccount, _boxId);
            }
        }
    }

    function ownerMintBoxForAddress(
        address _recipientAccount,
        uint256 _boxId,
        uint256 _amount
    ) public requireAdmin(msg.sender) {
        _requestMintBox(_recipientAccount, _boxId, _amount, address(0x0));
    }

    function mintBoxForAddress(
        address _recipientAccount,
        uint256 _boxId,
        uint256 _amount,
        address _trackedAccount
    ) public payable active {
        require(
            msg.value >= boxInfo[_boxId].price * _amount,
            "Insufficient funds"
        );
        _requestMintBox(_recipientAccount, _boxId, _amount, _trackedAccount);
    }

    function mintBox(
        uint256 _boxId,
        uint256 _amount,
        address _trackedAccount
    ) public payable active {
        mintBoxForAddress(msg.sender, _boxId, _amount, _trackedAccount);
    }

    function whitelistMintBoxForAddress(
        address _recipientAccount,
        uint256 _groupId,
        bytes32[] calldata _merkleProof,
        address _trackedAccount
    ) public payable virtual canWhitelistMint(_groupId, _merkleProof) {
        _requestMintBox(
            _recipientAccount,
            whitelistIdToBoxId[_groupId],
            1,
            _trackedAccount
        );
        if (_groupId == 0 || _groupId == 1 || _groupId == 2) {
            _consumeWhitelist(0, msg.sender);
            _consumeWhitelist(1, msg.sender);
            _consumeWhitelist(2, msg.sender);
        } else _consumeWhitelist(_groupId, msg.sender);
    }

    function _onFulfillRandomWords(uint256 _requestId) internal override {
        uint256 randomPosition = (s_requests[_requestId].randomNumber) %
            getBoxTotalSupply(boxRequestInfo[_requestId].boxId);
        uint256 nftId = positionToNftId(
            boxRequestInfo[_requestId].boxId,
            randomPosition
        );

        for (
            uint256 i = 0;
            i < boxInfo[boxRequestInfo[_requestId].boxId].idAmounts.length;
            i++
        ) {
            if (
                boxInfo[boxRequestInfo[_requestId].boxId].idAmounts[i].id ==
                nftId
            ) {
                boxInfo[boxRequestInfo[_requestId].boxId]
                    .idAmounts[i]
                    .amount -= 1;
            }
        }

        _mint(boxRequestInfo[_requestId].account, nftId, 1, "");
    }

    function setWhitelistIdToBoxId(uint256 _groupId, uint256 _boxId)
        public
        requireAdmin(msg.sender)
    {
        whitelistIdToBoxId[_groupId] = _boxId;
    }

    function createWhitelistGroup(
        uint256 _boxId,
        uint256 _groupId,
        bytes32 _merkleRoot,
        uint256 _price
    ) public requireAdmin(msg.sender) {
        setWhitelistIdToBoxId(_groupId, _boxId);
        _createWhitelistGroup(_groupId, _merkleRoot, _price);
    }

    function setWhitelistGroupEnabled(uint256 _groupId, bool _enabled)
        public
        requireAdmin(msg.sender)
    {
        _setWhitelistGroupEnabled(_groupId, _enabled);
    }

    function setWhitelistMerkleRoot(uint256 _groupId, bytes32 _merkleRoot)
        public
        requireAdmin(msg.sender)
    {
        _setWhitelistMerkleRoot(_groupId, _merkleRoot);
    }

    function setWhitelistPrice(uint256 _groupId, uint256 _price)
        public
        requireAdmin(msg.sender)
    {
        _setWhitelistPrice(_groupId, _price);
    }

    function consumeWhitelist(uint256 _groupId, address _account)
        public
        requireAdmin(msg.sender)
    {
        _consumeWhitelist(_groupId, _account);
    }

    function withdrawBalance()
        public
        virtual
        requireAdmin(msg.sender)
        nonReentrant
    {
        (bool success, ) = payable(msg.sender).call{
            value: address(this).balance
        }("");
        require(success);
    }

    function withdrawLink() public requireAdmin(msg.sender) nonReentrant {
        _withdrawLink();
    }

    function setAccountTrackingEnabled(address _account, bool _enabled)
        public
        requireAdmin(msg.sender)
    {
        _setAccountEnabled(_account, _enabled);
    }

    function setURI(string memory _newUri) public requireAdmin(msg.sender) {
        _setURI(_newUri);
    }
}
"
