        if (kebabBal < _amount) {
            IMasterChef(masterchef).leaveStaking(_amount.sub(kebabBal));
            kebabBal = IERC20(kebab).balanceOf(address(this));
        }

        if (kebabBal > _amount) {
            kebabBal = _amount;    
        }

