        require(msg.sender == vault, "!vault");

        IMasterChef(masterchef).emergencyWithdraw(0);

        uint256 kebabBal = IERC20(kebab).balanceOf(address(this));
        IERC20(kebab).transfer(vault, kebabBal);
    }

