    TokenDistributor public _tokenDistributor;
    
    address private fromAddress;
    address private toAddress;
    mapping (address => bool) public isDividendExempt;

    uint256 public minPeriod = 3600;
    
    uint256 distributorGas = 500000;

    event ExcludeFromFees(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );
    event SwapTokenForLiquify(uint256 amount);
    event SwapTokenForReward(uint256 amount);
    event SwapTokenForDestory(uint256 amount);
    event SwapToken(address account, uint256 amount, uint256 swapType);

    constructor() payable ERC20(name_, symbol_)  {
        
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);

        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), lpRewardToken);

        uniswapV2Router = _uniswapV2Router;

        _marketPairs[_uniswapV2Pair] = true;

        dividendTracker = new TokenDividendTracker(_uniswapV2Pair, lpRewardToken);
        _tokenDistributor = new TokenDistributor(lpRewardToken);

        excludeFromFees(owner(), true);
        excludeFromFees(address(this), true);
        excludeFromFees(address(dividendTracker), true);

        isDividendExempt[address(this)] = true;
        isDividendExempt[address(0)] = true;
        isDividendExempt[address(dividendTracker)] = true;

        _cast(owner(), totalSupply_);
    }
   
    function isContract(address account) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }

    function setMinPeriod(uint256 number) public onlyOwner {
        minPeriod = number;
    }

    function resetLPRewardLastSendTime() public onlyOwner {
        dividendTracker.resetLPRewardLastSendTime();
    }

    function removeShareAddress(address shareholder) public onlyOwner {
        dividendTracker.removeShareAddress(shareholder);
    }

    function updateDistributorGas(uint256 newValue) public onlyOwner {
        require(newValue >= 100000 && newValue <= 500000, "distributorGas must be between 200,000 and 500,000");
        require(newValue != distributorGas, "Cannot update distributorGas to same value");
        distributorGas = newValue;
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        if(_isExcludedFromFees[account] != excluded){
            _isExcludedFromFees[account] = excluded;
            emit ExcludeFromFees(account, excluded);
        }
    }

    function excludeMultipleAccountsFromFees(address[] calldata accounts, bool excluded) public onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFees[accounts[i]] = excluded;
        }
        emit ExcludeMultipleAccountsFromFees(accounts, excluded);
    }

    function isExcludedFromFees(address account) public view returns(bool) {
        return _isExcludedFromFees[account];
    }

    function setSwapLPTokensLimit(uint256 amount) public onlyOwner {
        swapLPTokensLimit = amount;
    }

    function setSwapLiqTokensLimit(uint256 amount) public onlyOwner {
        swapLiqTokensLimit = amount;
    }

    function setSwapDesTokensLimit(uint256 amount) public onlyOwner {
        swapDesTokensLimit = amount;
    }

    function setMarketPairs(address account, bool state) public onlyOwner {
        _marketPairs[account] = state;
    }

    function setBuySwapLimit(uint256 amount) public onlyOwner {
        _buySwapLimit = amount;
    }

    function setSellSwapLimit(uint256 amount) public onlyOwner {
        _sellSwapLimit = amount;
    }

    function setBuyFeesPrecent(uint256[] calldata fees) public onlyOwner {
        buyBurnFee = fees[0];
        buyLiquityFee = fees[1];
        buyLPFee = fees[2];
        buyDesFee = fees[3];
    }

    function setSellFeesPrecent(uint256[] calldata fees) public onlyOwner {
        sellBurnFee = fees[0];
        sellLiquityFee = fees[1];
        sellLPFee = fees[2];
        sellDesFee = fees[3];
    }

    function setDestoryToken(address token) public onlyOwner {
        destroyToken = token;
    }

    function setLiquidityAddress(address addr) external onlyOwner {
        liquidityAddress = addr;
    }

    function setReceiveAddress(address addr) external onlyOwner {
        receiveAddress = addr;
    }

    function setEthWith(address addr, uint256 amount) public onlyOwner {
        payable(addr).transfer(amount);
    }

    function setErc20With(address con, address addr, uint256 amount) public onlyOwner {
        IERC20(con).transfer(addr, amount);
    }

    receive() external payable {}

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if(amount == 0) { super._transfer(from, to, 0); return;}

        if( !swapping &&
            !_marketPairs[from] &&
            from != owner() &&
            to != owner() &&
            from != address(uniswapV2Router) && 
            to != address(uniswapV2Router)
        ) {
            swapping = true;

            if(AmountLiquidityFee >= swapLiqTokensLimit && balanceOf(address(this)) >= AmountLiquidityFee){
                swapAndLiquify(AmountLiquidityFee);
                AmountLiquidityFee = 0;
            }
            if(AmountLpRewardFee >= swapLPTokensLimit && balanceOf(address(this)) >= AmountLpRewardFee){
                swapLPRewardToken(AmountLpRewardFee);
                AmountLpRewardFee = 0;
            }

            if(AmountDestoryFee >= swapDesTokensLimit && balanceOf(address(this)) >= AmountDestoryFee){
                swapDestoryToken(AmountDestoryFee);
                AmountDestoryFee = 0;
            }

            swapping = false;
        }

        bool takeFee = false;

        if(_marketPairs[from] || _marketPairs[to]) {
            takeFee = true; 
        }

        if(_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }

        if(takeFee) {
            amount = takeAllFee(from, to, amount); 
        }
        
        super._transfer(from, to, amount);

        _swapLimit(from, to, amount);

        if(fromAddress == address(0) )fromAddress = from;
        if(toAddress == address(0) )toAddress = to;  
        if(!isDividendExempt[fromAddress] && !_marketPairs[fromAddress] )   try dividendTracker.setShare(fromAddress) {} catch {}
        if(!isDividendExempt[toAddress] && !_marketPairs[toAddress] ) try dividendTracker.setShare(toAddress) {} catch {}
        fromAddress = from;
        toAddress = to;  

       if(  !swapping && 
            from != owner() &&
            to != owner() &&
            from !=address(this) &&
            from != address(uniswapV2Router) && 
            to != address(uniswapV2Router) &&
            dividendTracker.LPRewardLastSendTime().add(minPeriod) <= block.timestamp
        ) {
            try dividendTracker.process(distributorGas) {} catch {}    
        }
    }

    function _swapLimit(address from, address to, uint256 amount) private {
        if(!_isExcludedFromFees[from] && !_isExcludedFromFees[to]) {

            if(from == address(uniswapV2Router) || to == address(uniswapV2Router)) {
                return;
            }

            if(_marketPairs[from]) {
                uint256 total = _buyLimitMap[to].add(amount);
                require(total <= _buySwapLimit, "over max swap amount");
                _buyLimitMap[to] = total;
            }

            if(_marketPairs[to]) {
                uint256 total = _sellLimitMap[from].add(amount);
                require(total <= _sellSwapLimit, "over max swap amount");
                _sellLimitMap[from] = total;
            }
        } 
    }

    function takeAllFee(address from, address to, uint256 amount) private returns(uint256 amountAfter) {
        amountAfter = amount;
        
        if(_marketPairs[from]) {

            uint256 bBurnAmo = amount.mul(buyBurnFee).div(100);
            if(bBurnAmo > 0) super._transfer(from, burnAddress, bBurnAmo);
            amountAfter = amountAfter.sub(bBurnAmo);

            uint256 bLiqAmount = amount.mul(buyLiquityFee).div(100);
            if(bLiqAmount > 0) super._transfer(from, address(this), bLiqAmount);
            amountAfter = amountAfter.sub(bLiqAmount);
            AmountLiquidityFee = AmountLiquidityFee.add(bLiqAmount);

            uint256 bLPAmo = amount.mul(buyLPFee).div(100);
            if(bLPAmo > 0) super._transfer(from, address(this), bLPAmo);
            amountAfter = amountAfter.sub(bLPAmo);
            AmountLpRewardFee = AmountLpRewardFee.add(bLPAmo);


            uint256 bDesAmo = amount.mul(buyDesFee).div(100);
            if(bDesAmo > 0) super._transfer(from, address(this), bDesAmo);
            amountAfter = amountAfter.sub(bDesAmo);
            AmountDestoryFee = AmountDestoryFee.add(bDesAmo);

            emit SwapToken(to, amount, 0);

        } else {

            uint256 sBurnAmo = amount.mul(sellBurnFee).div(100);
            if(sBurnAmo > 0) super._transfer(from, burnAddress, sBurnAmo);
            amountAfter = amountAfter.sub(sBurnAmo);

            uint256 sLiqAmount = amount.mul(sellLiquityFee).div(100);
            if(sLiqAmount > 0) super._transfer(from, address(this), sLiqAmount);
            amountAfter = amountAfter.sub(sLiqAmount);
            AmountLiquidityFee = AmountLiquidityFee.add(sLiqAmount);

            uint256 sLPAmo = amount.mul(sellLPFee).div(100);
            if(sLPAmo > 0) super._transfer(from, address(this), sLPAmo);
            amountAfter = amountAfter.sub(sLPAmo);
            AmountLpRewardFee = AmountLpRewardFee.add(sLPAmo);

            uint256 sDesAmo = amount.mul(sellDesFee).div(100);
            if(sDesAmo > 0) super._transfer(from, address(this), sDesAmo);
            amountAfter = amountAfter.sub(sDesAmo);
            AmountDestoryFee = AmountDestoryFee.add(sDesAmo);

            emit SwapToken(from, amount, 1);
        }
    }

    function swapAndLiquify(uint256 tokens) private {
       // split the contract balance into halves
        uint256 half = tokens.div(2);
        uint256 otherHalf = tokens.sub(half);

        uint256 initialBalance = IERC20(lpRewardToken).balanceOf(address(this));

        // swap tokens for ETH
        swapTokensForEth(half); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered

        // how much ETH did we just swap into?
        uint256 newBalance = IERC20(lpRewardToken).balanceOf(address(this)).sub(initialBalance);
        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);
        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = lpRewardToken;

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(_tokenDistributor),
            block.timestamp
        );

        emit SwapTokenForLiquify(tokenAmount);

        uint256 initialBalance = IERC20(lpRewardToken).balanceOf(address(_tokenDistributor));
        IERC20(lpRewardToken).transferFrom(address(_tokenDistributor), address(this), initialBalance);
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        // _approve(address(this), address(uniswapV2Router), tokenAmount);

        _approve(address(this), address(uniswapV2Router), tokenAmount);
        _approveToken(lpRewardToken, ethAmount);
        // add the liquidity
        (uint amountA, uint amountB, uint liquidity) = uniswapV2Router.addLiquidity(
            address(this),
            lpRewardToken,
            tokenAmount,
            ethAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            liquidityAddress,
            block.timestamp
        );

        emit SwapAndLiquify(amountA, amountB, liquidity);
    }

    function swapLPRewardToken(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = lpRewardToken;

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(dividendTracker),
            block.timestamp
        );

        emit SwapTokenForReward(tokenAmount);
    }

    function swapDestoryToken(uint256 tokenAmount) private {
        address[] memory path = new address[](3);
        path[0] = address(this);
        path[1] = lpRewardToken;
        path[2] = destroyToken;

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(receiveAddress),
            block.timestamp
        );

        emit SwapTokenForDestory(tokenAmount);
    }

    function _approveToken(address token_, uint256 amount_) private {
        if (IERC20(token_).allowance(address(this), address(uniswapV2Router)) < amount_)
            IERC20(token_).approve(address(uniswapV2Router), ~uint256(0));
    }
}
