    "/contracts/StakingV3.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.7.6;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/utils/EnumerableSet.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";

contract StakingV3 is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.AddressSet;

    EnumerableSet.AddressSet stakers;

    IERC20 public immutable MNST;

    constructor(address _mnst) {
        MNST = IERC20(_mnst);
    }

    // 4 Spots can pretend to white list IDO
    string[5] private _tiers = [
        "None",
        "Callisto",
        "Elara",
        "Himalia",
        "Kalyke"
    ];
    uint256[5] private _amountNeeded; // Number of MNST needed to get tier statut
    uint256[5] private _periodStaking; // Number of blocks

    uint256 public coolDownDuration = 7 days;
    uint256 public cosmos30Duration = 30 days;
    uint256 public cosmos60Duration = 60 days;
    uint256 public cosmos90Duration = 90 days;

    uint256 public inCoolDownProcess;

    // All deposits of user are stored here
    // [block.timestamp, amount]
    mapping(address => uint256[2][]) public deposits;

    struct User {
        bool isStaking;
        uint256 stakingAmount;
        uint256 startingStaking;
        uint256 coolDownEndTimestamp;
    }

    mapping(address => User) public users;
    mapping(address => bool) public cosmos30Registered;
    mapping(address => bool) public cosmos60Registered;
    mapping(address => bool) public cosmos90Registered;

    event NewDeposit(
        address indexed user,
        uint256 amount,
        uint256 blockTimestamp
    );

    event NewUnStaking(
        address indexed user,
        uint256 amount,
        uint256 blockTimestamp
    );

    function getNumberOfStakers() external view returns(uint256){
        return stakers.length();
    }

    function getStakerByIndex(uint256 _index) external view returns(
        address user,
        bool isCosmosSatus,
        uint256 cosmostype,
        string memory tierName, 
        uint256 amountValidated ){
        address _staker = stakers.at(_index);
        (bool _isCosmosStatus, uint256 _cosmosType)=_isCosmos(_staker);
        (string memory _tierName, uint256 _amountValidated) = _getUserStakingData(_staker);

        return (_staker,_isCosmosStatus, _cosmosType, _tierName, _amountValidated);
    }

    function coolDownEndTimestamp(address _user)
        external
        view
        returns (uint256)
    {
        return users[_user].coolDownEndTimestamp;
    }

    function _getCoolDownFinished(address _user) internal view returns(bool){
        if(users[_user].coolDownEndTimestamp == 0){
            return false;
        } else{
            return block.timestamp >= users[_user].coolDownEndTimestamp;
        }
    }

    function isCosmos(address _user) external view returns (bool isCosmosStatus, uint256 cosmostype){
        return _isCosmos(_user);
    }

    function _isCosmos(address _user) internal view returns (bool isCosmosStatus, uint256 cosmosType) {
        if(_getCoolDownFinished(_user)){
            return (false,0);
        }else if(users[_user].startingStaking == 0){
            return (false,0);
        }else if(
            cosmos90Registered[_user] ||
            block.timestamp.sub(users[_user].startingStaking) >=cosmos90Duration
            ){
            return (true,90);

        }else if(
            cosmos60Registered[_user] ||
            block.timestamp.sub(users[_user].startingStaking) >=cosmos60Duration
            ){
            return (true,60);

        }else if(
            cosmos30Registered[_user] ||
            block.timestamp.sub(users[_user].startingStaking) >=cosmos30Duration
            ){
            return (true,30);

        }else{
            return(false,0);
        }
    }

    // _coolDownDuration must be between 1 and 30 (number of days)
    function setCoolDownTiming(uint256 _coolDownDuration) external onlyOwner { 
        require(_coolDownDuration > 0 && _coolDownDuration <= 30, "Duration not accepted");
        coolDownDuration = _coolDownDuration.mul(1 days);
    }

    // setting tiers amounts and period
    function setTiers(
        uint256 _tierIndex,
        uint256 _amount,
        uint256 _period // in days
    ) external onlyOwner {
        require(_tierIndex <= 4, "Not existing tiers");
        require(_amount > 0, "amount can't be Zero");
        require(_period > 0, "period can't be Zero");
        _amountNeeded[_tierIndex] = _amount;
        _periodStaking[_tierIndex] = _period.mul(1 days);
    }

    // get number of deposit for an user,
    // used in front end to enumerate all deposits in a for loop
    function getNumberOfDeposits(address _user)
        external
        view
        returns (uint256)
    {
        return deposits[_user].length;
    }

    // Get tiers datas
    function getTiersData(uint256 _tierIndex)
        external
        view
        returns (
            string memory tierName,
            uint256 amountMini,
            uint256 periodMini
        )
    {
        return (
            _tiers[_tierIndex],
            _amountNeeded[_tierIndex],
            _periodStaking[_tierIndex]
        );
    }

    // return the tier and amount validated of an user at calling block number
    // Function used by MoonStarter team at snapshot
    function getUserStakingData(address _user)
        external
        view
        returns (string memory tierName, uint256 amountValidated){
            return _getUserStakingData(_user);
    }

    function _getUserStakingData(address _user)
        internal
        view
        returns (string memory tierName, uint256 amountValidated)
    {
        User memory u = users[_user];
        // if user doesn't staking or his coolDown is finished, return "NONE" and 0 amount
        if (!u.isStaking || _getCoolDownFinished(_user)) {
            return (_tiers[0], 0);
        }

        // the amount validated for staking user
        // need to have a staking duration for this tier
        uint256 _amount;
        uint256 _tierIndex;

        if(cosmos30Registered[_user] || cosmos60Registered[_user] || cosmos90Registered[_user]){
            _amount = u.stakingAmount;
            if(_amount >= _amountNeeded[4]){
                _tierIndex = 4;
            }else if(_amount >= _amountNeeded[3]){
                _tierIndex = 3;
            }else if(_amount >= _amountNeeded[2]){
                _tierIndex = 2;
            }else{
                _tierIndex = 1;
            }
        }
        else{
            // loop in the user's deposits list
            for (uint256 i = 0; i < deposits[_user].length; i++) {
                uint256[2] memory d = deposits[_user][i];

                // check all tier beginning by the higher tier
                // check first if deposit have the good period staking for this tier
                // and if amount is tier elligible
                if (
                    block.timestamp >= d[0].add(_periodStaking[4]) &&
                    (d[1] >= _amountNeeded[4] ||
                        _amount.add(d[1]) >= _amountNeeded[4])
                ) {
                    //upgrade tier if needed
                    if (_tierIndex < 4) {
                        _tierIndex = 4;
                    }
                    if (_tierIndex == 4) {
                        //add deposit to amount validated if tier allowed
                        _amount = _amount.add(d[1]);
                    }
                } else if (
                    block.timestamp >= d[0].add(_periodStaking[3]) &&
                    (d[1] >= _amountNeeded[3] ||
                        _amount.add(d[1]) >= _amountNeeded[3])
                ) {
                    if (_tierIndex < 3) {
                        _tierIndex = 3;
                    }
                    if (_tierIndex == 3) {
                        _amount = _amount.add(d[1]);
                    }
                } else if (
                    block.timestamp >= d[0].add(_periodStaking[2]) &&
                    (d[1] >= _amountNeeded[2] ||
                        _amount.add(d[1]) >= _amountNeeded[2])
                ) {
                    if (_tierIndex < 2) {
                        _tierIndex = 2;
                    }
                    if (_tierIndex == 2) {
                        _amount = _amount.add(d[1]);
                    }
                } else if (
                    block.timestamp >= d[0].add(_periodStaking[1]) &&
                    (d[1] >= _amountNeeded[1] ||
                        _amount.add(d[1]) >= _amountNeeded[1])
                ) {
                    if (_tierIndex == 0) {
                        _tierIndex = 1;
                    }
                    if (_tierIndex == 1) {
                        _amount = _amount.add(d[1]);
                    }
                }
            }
        }

        return (_tiers[_tierIndex], _amount);
    }

    function chooseToBeCosmos(uint256 _cosmosType) external {
        require(_cosmosType == 30 || _cosmosType == 60 || _cosmosType ==90,'Not a good duration');
        require(users[msg.sender].isStaking, "You need to stake some MNST");
        require(
            users[msg.sender].coolDownEndTimestamp == 0,
            "You can't registered to cosmos in cool down period"
        );
        if(_cosmosType == 30){
            cosmos30Registered[msg.sender] = true;
        }
        if(_cosmosType == 60){
            cosmos60Registered[msg.sender] = true;
        }
        if(_cosmosType == 90){
            cosmos90Registered[msg.sender] = true;
        }
    }

    // Staking
    function staking(uint256 _amount) external {
        require(tx.origin == msg.sender, "Contracts not allowed.");
        User storage u = users[msg.sender];

        require(
            u.coolDownEndTimestamp == 0,
            "You can't stake in cool down period"
        );
        // if first deposit, amount need to be the minimal tier
        if (!u.isStaking) {
            require(_amount >= _amountNeeded[1], "Not enough amount");
            u.isStaking = true;
            u.startingStaking = block.timestamp;
            stakers.add(msg.sender);
        }

        require(MNST.balanceOf(msg.sender) >= _amount, "Not enough Balance");

        require(MNST.transferFrom(msg.sender, address(this), _amount));

        deposits[msg.sender].push([block.timestamp, _amount]);

        // update total amount staking
        u.stakingAmount = u.stakingAmount.add(_amount);
        emit NewDeposit(msg.sender, _amount, block.timestamp);
    }

    function canInitCoolDown(address _user) external view returns(bool canInit, uint256 initiableTimestamp){
        return _canInitCoolDown(_user);
    }

    function _canInitCoolDown(address _user) internal view returns(bool canInit, uint256 initiableTimestamp){
        if(!users[_user].isStaking){
            return (false,0);
        }else if(cosmos90Registered[_user]){
            return (
                block.timestamp >= users[_user].startingStaking.add(cosmos90Duration),
                users[_user].startingStaking.add(cosmos90Duration)
                );
        }else if(cosmos60Registered[_user]){
            return (
                block.timestamp >= users[_user].startingStaking.add(cosmos60Duration),
                users[_user].startingStaking.add(cosmos60Duration)
                );
        } else if(cosmos30Registered[_user]){
            return (
                block.timestamp >= users[_user].startingStaking.add(cosmos30Duration),
                users[_user].startingStaking.add(cosmos30Duration)
                );
        }else{
            return (
                true,
                0
                );
        }
    }

    function initCoolDown() external {
        require(users[msg.sender].isStaking, "You are not a staker");
        require(users[msg.sender].coolDownEndTimestamp == 0, "You already are in coolDown process");
        (bool _canInit, ) = _canInitCoolDown(msg.sender);
        require(_canInit,"You need to wait");
        users[msg.sender].coolDownEndTimestamp = block.timestamp.add(coolDownDuration);
        inCoolDownProcess = inCoolDownProcess.add(users[msg.sender].stakingAmount);
    }

    // Unstaking meens delete all status and history deposit
    // User unstaking loose all his tier right
    function unStake() external returns (bool) {
        require(users[msg.sender].isStaking, "Nothing to unstak");
        require(
            users[msg.sender].coolDownEndTimestamp != 0,
            "You must init a cool down period before unstaking"
        );
        require(
            block.timestamp >=
                users[msg.sender].coolDownEndTimestamp,
            "Your cool down period isn't finished"
        );
        // Re entry secure
        uint256 _amount = users[msg.sender].stakingAmount;

        // delete all users datas
        delete users[msg.sender];
        delete deposits[msg.sender];
        stakers.remove(msg.sender);

        if(cosmos30Registered[msg.sender]){
            cosmos30Registered[msg.sender] = false;
        }
        if(cosmos60Registered[msg.sender]){
            cosmos60Registered[msg.sender] = false;
        }
        if(cosmos90Registered[msg.sender]){
            cosmos90Registered[msg.sender] = false;
        }
        emit NewUnStaking(msg.sender, _amount, block.timestamp);

        inCoolDownProcess = inCoolDownProcess.sub(_amount);

        // return back MNST staked to user
        return MNST.transfer(msg.sender, _amount);
    }
}
