        _nonReentrantBefore();
        _;
        _nonReentrantAfter();
    }

    function _nonReentrantBefore() private {
        // On the first call to nonReentrant, _status will be _NOT_ENTERED
        require(_status != _ENTERED, "ReentrancyGuard: reentrant call");

        // Any calls to nonReentrant after this point will fail
        _status = _ENTERED;
    }

    function _nonReentrantAfter() private {
        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        _status = _NOT_ENTERED;
    }

    /**
     * @dev Returns true if the reentrancy guard is currently set to "entered", which indicates there is a
     * `nonReentrant` function in the call stack.
     */
    function _reentrancyGuardEntered() internal view returns (bool) {
        return _status == _ENTERED;
    }
}

contract MoonNFTs is ERC721Enumerable, Ownable, ReentrancyGuard {
    using Strings for uint256;
    using SafeMath for uint256;
    using Counters for Counters.Counter;

    // Constants
    string public baseExtension = ".json";
    uint256 public cost = 0.15 ether;

    // Supply
    uint256 public mintReward = 60;
    uint256 public maxSupply = 1500;
    uint256 public lastSupply = maxSupply;
    uint256 public maxMintAmount = 10;

    // Reflection
    uint256 public reflectionBalance;
    uint256 public totalDividend;

    // Lists
    uint256[1500] public remainingIds;
    mapping(uint256 => uint256) public lastDividendAt;
    mapping(uint256 => address) public minters;

    // Params
    bool public paused = true;
    string private _baseTokenURI;
    string private _contractURI;
    address private _wallet;

    // Addresses
    address public DexRouter = 0x10ED43C718714eb63d5aA57B78B54704E256024E; //0x10ED43C718714eb63d5aA57B78B54704E256024E
    address public FeeReceiver = 0x4f4E12f0bB320Db9B1da5642d3de207f198921Db; //0x4f4E12f0bB320Db9B1da5642d3de207f198921Db


    // Rewards
    address public RewardToken1 = 0x2134374eeEfF09a3c4c7Fd9A5D83Bb5Baef4385E; // 0x2134374eeEfF09a3c4c7Fd9A5D83Bb5Baef4385E
    address public RewardToken2 = 0x70a8Efa4499a1e1913AE3B13C4A8B529BF228d4b; // 0x70a8Efa4499a1e1913AE3B13C4A8B529BF228d4b
    uint256 public RewardToken1Share = 50;

    //Contracts
    IUniswapV2Router02 public router;
    IERC20 public token1;
    IERC20 public token2;

    // Constructor
    constructor() ERC721("Moon Project NFTs", "MOONFT") {
        _baseTokenURI = "ipfs://bafybeif5dbzj4hbtm7nce4s2pyu4uv56c54p23mge6e53zbl6msxbe5y2q/";
        _contractURI = "ipfs://bafkreieqweh2xqg4uxdqbc4ejtakt3wmv66mx3aghogb4buitregaq4ysa/";
        _wallet = msg.sender;
        router = IUniswapV2Router02(DexRouter);
        token1 = IERC20(RewardToken1);
        token2 = IERC20(RewardToken2);
    }

    function contractURI() public view returns (string memory) {
        return _contractURI;
    }

    // URI Handling
    function _baseURI() internal view virtual override returns (string memory) {
        return _baseTokenURI;
    }

    // Setters
    function setBaseURI(string memory baseURI) external onlyOwner {
        _baseTokenURI = baseURI;
    }

    function setContractURI(string memory contractURI_) external onlyOwner {
        _contractURI = contractURI_;
    }

    function setCost(uint256 _newCost) public onlyOwner {
        cost = _newCost;
    }

    function setmaxMintAmount(uint256 _newmaxMintAmount) public onlyOwner {
        maxMintAmount = _newmaxMintAmount;
    }

    function setBaseExtension(string memory _newBaseExtension)
        public
        onlyOwner
    {
        baseExtension = _newBaseExtension;
    }

    function pause(bool _state) public onlyOwner {
        paused = _state;
    }

    function setRewardPercentSplit(uint256 _newRewardToken1Share) public onlyOwner {
        RewardToken1Share = _newRewardToken1Share;
    }

    function setRewardTokens(address _newRewardToken1, address _newRewardToken2) public onlyOwner {
        RewardToken1 = _newRewardToken1;
        token1 = IERC20(RewardToken1);
        RewardToken2 = _newRewardToken2;
        token2 = IERC20(RewardToken2);
    }

    function setDexRouter(address _newDexRouter) public onlyOwner {
        DexRouter = _newDexRouter;
        router = IUniswapV2Router02(DexRouter);
    }

    function setFeeReceiver(address _newFeeReceiver) public onlyOwner {
        FeeReceiver = _newFeeReceiver;
    }

    function setMintShare(uint256 _newMintShare) public onlyOwner {
        mintReward = _newMintShare;
    }

    // Minting
    function mint(uint256 _mintAmount) public payable {
        // Checks
        require(!paused, "MoonNFTs: minting has not started yet");
        require(_mintAmount > 0, "MoonNFTs: you have to mint at least one");
        require(
            _mintAmount <= maxMintAmount,
            "MoonNFTs: you can only mint 10 at a time"
        );
        require(
            lastSupply >= _mintAmount,
            "MoonNFTs: the collection is sold out"
        );
        if (msg.sender != owner()) {
            require(
                msg.value >= cost * _mintAmount,
                "MoonNFTs: total cost isn't match"
            );
        }
        // Minting
        for (uint256 i = 1; i <= _mintAmount; i++) {
            // Mint for caller
            _randomMint(msg.sender);
            // Split cost
            handleMintReward(msg.value / _mintAmount);
        }
    }

    // Give a random NFT to a contest winner
    function randomGiveaway(address _winner, uint256 _amount)
        external
        onlyOwner
    {
        // Checks
        require(_winner != address(0), "MoonNFTs: zero address");
        require(_amount > 0, "MoonNFTs: you have to mint at least one");
        require(lastSupply != 0, "MoonNFTs: the collection is sold out");
        // Mint random NFTs for a winner
        for (uint256 i = 1; i <= _amount; i++) {
            _randomMint(_winner);
        }
    }

    // Random mint
    function _randomMint(address _target) nonReentrant() internal returns (uint256) {
        // Get Random id to mint
        uint256 _index = _getRandom() % lastSupply;
        uint256 _realIndex = getValue(_index) + 1;
        // Reduce supply
        lastSupply--;
        // Replace used id by last
        remainingIds[_index] = getValue(lastSupply);
        // Save dividend
        lastDividendAt[_realIndex] = totalDividend;
        // Mint
        _safeMint(_target, _realIndex);
        // Save Original minters
        minters[_realIndex] = msg.sender;
        return _realIndex;
    }

    function multipleRegularMintsWithRecipients(address[] memory recipients)
        external
        onlyOwner
    {
        for (uint256 i = 0; i < recipients.length; i++) {
            _regularMintWithAddress(recipients[i]);
        }
    }

    function _regularMintWithAddress(address recipient) internal returns (uint256) {
        // Get Actual id to mint
        uint256 _index = totalSupply();
        uint256 _realIndex = getValue(_index) + 1;
        // Reduce supply
        lastSupply--;
        // Replace used id by last
        remainingIds[_index] = getValue(lastSupply);
        // Mint
        _safeMint(recipient, _realIndex);
        // Save Original minters
        minters[_realIndex] = msg.sender;
        // Save dividend
        lastDividendAt[_realIndex] = totalDividend;
        return _realIndex;
    }

    // Mint for reserved spots
    function _regularMint() internal returns (uint256) {
        // Get Actual id to mint
        uint256 _index = totalSupply();
        uint256 _realIndex = getValue(_index) + 1;
        // Reduce supply
        lastSupply--;
        // Replace used id by last
        remainingIds[_index] = getValue(lastSupply);
        // Mint
        _safeMint(msg.sender, _realIndex);
        // Save Original minters
        minters[_realIndex] = msg.sender;
        // Save dividend
        lastDividendAt[_realIndex] = totalDividend;
        return _realIndex;
    }

    // Get Token List
    function getTokenIds(address _owner)
        public
        view
        returns (uint256[] memory)
    {
        // Count owned Token
        uint256 ownerTokenCount = balanceOf(_owner);
        uint256[] memory tokenIds = new uint256[](ownerTokenCount);
        // Get ids of owned Token
        for (uint256 i; i < ownerTokenCount; i++) {
            tokenIds[i] = tokenOfOwnerByIndex(_owner, i);
        }
        return tokenIds;
    }

    // Return compiled Token URI
    function tokenURI(uint256 _id)
        public
        view
        virtual
        override
        returns (string memory)
    {
        require(_exists(_id), "MoonNFTs: URI query for nonexistent token");
        string memory currentBaseURI = _baseURI();
        return
            bytes(currentBaseURI).length > 0
                ? string(
                    abi.encodePacked(
                        currentBaseURI,
                        _id.toString(),
                        baseExtension
                    )
                )
                : "";
    }
    // Reflections
    function handleMintReward(uint256 _amount) private {
        // Split payment
        uint256 mintShare = _amount.mul(mintReward).div(100);
        uint256 devShare = _amount.sub(mintShare);
        // Save dividend for community
        reflectDividend(mintShare);
        // Send owner share
        payable(FeeReceiver).transfer(devShare);
    }

    // Add dividend
    function reflectDividend(uint256 _amount) private {
        reflectionBalance = reflectionBalance.add(_amount);
        totalDividend = totalDividend.add((_amount.div(totalSupply())));
    }

    function reflectToOwners() public payable {
        reflectDividend(msg.value);
    }

    function getAmountOfTokensForEth(address _tokenAddress, uint256 _amount)
        public
        view
        returns (uint256 value)
    {
        address[] memory path = new address[](2);
        path[0] = router.WETH();
        path[1] = _tokenAddress;
        uint256[] memory amounts = router.getAmountsOut(_amount, path);
        value = amounts[1];
    }

    function getReflectionBalancesTokens(address _owner) public view returns (uint256, uint256) {
        uint256 ethTotal = getReflectionBalances(_owner);
        uint256 ethToken1 = ethTotal.mul(RewardToken1Share).div(100);
        uint256 ethToken2 = ethTotal.sub(ethToken1);

        uint256 token1Total = getAmountOfTokensForEth(RewardToken1, ethToken1);
        uint256 token2Total = getAmountOfTokensForEth(RewardToken2, ethToken2);

        return (token1Total, token2Total);
    }

    function getReflectionBalances(address _owner)
        public
        view
        returns (uint256)
    {
        uint256 count = balanceOf(_owner);
        uint256 total = 0;
        for (uint256 i = 0; i < count; i++) {
            uint256 tokenId = tokenOfOwnerByIndex(_owner, i);
            total = total.add(getReflectionBalance(tokenId));
        }
        return total;
    }

    function getReflectionBalance(uint256 tokenId)
        public
        view
        returns (uint256)
    {
        return totalDividend.sub(lastDividendAt[tokenId]);
    }

    function claimRewardsTokens() public nonReentrant() {
        uint256 count = balanceOf(msg.sender);
        uint256 initialBalanceOf1 = token1.balanceOf(address(this));
        uint256 initialBalanceOf2 = token2.balanceOf(address(this));

        uint256 balance = 0;
        for (uint256 i = 0; i < count; i++) {
            uint256 tokenId = tokenOfOwnerByIndex(msg.sender, i);
            balance = balance.add(getReflectionBalance(tokenId));
            lastDividendAt[tokenId] = totalDividend;
        }

        // Split the balance between the tokens
        uint256 balance1 = balance.mul(RewardToken1Share).div(100);
        uint256 balance2 = balance.sub(balance1);

        // Covert Balance to Tokens
        address[] memory path = new address[](2);
        path[0] = router.WETH();
        path[1] = RewardToken1;

        router.swapExactETHForTokensSupportingFeeOnTransferTokens{value : balance1}(
            0,
            path,
            address(this),
            block.timestamp
        );

        path[1] = RewardToken2;

        router.swapExactETHForTokensSupportingFeeOnTransferTokens{value : balance2}(
            0,
            path,
            address(this),
            block.timestamp
        );

        uint256 finalBalanceOf1 = token1.balanceOf(address(this));
        uint256 finalBalanceOf2 = token2.balanceOf(address(this));

        uint256 tokens1 = finalBalanceOf1.sub(initialBalanceOf1);
        uint256 tokens2 = finalBalanceOf2.sub(initialBalanceOf2);
        
        token1.transfer(msg.sender, tokens1);
        token2.transfer(msg.sender, tokens2);
    }
    // Utils

    // Get value from a remaining id node
    function getValue(uint256 _index) internal view returns (uint256) {
        if (remainingIds[_index] != 0) return remainingIds[_index];
        else return _index;
    }

    // Create a random id for minting
    function _getRandom() internal view returns (uint256) {
        return
            uint256(
                keccak256(
                    abi.encodePacked(
                        block.prevrandao,
                        block.timestamp,
                        lastSupply
                    )
                )
            );
    }
}
