      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract Eggs is ERC1155, ReentrancyGuard, Ownable {
    struct Egg {
        uint256 id;
        string name;
        uint256 rarity;
        uint256 class;
    }
    struct SaleConfig {
        uint256 startTime;
        uint256 endTime;
        uint256 price;
        uint256 totalSell;
        uint256 totalBought;
        IERC20 tokenAddress;
        uint256 maxBuy;
        bool onlyWhiteList;
    }

    
    event BuyEgg(address indexed buyer, uint256 indexed id, uint256 amount);
    event NewEggAdded(uint256 indexed id);
    event AddNewSaleConfig(uint256 indexed saleIndex);
    event ChangeNewReceiveFundAddress(address indexed newAddress);
    event EnableDisableBuy(uint256 indexed eggId, bool status);
    event BindingEggAndSaleConfig(
        uint256 indexed eggId,
        uint256 indexed saleIndex
    );

    Egg[] private _eggs;
    SaleConfig[] private saleConfigs;
    mapping(uint256 => uint256) private _totalSupply;
    address private receiveFundAddress;
    mapping(uint256 => bool) private allowBuy;
    mapping(uint256 => uint256) private eggSaleConfig;
    mapping(uint256 => uint256) public indexOfEgg;
    mapping(uint256 => mapping(uint256 => mapping(address => uint256)))
        public totalBuyOfUser;
    mapping(uint256 => mapping(uint256 => mapping(address => uint256)))
        public maxBuyOfWhiteList;
    mapping(address => uint256) private totalBuyEggs;
    uint256 public maxTotalBuyEggs;
    mapping (address => bool) private isBlackListed;

    constructor() ERC1155("") {
        receiveFundAddress = msg.sender;
    }

    /**
    External function 
    */

    function buyEgg(uint256 eggId, uint256 amount)
        external
        payable
        nonReentrant
    {
        require(allowBuy[eggId], "OT: Egg id not sale");
        require(indexOfEgg[eggId] > 0, "OT: Egg id not exist");
        uint256 saleIndex = eggSaleConfig[eggId];
        SaleConfig storage saleConfig = saleConfigs[saleIndex];
        require(block.timestamp >= saleConfig.startTime, "OT: Not start");
        require(block.timestamp <= saleConfig.endTime, "OT: Sale ended");
        uint256 maxBuyOfUser = getMaxAmountBuy(eggId, msg.sender);
        require(
            totalBuyOfUser[eggId][saleIndex][msg.sender] + amount <=
                maxBuyOfUser &&
                totalBuyEggs[msg.sender] + amount <= maxTotalBuyEggs,
            "OT: buy over quota"
        );
        require(
            saleConfig.totalBought + amount <= saleConfig.totalSell,
            "OT: over total sell"
        );
        saleConfig.totalBought += amount;
        totalBuyOfUser[eggId][saleIndex][msg.sender] += amount;
        totalBuyEggs[msg.sender] += amount;
        if (address(saleConfig.tokenAddress) != address(0)) {
            SafeERC20.safeTransferFrom(
                saleConfig.tokenAddress,
                msg.sender,
                receiveFundAddress,
                amount * saleConfig.price
            );
        } else {
            require(
                msg.value == saleConfig.price * amount,
                "OT: Invalid value"
            );
            (bool isSuccess, ) = receiveFundAddress.call{value: msg.value}("");
            require(isSuccess);
        }

        _mint(msg.sender, eggId, amount, "");
        emit BuyEgg(msg.sender, eggId, amount);
    }

    function setMaxTotalBuy(uint256 newValue) external onlyOwner {
        maxTotalBuyEggs = newValue;
    }

    function enableOrDisableBuyForEgg(uint256 eggId, bool status)
        external
        onlyOwner
    {
        allowBuy[eggId] = status;
        emit EnableDisableBuy(eggId, status);
    }

    function setReceiveAddress(address _receiveFundAddress) external onlyOwner {
        receiveFundAddress = _receiveFundAddress;
        emit ChangeNewReceiveFundAddress(_receiveFundAddress);
    }

    function addWLOfEgg(
        uint256 eggId,
        uint256 saleIndex,
        address user,
        uint256 amount
    ) external onlyOwner {
        maxBuyOfWhiteList[eggId][saleIndex][user] = amount;
    }

    function addSaleConfig(
        uint256 saleIndexPlusOne,
        uint256 startTime,
        uint256 endTime,
        uint256 price,
        uint256 totalSell,
        address tokenAddress,
        uint256 maxBuy,
        bool onlyWhiteList
    ) external onlyOwner {
        if (saleIndexPlusOne == 0) {
            saleConfigs.push(
                SaleConfig(
                    startTime,
                    endTime,
                    price,
                    totalSell,
                    0,
                    IERC20(tokenAddress),
                    maxBuy,
                    onlyWhiteList
                )
            );
        } else {
            SaleConfig storage saleConfig = saleConfigs[saleIndexPlusOne - 1];
            saleConfig.startTime = startTime;
            saleConfig.endTime = endTime;
            saleConfig.price = price;
            saleConfig.totalSell = totalSell;
            saleConfig.tokenAddress = IERC20(tokenAddress);
            saleConfig.maxBuy = maxBuy;
            saleConfig.onlyWhiteList = onlyWhiteList;
            saleConfig.totalBought = 0;
        }

        emit AddNewSaleConfig(saleConfigs.length - 1);
    }

    function bindingEggAndSale(uint256 eggId, uint256 saleIndex)
        external
        onlyOwner
    {
        eggSaleConfig[eggId] = saleIndex;
        emit BindingEggAndSaleConfig(eggId, saleIndex);
    }

    function addNewEgg(
        uint256 eggId,
        string calldata name,
        uint256 rarity,
        uint256 class
    ) external onlyOwner {
        require(indexOfEgg[eggId] == 0, "OT: Egg Id exist");
        _eggs.push(Egg(eggId, name, rarity, class));
        indexOfEgg[eggId] = _eggs.length;

        emit NewEggAdded(eggId);
    }

    function setURI(string memory newuri) external onlyOwner {
        _setURI(newuri);
    }

    function mint(
        address account,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) external onlyOwner {
        _mint(account, id, amount, data);
    }

    function mintBatch(
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) external onlyOwner {
        _mintBatch(to, ids, amounts, data);
    }

    function addBlackList (address _evilUser) external onlyOwner {
        isBlackListed[_evilUser] = true;
       // emit AddedBlackList(_evilUser);
    }

    function removeBlackList (address _clearedUser) external onlyOwner {
        isBlackListed[_clearedUser] = false;
       // emit  RemovedBlackList(_clearedUser);
    }
    /**
     * @dev Total amount of tokens in with a given id.
     */
    function totalSupply(uint256 id) external view virtual returns (uint256) {
        return _totalSupply[id];
    }

    /**
     * @dev Indicates whether any token exist with a given id, or not.
     */

    function getTotalEggs() external view virtual returns (uint256) {
        return _eggs.length;
    }

    function getEggById(uint256 eggId)
        external
        view
        returns (
            string memory name,
            uint256 rarity,
            uint256 class,
            bool allowBuyConfig,
            uint256 saleConfigBind
        )
    {
        require(indexOfEgg[eggId] > 0, "OT: Egg id not exist");
        Egg memory egg = _eggs[indexOfEgg[eggId] - 1];
        name = egg.name;
        rarity = egg.rarity;
        class = egg.class;
        allowBuyConfig = allowBuy[eggId];
        saleConfigBind = eggSaleConfig[eggId];
    }

    function getTotalSale() external view returns (uint256) {
        return saleConfigs.length;
    }

    function getSaleConfigByIndex(uint256 saleIndex)
        external
        view
        returns (
            uint256 startTime,
            uint256 endTime,
            uint256 price,
            uint256 totalSell,
            uint256 totalBought,
            address tokenAddress,
            uint256 maxBuy,
            bool onlyWhiteList
        )
    {
        SaleConfig storage saleConfig = saleConfigs[saleIndex];
        startTime = saleConfig.startTime;
        endTime = saleConfig.endTime;
        price = saleConfig.price;
        totalSell = saleConfig.totalSell;
        tokenAddress = address(saleConfig.tokenAddress);
        maxBuy = saleConfig.maxBuy;
        onlyWhiteList = saleConfig.onlyWhiteList;
        totalBought = saleConfig.totalBought;
    }

    // The following functions are overrides required by Solidity.

    /**
     public function
     */

    function getMaxAmountBuy(uint256 eggId, address user)
        public
        view
        returns (uint256)
    {
        require(indexOfEgg[eggId] > 0, "OT: Egg id not exist");
        uint256 saleIndex = eggSaleConfig[eggId];
        SaleConfig storage saleConfig = saleConfigs[saleIndex];
        if (saleConfig.onlyWhiteList == true) {
            return maxBuyOfWhiteList[eggId][saleIndex][user];
        }
        return saleConfig.maxBuy;
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC1155)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    /**
     internal function
     */

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
        require(!isBlackListed[from],"OT: Transfer from blacklist!");
        if (from == address(0)) {
            //mint
            for (uint256 i = 0; i < ids.length; ++i) {
                _totalSupply[ids[i]] += amounts[i];
            }
        }

        if (to == address(0)) {
            //burn
            for (uint256 i = 0; i < ids.length; ++i) {
                _totalSupply[ids[i]] -= amounts[i];
            }
        }
    }
}
