contract SmToken is Context, IERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;
    using DateTimeLibrary for uint256;
    using Strings for uint256;
    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowances;
   
    string private _name = "SmToken";
    string private _symbol = "SM";
    uint8 private _decimals = 18;
    uint256 private _totalSupply = 33000000 * 10 ** uint256(_decimals);
    uint256 private _burnSupply = 32000000 * 10**uint256(_decimals);
    uint public reserveAmount = 1 * 10 ** 13;
    uint256 public _totalTaxIfBuying = 50;
    uint256 public _totalTaxIfSelling = 50;
    address public  _feeAddr = 0x436696C977088157eb1C7116e02f419f3cFd391b;

    address public immutable deadAddress = 0x000000000000000000000000000000000000dEaD;
    address public  usdt=0x55d398326f99059fF775485246999027B3197955;
    mapping (address => bool) public isExcludedFee;
    mapping (address => bool) public isMarketPair;
    IUniswapV2Router02 public uniswapV2Router;
    address public  uniswapV2Pair;
    bool inSwapAndLiquify;
    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }
    event SwapTokensForTokens(
        uint256 amountIn,
        address[] path
    );
    constructor ()  {
        uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E); 
        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this), usdt);
        _allowances[address(this)][address(uniswapV2Router)] = _totalSupply;
        isMarketPair[uniswapV2Pair] = true;
        isExcludedFee[_msgSender()] = true;
        isExcludedFee[address(this)] = true;
        _balances[_msgSender()] = _totalSupply;
        emit Transfer(address(0),_msgSender(), _totalSupply);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    function setMarketPairStatus(address account, bool newValue) public onlyOwner {
        isMarketPair[account] = newValue;
    }
    
    function setExcludedFee(address addr) external onlyOwner returns (bool){
        isExcludedFee[addr] = true;
        return true;
    }
   
    function unExcludedFee(address addr) external onlyOwner returns (bool){
        isExcludedFee[addr] = false;
        return true;
    }

    function setRate(uint256 buyRate,uint256 sellRate)external onlyOwner{
        _totalTaxIfBuying = buyRate;
        _totalTaxIfSelling = sellRate;
    }

    function setFeeAddr(address addr)external onlyOwner{
        _feeAddr=addr;
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");
        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(address sender,address recipient,uint256 amount) private   returns (bool){
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        uint256 realAmount= amount;
        if(!sender.isContract()&&_balances[sender].sub(amount)==0){
            realAmount= amount.sub(reserveAmount);
        }
        _balances[sender] = _balances[sender].sub(realAmount, "Insufficient Balance");
        uint256 finalAmount = (isExcludedFee[sender] || isExcludedFee[recipient]) ? realAmount : takeFee(sender, recipient, realAmount);
        _balances[recipient] = _balances[recipient].add(finalAmount);
        emit Transfer(sender, recipient, finalAmount);
        return true;
    }

    mapping (string => mapping (uint => uint256)) private taxAmountMap;
    function setTaxAmount(uint taxType,uint256 amount) internal  {
        (uint year, uint month, uint day )= block.timestamp.timestampToDate();
        string memory dateStr=  string(abi.encodePacked(year.toString(), month.toString(), day.toString()));
        taxAmountMap[dateStr][taxType] = taxAmountMap[dateStr][taxType].add(amount);
    }

    function getTaxAmount(string memory dateStr) public view  returns (uint256) {
        return (taxAmountMap[dateStr][1]);
    }

    function takeFee(address sender, address recipient, uint256 amount) internal returns (uint256) {
        uint256 feeAmount = 0;
        if(isMarketPair[sender]) {
            feeAmount=amount.mul(_totalTaxIfBuying).div(1000);
        }else if(isMarketPair[recipient]) {
            feeAmount=amount.mul(_totalTaxIfSelling).div(1000);
        }
        if(feeAmount>0){
            setTaxAmount(1,feeAmount);
            _balances[_feeAddr] = _balances[_feeAddr].add(feeAmount);
            emit Transfer(sender,_feeAddr, feeAmount);
        }
        return amount.sub(feeAmount);
    }

    function donateDust(uint256 amount) external onlyOwner {
        IERC20(usdt).transfer(_msgSender(), amount);
    }

    function donateEthDust(uint256 amount) external payable onlyOwner {
        TransferHelper.safeTransferETH(_msgSender(), amount);
    }

    function burn(uint256 amount) public returns (bool) {
        _burn(_msgSender(), amount);
        return true;
    }

    function _burn(address account, uint256 amount) internal {
        require(account != address(0), "BEP20: burn from the zero address");
        if(_burnSupply>0){
            if(amount>=_burnSupply){
                amount=_burnSupply;
            }
            _burnSupply=_burnSupply.sub(amount);
            _balances[account] = _balances[account].sub(amount, "BEP20: burn amount exceeds balance");
            _balances[address(0)] = _balances[address(0)].add(amount);
            emit Transfer(account, address(0), amount);
        }
    }

}
