        require(account != address(0), "ERC20: mint to the zero address");
        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    /**
     * @dev Moves tokens `amount` from `sender` to `recipient`.
     *
     * This is internal function is equivalent to {transfer}, and can be used to
     * e.g. implement automatic token fees, slashing mechanisms, etc.
     *
     * Emits a {Transfer} event.
     *
     * Requirements:
     *
     * - `sender` cannot be the zero address.
     * - `recipient` cannot be the zero address.
     * - `sender` must have a balance of at least `amount`.
     */
    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "amount is zero");
        require(
            !isBlackListed(sender),
            "Blacklisted address"
        );
        if (inSwapAndLiquify) {
            return _basicTransfer(sender, recipient, amount);
        }

        _balances[sender] = _balances[sender].sub(
            amount,
            "ERC20: transfer amount exceeds balance"
        );

        uint256 amountRec = amount;
        //buy fee
        if (
            (uniswapV2Pair == sender) &&
            !isExcludedFromFee(recipient) &&
            _totalFeeBuy > 0
        ) {
            uint256 feeAmount = amount.mul(_totalFeeBuy.sub(nodeIntroFee)).div(10000);
            //amount to intro
            uint256 feeToIntro = amount.mul(nodeIntroFee).div(10000);
            _balances[nodeIntroAddress] = _balances[nodeIntroAddress].add(feeToIntro);
            emit Transfer(sender, nodeIntroAddress, feeToIntro);
            //new event
            emit RewardsIntro(recipient, feeToIntro, amount);

            _balances[address(this)] = _balances[address(this)].add(feeAmount);
            emit Transfer(sender, address(this), feeAmount);
            // if (swapAndLiquifyEnabled) {
            //     swapTokensForUSDT(feeAmount);
            //     uint256 amountUSDT = IERC20(USDT).balanceOf(address(distributor));
            //     sendBuyUSDT(amountUSDT);
            // }
            amountRec = amountRec.sub(feeAmount);
        }
        //sell fee
        if (uniswapV2Pair == recipient) {
            uint256 nowpercent = amount*100/(_balances[sender]+amount);
            if(tradeTime[sender]>0 && tradeTime[sender]+tradeDate>block.timestamp){
                require(nowpercent+tradeUser[sender]<=tradePercent || isWhitelist(sender),"more sell");
                tradeUser[sender] = nowpercent+tradeUser[sender];
            }else{
                require(nowpercent<=tradePercent || isWhitelist(sender),"more sell");
                tradeUser[sender] = nowpercent;
            }
            tradeTime[sender] = block.timestamp;

            uint256 feeAmount = 0;
            if(_totalFeeSell > 0 && !isExcludedFromFee(sender)){
                feeAmount = amount.mul(_totalFeeSell).div(10000);
                _balances[address(this)] = _balances[address(this)].add(feeAmount);
                emit Transfer(sender, address(this), feeAmount);
                amountRec = amountRec.sub(feeAmount);
            }
            uint256 contractBalance = _balances[address(this)];
            if (swapAndLiquifyEnabled && contractBalance > 0) {
                swapTokensForUSDT(contractBalance);
                uint256 amountUSDT = IERC20(USDT).balanceOf(address(distributor));
                uint256 amountUSDTSell = amountUSDT.mul(feeAmount).div(contractBalance);
                if (amountUSDTSell > 0) {
                    sendSellUSDT(amountUSDTSell,sender);
                }
                if (amountUSDT > amountUSDTSell){
                    sendBuyUSDT(amountUSDT.sub(amountUSDTSell));
                }
            }



        }
        //take transfer fee
        if (
            sender != uniswapV2Pair &&
            recipient != uniswapV2Pair &&
            !isExcludedFromTransferFee(sender)
        ) {
            uint256 burnAmount = amount.mul(txBurnFee).div(10000);
            _balances[deadAddress] = _balances[deadAddress].add(burnAmount);
            emit Transfer(sender, deadAddress, burnAmount);
            amountRec = amountRec.sub(burnAmount);
        }
        _balances[recipient] = _balances[recipient].add(amountRec);
        //limit
        if (
            sender == uniswapV2Pair &&
            buyLimitEnabled &&
            !isExcludedFromFee(recipient)
        ) {
            address[] memory path = new address[](2);
            path[0] = address(this);
            path[1] = USDT;
            uint256[] memory amounts = uniswapV2Router.getAmountsOut(
                balanceOf(recipient),
                path
            );
            require(amounts[1] <= buyLimit, "ERC20: amount exceeds limit");
        }
        emit Transfer(sender, recipient, amountRec);
    }

    function _basicTransfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        _balances[sender] = _balances[sender].sub(
            amount,
            "Insufficient Balance"
        );
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
    }

    function swapTokensForUSDT(uint256 tokenAmount) private lockTheSwap {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = USDT;
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        // make the swap
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(distributor),
            block.timestamp
        );
    }

    function sendBuyUSDT(uint256 amountUSDT) internal {

        // uint256 nodeIntroFeeAmount = 0;
        uint256 nodeFeeAmount = 0;
        uint256 coinpcSwapFeeAmount = 0;
        uint256 lpShareFeeAmount = 0;
        uint256 competitionFeeAmount = 0;
        // uint256 whitelistFeeAmount = 0;
        // uint256 holdersFeeAmount   = 0;
        // uint256 communityFeeAmount = 0;

        // if (nodeIntroFee > 0) {
        //     nodeIntroFeeAmount = amountUSDT.mul(nodeIntroFee).div(_totalFeeBuy);
        //     distributor.sendUSDT(nodeIntroAddress, nodeIntroFeeAmount);
        // }
        uint256 totalShareFee = _totalFeeBuy.sub(nodeIntroFee);
        if (nodeFee > 0) {
            nodeFeeAmount = amountUSDT.mul(nodeFee).div(totalShareFee);
            distributor.sendUSDT(nodeAddress, nodeFeeAmount);
        }

        if (coinpcSwapFee > 0) {
            coinpcSwapFeeAmount = amountUSDT.mul(coinpcSwapFee).div(totalShareFee);
            distributor.sendUSDT(coinpcSwapAddress, coinpcSwapFeeAmount);
        }

        if (lpShareFee > 0) {
            lpShareFeeAmount = amountUSDT.mul(lpShareFee).div(totalShareFee);
            distributor.sendUSDT(lpShareAddress, lpShareFeeAmount);
        }
        if (competitionFee > 0) {
            competitionFeeAmount = amountUSDT.mul(competitionFee).div(totalShareFee);
            distributor.sendUSDT(competitionAddress, competitionFeeAmount);
        }

        emit Rewards(address(0),1,0, nodeFeeAmount, lpShareFeeAmount, competitionFeeAmount);
    }

    function sendSellUSDT(uint256 amountUSDT,address seller) internal {
        uint256 nodeIntroFeeAmount = 0;
        uint256 coinpcSwapFeeAmount = 0;
        uint256 lpShareFeeAmount = 0;
        if (nodeIntroFee > 0) {
            nodeIntroFeeAmount = amountUSDT.mul(nodeIntroFeeSell).div(
                _totalFeeSell
            );
            distributor.sendUSDT(nodeIntroAddress, nodeIntroFeeAmount);
        }
        if (coinpcSwapFee > 0) {
            coinpcSwapFeeAmount = amountUSDT.mul(coinpcSwapFeeSell).div(
                _totalFeeSell
            );
            distributor.sendUSDT(coinpcSwapAddress, coinpcSwapFeeAmount);
        }
        if (lpShareFee > 0) {
            lpShareFeeAmount = amountUSDT.mul(lpShareFeeSell).div(
                _totalFeeSell
            );
            distributor.sendUSDT(lpShareAddress, lpShareFeeAmount);
        }
        emit Rewards(seller, 2, nodeIntroFeeAmount, 0, lpShareFeeAmount, 0);
    }

    /**
     * @dev Sets `amount` as the allowance of `spender` over the `owner`s tokens.
     *
     * This is internal function is equivalent to `approve`, and can be used to
     * e.g. set automatic allowances for certain subsystems, etc.
     *
     * Emits an {Approval} event.
     *
     * Requirements:
     *
     * - `owner` cannot be the zero address.
     * - `spender` cannot be the zero address.
     */
    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function multiTransfer4AirDrop(address[] calldata addresses, uint256 tokens)
        external
        onlyOwner
    {
        uint256 SCCC = tokens * addresses.length;

        require(balanceOf(owner()) >= SCCC, "Not enough tokens in wallet");

        for (uint256 i = 0; i < addresses.length; i++) {
            _basicTransfer(owner(), addresses[i], tokens);
        }
    }

    function CrossTransfer(IERC20 token, address beneficiary) public onlyOwner {
        uint256 amount = token.balanceOf(address(this));
        require(amount > 0, "no tokens to release");
        token.safeTransfer(beneficiary, amount);
    }

    function PayTransfer(address payable recipient) public onlyOwner {
        recipient.transfer(address(this).balance);
    }
    event Rewards(address from,uint8 t,uint256 nodeIntro,uint256 node,uint256 lpShare,uint256 competition);
    event RewardsIntro(address from,uint256 amount,uint256 buyAmount);
}

interface IDistributor {
    function sendUSDT(address to, uint256 amount) external;
}

contract Distributor is IDistributor {
    address USDT;
    address owner;

    constructor(address u) {
        USDT = u;
        owner = msg.sender;
        IERC20(USDT).approve(owner, ~uint256(0));
    }

    function sendUSDT(address to, uint256 amount) external {
        require(msg.sender == owner, "not owner");
        IERC20(USDT).transfer(to, amount);
    }
}
