contract TXGLOCKING is Ownable  {

    using SafeMath for uint256;

    uint256 slab_amount = 12000000000000000000000;
    address token;
    uint256 public depositId;
    uint256 lastWithdrwaltimestamp;


    struct Items {
        uint256 tokenAmount;
        uint256 unlockTime;
    }

    mapping(uint256 => Items) public lockedToken;

    event TokensLocked(address indexed sender, uint256 amount, uint256 unlockTime, uint256 depositId);

    constructor(address token_address) {
       token = token_address;
    }

    function lockTokens(uint256 _amount, uint256 _unlockTime) external returns (uint256 _id) {
        require(_amount > 0, "Tokens amount must be greater than 0");

        require(IERC20(token).transferFrom(msg.sender, address(this), _amount), "Failed to transfer tokens to locker");

        _id = ++depositId;
        lockedToken[_id].tokenAmount =  _amount;
        lockedToken[_id].unlockTime = _unlockTime;
        emit TokensLocked(msg.sender, _amount, _unlockTime, depositId);
    }

    function withdrawTokens(address recevier, uint256 amount, uint256 _id) public onlyOwner  {
        require(block.timestamp <= lockedToken[_id].unlockTime, "Tokens are locked");
        require(lastWithdrwaltimestamp + 7 days < block.timestamp, "Week is over");
        require(slab_amount >= amount, "Amount exceeds the weekly limit.");
            lastWithdrwaltimestamp = block.timestamp;
            IERC20(token).transfer(recevier, amount);
    }


    function getLastWithdrawTimestamp() public view returns(uint256)
    {
        return lastWithdrwaltimestamp;
    }

    function release(address recevier, uint256 amount, uint256 _id) public onlyOwner  {
        require(block.timestamp > lockedToken[_id].unlockTime, "Tokens are locked");
        IERC20(token).transfer(recevier, amount);
    }


}
