    "DonationChain.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract Donations is ReentrancyGuard {
    using SafeMath for uint256;

    struct Donor {
        uint256 donationAmount;
        uint256 rewardClaimed;
        uint256 lastRewardClaim;
        address referrer;
    }

    address payable private owner;
    mapping(address => Donor) public donors;
    address[] private donorAddresses;
    uint8[5] private rewardsPercentages = [10, 5, 3, 2, 1];

    event Donation(address indexed donor, address indexed referrer, uint256 amount);
    event RewardClaimed(address indexed donor, uint256 amount);

    constructor() {
        owner = payable(msg.sender);
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only the owner can call this function");
        _;
    }

    function donate(address referrer) public payable nonReentrant {
        require(msg.value > 0, "Donation amount should be greater than 0");
        require(donors[msg.sender].donationAmount == 0, "Donor has already donated");

        if (referrer == address(0) || 
            (referrer != owner && donors[referrer].donationAmount < 1 ether)) {
            referrer = owner;
        }

        uint256 commission = (msg.value * 10) / 100;
        owner.transfer(commission);

        address currentReferrer = referrer;
        for (uint8 i = 0; i < 5; i++) {
            if (currentReferrer == address(0)) {
                break;
            }

            if (currentReferrer != owner && donors[currentReferrer].donationAmount < 1 ether) {
                currentReferrer = donors[currentReferrer].referrer;
                i--;  // Decrease counter to ensure that 5 eligible referrers receive rewards.
                continue;
            }

            uint256 reward = (msg.value * rewardsPercentages[i]) / 100;
            payable(currentReferrer).transfer(reward);
            currentReferrer = donors[currentReferrer].referrer;
        }

        donors[msg.sender] = Donor({
            donationAmount: msg.value,
            rewardClaimed: 0,
            lastRewardClaim: block.timestamp,
            referrer: referrer
        });

        donorAddresses.push(msg.sender);
        emit Donation(msg.sender, referrer, msg.value);
    }

    function claimReward() public nonReentrant {
        Donor storage donor = donors[msg.sender];
        require(donor.donationAmount > 0, "Donor has not donated yet");

        uint256 timeSinceLastClaim = (block.timestamp - donor.lastRewardClaim) / 1 days; // Convert to days
        uint256 rewardPerDay = donor.donationAmount.mul(2).div(100); // 2% of donation per day
        uint256 totalReward = rewardPerDay.mul(timeSinceLastClaim);
        uint256 maxReward = donor.donationAmount.mul(200).div(100); // max reward is 200% of donation
        uint256 remainingReward = maxReward.sub(donor.rewardClaimed);
        uint256 rewardToSend = totalReward < remainingReward ? totalReward : remainingReward;

        require(rewardToSend > 0, "No more rewards available");

        donor.rewardClaimed = donor.rewardClaimed.add(rewardToSend);
        donor.lastRewardClaim = block.timestamp;

        (bool success, ) = payable(msg.sender).call{value: rewardToSend}("");
        require(success, "Failed to transfer the reward");

        emit RewardClaimed(msg.sender, rewardToSend);
    }

    function getTotalBalance() public view returns (uint256) {
        return address(this).balance;
    }

    function getNumberOfDonors() public view returns (uint256) {
        return donorAddresses.length;
    }
}"
