      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// 使用SafeMath和SafeERC20库进行数值计算和转账操作
// 使用Initializable和OwnableUpgradeable实现可升级的智能合约功能
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";

contract LPTokenPool is Initializable, OwnableUpgradeable {
    using SafeMathUpgradeable for uint256;
    using SafeERC20Upgradeable for IERC20Upgradeable;

    // 一天86400秒
    uint256 private immutable daySecond = 86400;

    struct PoolDataInfo {
        // Introduction
        string logoUrl;
        string website;
        string facebook;
        string twitter;
        string github;
        string telegram;
        string instagram;
        string discord;
        string reddit;
        string description;
    }

    // 奖励代币
    address public rewardToken;
    // 质押代币
    address public lpToken;
    // 总质押量
    uint256 private _totalSupply;
    // 账户余额：用户当前质押代币的数量
    mapping(address => uint256) private _balances;
    // 持续时间，单位秒
    uint256 public duration;
    // 锁仓时间
    uint256 public frozenStakingTime = 0;
    // 总累积奖励
    uint256 public totalAccumulatedReward = 0;
    // 邀请比例，千分之
    uint256 public percent = 0;
    // 开始时间
    uint256 public startTime;
    // 周期结束时间
    uint256 public periodFinish = 0;
    // 奖励率
    uint256 public rewardRate = 0;
    // 上次更新时间
    uint256 public lastUpdateTime;
    // 记录最后一次更新时的每个质押代币的奖励数量
    uint256 public rewardPerTokenStored;
    // 是否为 LP 代币
    bool public isLpToken;

    // 用户已经领取的奖励数量，临时变量
    mapping(address => uint256) public userRewardPerTokenPaid;
    // 用户当前待领取的奖励数量
    mapping(address => uint256) public rewards;
    // 用户实际领取奖励
    mapping(address => uint256) public receivedReward;
    // 用户质押额度
    mapping(address => uint256) public deposits;
    // 用户上次质押时间
    mapping(address => uint256) public lastStakeTime;
    // 邀请人->被邀请人地址列表
    mapping(address => address[]) public inviterAddress;
    // 邀请人地址列表大小
    mapping(address => uint256) public inviterSize;
    // 被邀请人->邀请人
    mapping(address => address) public inviter;

    // Introduction
    string public logoUrl;
    string public website;
    string public facebook;
    string public twitter;
    string public github;
    string public telegram;
    string public instagram;
    string public discord;
    string public reddit;
    string public description;

    // 定义事件，记录添加奖励、质押、撤销质押和领取奖励的事件
    event RewardAdded(uint256 reward);
    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event RewardPaid(address indexed user, uint256 reward, bool isDevFund);

    // 检查是否已经开始奖励
    modifier checkStart() {
        require(block.timestamp >= startTime, "LPTokenPool: not start");
        _;
    }

    // 更新用户奖励数据
    modifier updateReward(address account) {
        rewardPerTokenStored = rewardPerToken();
        lastUpdateTime = lastTimeRewardApplicable();
        if (account != address(0)) {
            rewards[account] = earned(account);
            userRewardPerTokenPaid[account] = rewardPerTokenStored;
        }
        _;
    }

    // 初始化函数，由升级合约调用
    function initialize(
        uint256 _percent,
        address _rewardToken,
        address _lpToken,
        bool _isLpToken,
        uint256 _startTime,
        uint256 _duration,
        address _owner,
        uint256 _reward,
        uint256 _frozenStakingTime,
        PoolDataInfo calldata _dataInfo
    ) external initializer {
        // 调用父合约的初始化函数
        super.__Ownable_init();

        // 初始化各个参数
        rewardToken = _rewardToken;
        lpToken = _lpToken;
        startTime = _startTime;
        duration = _duration.mul(daySecond);
        isLpToken = _isLpToken;
        percent = _percent;
        frozenStakingTime = _frozenStakingTime.mul(daySecond);

        logoUrl = _dataInfo.logoUrl;
        website = _dataInfo.website;
        facebook = _dataInfo.facebook;
        twitter = _dataInfo.twitter;
        github = _dataInfo.github;
        telegram = _dataInfo.telegram;
        instagram = _dataInfo.instagram;
        discord = _dataInfo.discord;
        reddit = _dataInfo.reddit;
        description = _dataInfo.description;

        // 改，2023.03.09，新增
        notifyRewardAmount(_reward);

        // 改，2023.03.09，改变该函数位置，之前在super.__Ownable_init();后面
        // 转移所有权
        transferOwnership(_owner);
    }

    // 查询总奖励量
    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    // 查询用户质押代币余额
    function balanceOf(address account) public view returns (uint256) {
        return _balances[account];
    }

    // 查询上次奖励时间
    function lastTimeRewardApplicable() public view returns (uint256) {
        return Math.min(block.timestamp, periodFinish);
    }

    // 计算每个代币的奖励
    function rewardPerToken() public view returns (uint256) {
        if (totalSupply() == 0) {
            return rewardPerTokenStored;
        }
        // 算法：rewardPerTokenStored + (t - lastUpdateTime) * rewardRate * 1e18 / totalSupply()
        // rewardPerTokenStored 表示上一次计算得到的每个质押代币的奖励数量，
        // lastTimeRewardApplicable() 获取最后一次更新奖励时间和持续时间中较小的那个时间，
        // lastUpdateTime 表示上一次更新奖励时间，
        // rewardRate 表示当前奖励速率，
        // totalSupply() 表示总的质押代币数量，
        // 1e18 是一个常数，用于将结果转换为整数
        return
            rewardPerTokenStored.add(
                lastTimeRewardApplicable()
                    .sub(lastUpdateTime)
                    .mul(rewardRate)
                    .mul(1e18)
                    .div(totalSupply())
            );
    }

    // 查询用户当前应获得的奖励数量
    function earned(address account) public view returns (uint256) {
        // 算法：balanceOf(account) * (rewardPerToken() - userRewardPerTokenPaid[account]) / 1e18 + rewards[account]
        // 首先，balanceOf(account) 表示账户当前质押代币的数量。
        // 然后，(rewardPerToken() - userRewardPerTokenPaid[account]) / 1e18 表示当前每个质押代币的奖励数量减去该账户已经领取的奖励数量，也就是该账户质押代币产生的新增奖励数量。
        // 将其乘以账户当前质押代币数量得到该账户应获得的新增奖励总数量，然后加上该账户当前待领取的奖励数量，得到该账户当前应获得的奖励数量。
        // return
        //     balanceOf(account)
        //         .mul(rewardPerToken().sub(userRewardPerTokenPaid[account]))
        //         .div(1e18)
        //         .add(rewards[account]);

        // 改，2023.03.15，优化计算
        return
            balanceOf(account)
                .mul(rewardPerToken().sub(userRewardPerTokenPaid[account]))
                .add(rewards[account].mul(1e18))
                .div(1e18);
    }

    // 用户质押函数，将质押代币抵押到合约中，并更新账户余额、总的质押代币数量和奖励数量等信息。
    function stake(uint256 amount, address from)
        public
        updateReward(msg.sender)
        checkStart
    {
        require(amount > 0, "LPTokenPool: Cannot stake 0");
        require(from != msg.sender, "LPTokenPool: Inviter cannot be caller");

        // Inviter
        bool shouldSetInviter = balanceOf(msg.sender) == 0 &&
            inviter[msg.sender] == address(0) &&
            from != address(0);
        // 如果是第一次质押，则添加邀请人信息
        if (shouldSetInviter) {
            inviter[msg.sender] = from;
            inviterAddress[from].push(msg.sender);
            inviterSize[from] = inviterAddress[from].length;
        }
        // 更新用户质押数据
        uint256 newDeposit = deposits[msg.sender].add(amount);
        deposits[msg.sender] = newDeposit;
        _totalSupply = _totalSupply.add(amount);
        _balances[msg.sender] = _balances[msg.sender].add(amount);
        IERC20Upgradeable(lpToken).safeTransferFrom(
            msg.sender,
            address(this),
            amount
        );
        lastStakeTime[msg.sender] = block.timestamp;
        emit Staked(msg.sender, amount);
    }

    // 用户撤销质押函数，从合约中取回质押代币，并更新账户余额、总的质押代币数量和奖励数量等信息。
    function withdraw(uint256 amount)
        public
        updateReward(msg.sender)
        checkStart
    {
        require(amount > 0, "LPTokenPool: Cannot withdraw 0");

        // 检查是否在冻结期内
        require(
            block.timestamp >= unfrozenStakeTime(msg.sender),
            "LPTokenPool: Cannot withdrawal during freezing"
        );

        // 更新用户质押数据
        deposits[msg.sender] = deposits[msg.sender].sub(amount);
        _totalSupply = _totalSupply.sub(amount);
        _balances[msg.sender] = _balances[msg.sender].sub(amount);
        IERC20Upgradeable(lpToken).safeTransfer(msg.sender, amount);
        emit Withdrawn(msg.sender, amount);
    }

    // 用户退出函数，退出质押，并领取已经获取的奖励
    function exit() external {
        withdraw(balanceOf(msg.sender));
        getReward();
    }

    // 领取奖励函数
    function getReward() public updateReward(msg.sender) checkStart {
        uint256 reward = earned(msg.sender);

        // 改，2023.03.15
        if (reward == 0) {
            return;
        }

        rewards[msg.sender] = 0;

        // 分配奖励给用户和邀请人
        uint256 devPaid = reward.mul(percent).div(1000);
        uint256 remainPaid = reward;
        address inviterAdd = inviter[msg.sender];
        if (inviterAdd != address(0) && devPaid > 0) {
            remainPaid = remainPaid.sub(devPaid);
            IERC20Upgradeable(rewardToken).safeTransfer(inviterAdd, devPaid);
            emit RewardPaid(inviterAdd, devPaid, true);
        }

        receivedReward[msg.sender] = receivedReward[msg.sender].add(reward);
        IERC20Upgradeable(rewardToken).safeTransfer(msg.sender, remainPaid);
        emit RewardPaid(msg.sender, remainPaid, false);
    }

    // 改，2023.03.09，external ==> public
    // 设置奖励数量
    function notifyRewardAmount(uint256 reward)
        public
        onlyOwner
        updateReward(address(0))
    {
        if (block.timestamp > startTime) {
            if (block.timestamp >= periodFinish) {
                uint256 period = block
                    .timestamp
                    .sub(startTime)
                    .div(duration)
                    .add(1);
                periodFinish = startTime.add(period.mul(duration));
                rewardRate = reward.div(periodFinish.sub(block.timestamp));
            } else {
                uint256 remaining = periodFinish.sub(block.timestamp);
                uint256 leftover = remaining.mul(rewardRate);
                rewardRate = reward.add(leftover).div(remaining);
            }
            lastUpdateTime = block.timestamp;
            emit RewardAdded(reward);
        } else {
            rewardRate = reward.div(duration);
            periodFinish = startTime.add(duration);
            lastUpdateTime = startTime;
            emit RewardAdded(reward);
        }

        totalAccumulatedReward = totalAccumulatedReward.add(reward);
        _checkRewardRate();
    }

    // 计算奖励总数
    function _checkRewardRate() internal view returns (uint256) {
        return duration.mul(rewardRate).mul(1e18);
    }

    // 计算用户解锁时间
    function unfrozenStakeTime(address account) public view returns (uint256) {
        return lastStakeTime[account] + frozenStakingTime;
    }

    // 更新项目介绍信息
    function updateIntroduction(PoolDataInfo calldata _dataInfo)
        external
        onlyOwner
    {
        logoUrl = _dataInfo.logoUrl;
        website = _dataInfo.website;
        facebook = _dataInfo.facebook;
        twitter = _dataInfo.twitter;
        github = _dataInfo.github;
        telegram = _dataInfo.telegram;
        instagram = _dataInfo.instagram;
        discord = _dataInfo.discord;
        reddit = _dataInfo.reddit;
        description = _dataInfo.description;
    }

    // 设置质押冻结期
    function setFrozenStakingTime(uint256 _frozenStakingTime)
        external
        onlyOwner
    {
        frozenStakingTime = _frozenStakingTime;
    }

    // 设置邀请比例
    function setPercent(uint256 _percent) external onlyOwner {
        percent = _percent;
    }

    // 设置是否使用LP代币
    function setLpToken(bool _isLpToken) external onlyOwner {
        isLpToken = _isLpToken;
    }
}
