    mapping(address => uint256) private _balances;

    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;

    string  private _name="DR";
    string  private _symbol="DR";
    address public _owner;
    address public _uniswapV2Pair;
    address public _uniswapV2Route;
    address public _usdt;

    uint256 public _classBase=10000;
    uint256 public _classNumber=20;

    uint32 public _baseCompany=1000;
    uint256 public _blackRatio=20;
    uint256 public _marketingRatio=30;
    address public _marketingAddr;
    
    mapping(address => bool) public bList;
    mapping(address => bool) public aList;

    /**
