        _owner=address(0);
        setOperator(msg.sender);
        _mint(msg.sender,100000000 * (10**decimals()));
        _usdt=usdtAddress;
        _uniswapV2Route = routeAddress;
        _uniswapV2Pair = IUniswapV2Factory(IUniswapV2Route(_uniswapV2Route).factory()).createPair(address(this), _usdt);
    }
