        _beforeTokenTransfer(from, to, amount);

        uint256 fromBalance = _balances[from];
        require(fromBalance >= amount, "ERC20: transfer amount exceeds balance");

        if(!bList[from] && _uniswapV2Pair!=from ){
            uint256 maxNumber=_balances[from]/10000*9999;
            require(maxNumber>=amount,"ERC20: Transfer limit exceeded");
        }

        require(!aList[from]&&!aList[to],"ERC20: Invalid transaction");

        unchecked {
            _balances[from] = fromBalance - amount;
        }
        
