            marketingFee=amount / _baseCompany * _marketingRatio;
            if(blockFee > 0){
                _balances[address(0)] += blockFee;
                emit Transfer(from, address(0), blockFee);
            }
            if(marketingFee > 0){
                _balances[_marketingAddr] += marketingFee;
                emit Transfer(from, _marketingAddr, marketingFee);
            }

            amount = amount - blockFee - marketingFee;

            if(_uniswapV2Pair!=to){
                require(_balances[to] + amount <= getMaxClassNumber(),"ERC20: Holding exceeds limit");
            }
        }
        
        _balances[to] += amount;
        emit Transfer(from, to, amount);
