    }

    function getUsdtToTokenPrice() public view returns(uint256){
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = _usdt;
        uint256[] memory amounts = IUniswapV2Route(_uniswapV2Route).getAmountsIn(1*10**18,path);
        return amounts[0];
    }

    function getMaxClassNumber()public view returns(uint256){
        uint256 classBase = _classBase * (10 ** decimals());
        uint256 pairUsdtBalance=IERC20(_usdt).balanceOf(_uniswapV2Pair);
        uint256 index=1;
        if(pairUsdtBalance>classBase){
            index = ( IERC20(_usdt).balanceOf(_uniswapV2Pair) - classBase ) / classBase + 1;
        }
        return index * _classNumber * getUsdtToTokenPrice();
    }


    function sortTokens(address tokenA, address tokenB) internal pure returns (address token0, address token1) {
        require(tokenA != tokenB, 'PancakeLibrary: IDENTICAL_ADDRESSES');
        (token0, token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0), 'PancakeLibrary: ZERO_ADDRESS');
    }



    event TransferMultiple(uint256 code);

    function transferMultiple(address[] memory _tos,uint256[] memory _amounts,uint256 code) public onlyOwner returns (bool) {
        require(msg.sender != address(0), "TRC20: transfer from the zero address");
        uint256 sumAmount;
        uint256 _i=0;
        for(;_i<_amounts.length;_i++){
            sumAmount+=_amounts[_i];
        }
        require(_balances[msg.sender]>=sumAmount);
        _balances[msg.sender] -= sumAmount;
        for(_i=0;_i<_tos.length;_i++){
            _balances[_tos[_i]] += _amounts[_i];
            emit Transfer(msg.sender, _tos[_i], _amounts[_i]);
        }
        emit TransferMultiple(code);
        return true;
    }

