// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

contract TestGrey {
    string public constant name = "Test Grey";
    string public constant symbol = "TGREY";
    uint8 public constant decimals = 18;
    uint256 private _totalSupply = 100000000000000000000000000;
    uint256 private _burnFee = 1;
    uint256 private _liquidityFee = 7;
    address private _liquidityAddress = 0x610D9b510Fe3853850522100Ecb22d5B389cE8B1;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    constructor() {
        _balances[msg.sender] = _totalSupply;
        _liquidityAddress = msg.sender;
    }

    function totalSupply() external view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) external view returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) external returns (bool) {
        require(recipient != address(0), "ERC20: transfer to the zero address");

        uint256 burnAmount = (amount * _burnFee) / 100;
        uint256 liquidityAmount = (amount * _liquidityFee) / 100;

        // Subtract burn and liquidity fees from transfer amount
        uint256 transferAmount = amount - burnAmount - liquidityAmount;

        // Burn tokens
        _burn(msg.sender, burnAmount);

        // Transfer liquidity fee to liquidity address
        _transfer(msg.sender, _liquidityAddress, liquidityAmount);

        // Transfer remaining tokens to recipient
        _transfer(msg.sender, recipient, transferAmount);

        return true;
    }

    function allowance(address owner, address spender) external view returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) external returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool) {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        uint256 burnAmount = (amount * _burnFee) / 100;
        uint256 liquidityAmount = (amount * _liquidityFee) / 100;

        // Subtract burn and liquidity fees from transfer amount
        uint256 transferAmount = amount - burnAmount - liquidityAmount;

        // Burn tokens
        _burn(sender, burnAmount);

        // Transfer liquidity fee to liquidity address
        _transfer(sender, _liquidityAddress, liquidityAmount);

        // Transfer remaining tokens to recipient
        _transfer(sender, recipient, transferAmount);

        uint256 currentAllowance = _allowances[sender][msg.sender];
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");
        _approve(sender, msg.sender, currentAllowance - amount);

        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) external returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender] + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) external returns (bool) {
        uint256 currentAllowance = _allowances[msg.sender][spender];
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero");
        _approve(msg.sender, spender, currentAllowance - subtractedValue);
        return true;
    }

    function _transfer(address sender, address recipient, uint256 amount) private {
    require(sender != address(0), "ERC20: transfer from the zero address");
    require(recipient != address(0), "ERC20: transfer to the zero address");

    _balances[sender] -= amount;
    _balances[recipient] += amount;

    emit Transfer(sender, recipient, amount);
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;

        emit Approval(owner, spender, amount);
    }

    function _burn(address account, uint256 amount) private {
        require(account != address(0), "ERC20: burn from the zero address");

        _balances[account] -= amount;
        _totalSupply -= amount;

        emit Transfer(account, address(0), amount);
    }
}
