// File contracts/bonusLock.sol



pragma solidity 0.8.10;




contract BonusLock is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    IERC20 public token;

    uint32 private MINUTES_IN_DAY;

    struct StakeConfig {
        uint32 periodStaking;
        uint256 rewardPercent;
    }

    uint256 percentDenominator = uint256(10000);

    StakeConfig[] public stakeTypes;

    struct StakeInfo {
        bool settled;
        uint8 stakeType;
        uint32 depositTime;
        uint32 endOfStaking;
        uint256 stakeAmount;
        address staker;
    }
    uint8 public stakeTypeCounter;

    uint256 public totalStakedAmount;

    uint16 public stakeIdCounter;

    mapping(uint16 => StakeInfo) stakeById;
    mapping(address => uint16[]) stakesIdsByOwnerAddress;

    constructor(IERC20 _token) {
        token = _token;

        MINUTES_IN_DAY = 1440; // 24 * 60 for mainnet, 1 for testnet

        addStakeConfig(180, 10400);
        addStakeConfig(360, 11000);

        totalStakedAmount = uint256(0);
    }

    event StakeConfigAdded(uint32 periodStaking, uint256 rewardPercent);

    event Stake(address indexed user, uint16 indexed stakeId, uint256 amount);

    event Withdraw(
        address indexed user,
        uint16 indexed stakeId,
        uint256 amount
    );

    event WithdrawAll(address indexed user, uint256 amount);

    function addStakeConfig(uint32 _periodStaking, uint256 _rewardPercent)
        internal
        onlyOwner
    {
        stakeTypes.push(
            StakeConfig({
                periodStaking: _periodStaking,
                rewardPercent: _rewardPercent
            })
        );
        stakeTypeCounter++;

        emit StakeConfigAdded(_periodStaking, _rewardPercent);
    }

    function _stake(
        address _staker,
        uint8 _stakeType,
        uint256 _amount
    ) internal {
        require(_stakeType <= stakeTypeCounter, "err _stakeType is invalid");
        require(_amount > 0, "err _amount=0");

        token.safeTransferFrom(address(msg.sender), address(this), _amount);
        totalStakedAmount += _amount;

        StakeConfig memory stakeConfig = stakeTypes[_stakeType];

        stakeIdCounter++;
        uint256 _endOfStaking = block.timestamp +
            stakeConfig.periodStaking *
            MINUTES_IN_DAY *
            60;

        StakeInfo memory stakeInfo = StakeInfo({
            settled: false,
            depositTime: uint32(block.timestamp),
            endOfStaking: uint32(_endOfStaking),
            stakeType: _stakeType,
            stakeAmount: _amount,
            staker: _staker
        });

        stakeById[stakeIdCounter] = stakeInfo;
        stakesIdsByOwnerAddress[_staker].push(stakeIdCounter);

        emit Stake(_staker, stakeIdCounter, _amount);
    }

    function stake(uint8 _stakeType, uint256 _amount) external nonReentrant {
        _stake(msg.sender, _stakeType, _amount);
    }

    function addStake(
        address[] memory _listOfStaker,
        uint8[] memory _listOfStakeType,
        uint256[] memory _listOfAmount,
        uint32 retrospectiveMultiplier
    ) external onlyOwner {
        require(_listOfStaker.length == _listOfStakeType.length && _listOfStaker.length == _listOfAmount.length, 'Invalid arguments.');
        for (uint i = 0; i < _listOfStaker.length; i++) {
            _stake(_listOfStaker[i], _listOfStakeType[i], _listOfAmount[i] * retrospectiveMultiplier / 10000);
        }
    }

    function withdraw(uint16 _stakeId) external nonReentrant {
        require(stakeById[_stakeId].staker == msg.sender, "Invalid stake id");

        require(
            stakeById[_stakeId].endOfStaking <= block.timestamp,
            "Staking is not ended"
        );

        require(
            !stakeById[_stakeId].settled,
            "Already withdrawn."
        );

        uint256 withdrawAmount = computeRewards(_stakeId);

        require(
            token.balanceOf(address(this)) >= withdrawAmount,
            "Insufficient token balance of contract."
        );

        token.safeTransfer(msg.sender, withdrawAmount);
        stakeById[_stakeId].settled = true;
        totalStakedAmount -= stakeById[_stakeId].stakeAmount;

        emit Withdraw(msg.sender, _stakeId, withdrawAmount);
    }

    function withdrawAll() external nonReentrant {
        uint16[] memory stakeIdArray = stakesIdsByOwnerAddress[msg.sender];
        require(0 < stakeIdArray.length, "User never staked.");

        uint256 totalToWithdraw = 0;
        for (uint256 i = 0; i < stakeIdArray.length; i++) {
            uint16 stakeId = stakeIdArray[i];

            if (!stakeById[stakeId].settled) {
                totalToWithdraw += computeRewards(stakeId);
            }
        }

        require(
            token.balanceOf(address(this)) >= totalToWithdraw,
            "Insufficient token balance of contract."
        );

        token.safeTransfer(msg.sender, totalToWithdraw);
        for (uint256 i = 0; i < stakeIdArray.length; i++) {
            uint16 stakeId = stakeIdArray[i];

            if (!stakeById[stakeId].settled) {
                stakeById[stakeId].settled = true;
                totalStakedAmount -= stakeById[stakeId].stakeAmount;
            }
        }

        emit WithdrawAll(msg.sender, totalToWithdraw);
    }

    function computeRewards(uint16 _stakeId) internal view returns (uint256) {
        require(_stakeId <= stakeIdCounter, "Invalid stakeID");

        StakeInfo memory stakeInfo = stakeById[_stakeId];
        StakeConfig memory stakeConfig = stakeTypes[stakeInfo.stakeType];
        uint256 rewards;

        if (stakeInfo.endOfStaking > block.timestamp) {
            rewards = 0;
        } else {
            rewards =
                (stakeInfo.stakeAmount * stakeConfig.rewardPercent) /
                percentDenominator;
        }

        return rewards;
    }

    // -- Informational Functions --

    function getStakeConfig(uint8 _stakeType)
        external
        view
        returns (StakeConfig memory)
    {
        return stakeTypes[_stakeType];
    }

    function getStake(uint16 _stakeId)
        external
        view
        returns (
            bool settled,
            uint8 stakeType,
            uint32 depositTime,
            uint32 endOfStaking,
            uint256 stakeAmount,
            address staker
        )
    {
        StakeInfo memory stakeInfo = stakeById[_stakeId];

        require(stakeInfo.staker == msg.sender || msg.sender == owner());

        settled = stakeInfo.settled;
        stakeType = stakeInfo.stakeType;
        depositTime = stakeInfo.depositTime;
        endOfStaking = stakeInfo.endOfStaking;
        stakeAmount = stakeInfo.stakeAmount;
        staker = stakeInfo.staker;
    }

    function getMyStakes(address _staker)
        external
        view
        returns (uint16[] memory)
    {
        require(_staker == msg.sender, 'Staker is invalid.');

        return stakesIdsByOwnerAddress[_staker];
    }
}
