      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.6;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract Elemon is Ownable {
    address public feeCollector;

    constructor() {
        transferOwnership(msg.sender);
        feeCollector = msg.sender;
    }

    /// @dev Variables
    uint public count = 0;
    uint public nextPlanId;
    struct Plan {
        address token;
        uint amount;
        uint period;
    }
    mapping(uint => Plan) public plans;
    mapping(address => uint) public subscriptions;
    mapping(address => bool) public isWhitelisted;
    address[] public whitelistedAddresses;
    /// @dev Events
    event PlanCreated(address tokenAdress, uint planId, uint date);
    event PlanUpdated(address tokenAdress, uint planId, uint amount, uint date);
    event Subscribed(address subscriber, uint planId, uint date);

    event PaymentSent(
        address from,
        address to,
        uint amount,
        address tokenAdress,
        uint planId,
        uint date
    );
    event Balance(address tokenAdress, uint balance);
    event Whitelisted(address _address);
    /// @dev Errors
    error NotEOA();
    error SubscriptionNotPaid();
    error FailedErc20Transfer();

    function createPlan(address token, uint amount, uint period) external {
        require(token != address(0), "address cannot be null address");
        require(amount > 0, "amount needs to be > 0");
        require(period > 0, "frequency needs to be > 0");
        plans[nextPlanId] = Plan(token, amount, period);
        nextPlanId++;
        emit PlanCreated(token, nextPlanId - 1, block.timestamp);
    }

    // @dev update plan info
    function updatePlan(
        uint planId,
        address token,
        uint amount,
        uint period
    ) external onlyOwner {
        require(planId < nextPlanId, "Plan does not exist");
        require(token != address(0), "address cannot be null address");
        require(amount > 0, "amount needs to be > 0");
        require(period > 0, "frequency needs to be > 0");
        plans[planId] = Plan(token, amount, period);
        emit PlanUpdated(token, planId, amount, block.timestamp);
    }

    function getPlan(
        uint planId
    ) external view returns (address token, uint amount, uint period) {
        Plan storage plan = plans[planId];
        return (plan.token, plan.amount, plan.period);
    }

    function isPaid(address subscriber) public view returns (bool) {
        return
            subscriptions[subscriber] == 0
                ? false
                : block.timestamp < subscriptions[subscriber];
    }

    function pay(
        address subscriber,
        uint planId
    ) external virtual returns (bool) {
        Plan storage plan = plans[planId];
        IERC20 token = IERC20(plan.token);
        uint tokenbalance = token.balanceOf(subscriber);
        uint tokenAllowance = token.allowance(subscriber, address(this));
        require(
            tokenbalance >= plan.amount,
            "not enough balance to pay for subscription"
        );
        require(
            tokenAllowance >= plan.amount,
            "not enough allowance approved to pay for subscription"
        );
        require(!isPaid(subscriber), "Paid, not due yet");
        require(
            token.transferFrom(subscriber, address(this), plan.amount),
            "failed to transfer token"
        );
        emit PaymentSent(
            subscriber,
            address(this),
            plan.amount,
            plan.token,
            planId,
            block.timestamp
        );
        subscriptions[subscriber] = block.timestamp + plan.period;
        emit Subscribed(subscriber, planId, subscriptions[subscriber]);
        return true;
    }

    /// @dev whitelist an address
    function testCount() external virtual onlyOwner returns (uint) {
        count++;
        return count;
    }

    function whitelist(
        address _address,
        uint256 _period
    ) external virtual onlyOwner {
        require(_address != address(0), "Address cannot be null address");
        require(
            _address != address(this),
            "Address cannot be contract address"
        );
        require(_address != owner(), "Address cannot be owner address");
        if (!isAddressWhitelisted(_address)) {
            isWhitelisted[_address] = true;
            whitelistedAddresses.push(_address);
            emit Whitelisted(_address);
        }
        subscriptions[_address] = block.timestamp + _period * 1 days;
    }

    /// @dev check if an address is whitelisted
    function isAddressWhitelisted(
        address _address
    ) internal virtual returns (bool) {
        return isWhitelisted[_address];
    }

    /// @dev Withdraw erc20 tokens
    function withdrawErc20(address tokenAdress) external virtual onlyOwner {
        IERC20 erc20Token = IERC20(tokenAdress);
        uint amount = erc20Token.balanceOf(address(this));
        require(amount > 0, "No tokens to withdraw");
        if (erc20Token.transfer(feeCollector, amount) == false)
            revert FailedErc20Transfer();
    }
}
