      "content": "//SPDX-License-Identifier: MIT

pragma solidity 0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

interface ICornToken {
    function mint(address, uint256) external;
}

contract MasterChef is Ownable {
    using SafeMath for uint256;

    // Info of each user.
    struct UserInfo {
        uint256 amount; // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt.
    }

    // Info of each pool.
    struct PoolInfo {
        IERC20 lpToken; // Address of LP token contract.
        uint256 allocPoint; // How many allocation points assigned to this pool.
        uint256 lastRewardBlock; // Last block number that CORNs distribution occurs.
        uint256 accCornPerShare; // Accumulated CORNs per share, times 1e12. See below.
    }

    ICornToken public corn;
    uint256 public cornPerBlock;
    uint256 public BONUS_MULTIPLIER = 1;

    // Info of each pool.
    PoolInfo[] public poolInfo;
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;

    uint256 public totalAllocPoint = 0;
    uint256 public startBlock;
    bool public initialized = false;
    bool public migrated = false;

    // Fee
    uint256 public fee = 10;
    address public feeRecipient;

    constructor(
        address _corn,
        uint256 _cornPerBlock,
        uint256 _startBlock,
        address _feeRecipient
    ) {
        corn = ICornToken(_corn); 
        cornPerBlock = _cornPerBlock;
        startBlock = _startBlock;
        feeRecipient = _feeRecipient;
    }

    function deposit(uint256 _pid, uint256 _amount) external {
        require(_pid != 0, "deposit DECA by staking");

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);
        if (user.amount > 0) {
            uint256 pending = user.amount.mul(pool.accCornPerShare).div(1e12).sub(user.rewardDebt);
            if (pending > 0) {
                mintCornRewards(msg.sender, pending);
            }
        }
        if (_amount > 0) {
            pool.lpToken.transferFrom(address(msg.sender), address(this), _amount);
            user.amount = user.amount.add(_amount);
        }
        user.rewardDebt = user.amount.mul(pool.accCornPerShare).div(1e12);
        emit Deposit(msg.sender, _pid, _amount);
    }

    function withdraw(uint256 _pid, uint256 _amount) external {
        require(_pid != 0, "withdraw DECA by unstaking");
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");

        updatePool(_pid);
        uint256 pending = user.amount.mul(pool.accCornPerShare).div(1e12).sub(user.rewardDebt);
        if (pending > 0) {
            mintCornRewards(msg.sender, pending);
        }
        if (_amount > 0) {
            user.amount = user.amount.sub(_amount);

            // Transfer fee to feeRecipient
            uint256 _fee = _amount.mul(fee).div(100);
            pool.lpToken.transfer(feeRecipient, _fee);

            // Transfer token to user
            pool.lpToken.transfer(address(msg.sender), _amount.sub(_fee));
        }
        user.rewardDebt = user.amount.mul(pool.accCornPerShare).div(1e12);
        emit Withdraw(msg.sender, _pid, _amount);
    }

    function enterStaking(uint256 _amount) external {
        PoolInfo storage pool = poolInfo[0];
        UserInfo storage user = userInfo[0][msg.sender];
        updatePool(0);
        if (user.amount > 0) {
            uint256 pending = user.amount.mul(pool.accCornPerShare).div(1e12).sub(user.rewardDebt);
            if (pending > 0) {
                mintCornRewards(msg.sender, pending);
            }
        }
        if (_amount > 0) {
            pool.lpToken.transferFrom(address(msg.sender), address(this), _amount);
            user.amount = user.amount.add(_amount);
        }
        user.rewardDebt = user.amount.mul(pool.accCornPerShare).div(1e12);

        emit Deposit(msg.sender, 0, _amount);
    }

    function leaveStaking(uint256 _amount) external {
        PoolInfo storage pool = poolInfo[0];
        UserInfo storage user = userInfo[0][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");
        updatePool(0);
        uint256 pending = user.amount.mul(pool.accCornPerShare).div(1e12).sub(user.rewardDebt);
        if (pending > 0) {
            mintCornRewards(msg.sender, pending);
        }
        if (_amount > 0) {
            user.amount = user.amount.sub(_amount);

            // Transfer fee to feeRecipient
            uint256 _fee = _amount.mul(fee).div(100);
            pool.lpToken.transfer(feeRecipient, _fee);

            // Transfer token to user
            pool.lpToken.transfer(address(msg.sender), _amount.sub(_fee));
        }
        user.rewardDebt = user.amount.mul(pool.accCornPerShare).div(1e12);
        emit Withdraw(msg.sender, 0, _amount);
    }

    function add(uint256 _allocPoint, address _lpToken, bool _withUpdate) external onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 lastRewardBlock = block.number > startBlock ? block.number : startBlock;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfo.push(
            PoolInfo(
                {
                    lpToken: IERC20(_lpToken), 
                    allocPoint: _allocPoint, 
                    lastRewardBlock: lastRewardBlock, 
                    accCornPerShare: 0
                }
            )
        );

        emit Add(_allocPoint, _lpToken, _withUpdate);
    }

    function set(uint256 _pid, uint256 _allocPoint, bool _withUpdate) external onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }

        uint256 prevAllocPoint = poolInfo[_pid].allocPoint;
        poolInfo[_pid].allocPoint = _allocPoint;

        if (prevAllocPoint != _allocPoint) {
            totalAllocPoint = totalAllocPoint.sub(prevAllocPoint).add(_allocPoint);
        }

        emit Set(_pid, _allocPoint, _withUpdate);
    }

    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (lpSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
        uint256 cornReward = multiplier.mul(cornPerBlock).mul(pool.allocPoint).div(totalAllocPoint);

        pool.accCornPerShare = pool.accCornPerShare.add(cornReward.mul(1e12).div(lpSupply));
        pool.lastRewardBlock = block.number;
    }

    function startMasterChef() external onlyOwner {
        require(!initialized, "Only can call once");

        startBlock = block.number;

        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            poolInfo[pid].lastRewardBlock = block.number;
        }

        initialized = true;
        emit StartMasterChef(block.number);
    }

    function migrate(uint256 _pid, address[] memory _userAddressList, uint256[] memory _amountList) external onlyOwner {
        require(!migrated, "Only can call once");
        require(_userAddressList.length == _amountList.length, "Length mismatch");

        for (uint256 i = 0; i < _userAddressList.length; i++) {
            UserInfo storage user = userInfo[_pid][_userAddressList[i]];
            user.amount = user.amount.add(_amountList[i]);
        }

        migrated = true;
        emit Migrate(_pid, _userAddressList, _amountList);
    }

    function updateMultiplier(uint256 multiplierNumber) external onlyOwner {
        BONUS_MULTIPLIER = multiplierNumber;
    }

    function setCornPerBlock(uint256 _cornPerBlock) external onlyOwner {
        cornPerBlock = _cornPerBlock;
        massUpdatePools();
    }

    function pendingCorn(uint256 _pid, address _user) external view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accCornPerShare = pool.accCornPerShare;
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
            uint256 cornReward = multiplier.mul(cornPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
            accCornPerShare = accCornPerShare.add(cornReward.mul(1e12).div(lpSupply));
        }
        return user.amount.mul(accCornPerShare).div(1e12).sub(user.rewardDebt);
    }

    function rescueToken(address _token, address _to, uint256 _amount) external onlyOwner {
        uint256 _contractBalance = IERC20(_token).balanceOf(address(this));
        require(_amount <= _contractBalance, "Insufficient token");

        IERC20(_token).transfer(_to, _amount);

        emit RescueToken(_token, _to, _amount);
    }

    function mintCornRewards(address _to, uint256 _amount) internal {
        corn.mint(_to, _amount);

        emit MintCornRewards(_to, _amount);
    }

    function setFee(uint256 _fee) external onlyOwner {
        fee = _fee;

        emit SetFee(_fee);
    }

    function setFeeRecipient(address _feeRecipient) external onlyOwner {
        feeRecipient = _feeRecipient;

        emit SetFeeRecipient(_feeRecipient);
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    function getMultiplier(uint256 _from, uint256 _to) public view returns (uint256) {
        return _to.sub(_from).mul(BONUS_MULTIPLIER);
    }

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event Add(uint256 allocPoint, address lpToken, bool withUpdate);
    event Set(uint256 pid, uint256 allocPoint, bool withUpdate);
    event StartMasterChef(uint256 startBlock);
    event Migrate(uint256 pid, address[] userAddressList, uint256[] amountList);
    event SetLastRewardBlock(uint256 indexed pid, uint256 lastRewardBlock);
    event RescueToken(address token, address to, uint256 amount);
    event MintCornRewards(address to, uint256 amount);
    event SetFee(uint256 fee);
    event SetFeeRecipient(address feeRecipient);
}
"
