    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal override {
        super._transfer(sender, recipient, amount);
        uint256 newAmount;
        uint256 holdingAmount = tokenHoldings[sender].rewardsHolding;
        bool rewardTriggered = false;

        // Update token holdings for sender
        tokenHoldings[sender].holdings -= amount;

        uint256 senderHolding = tokenHoldings[sender].holdings;

        if (senderHolding < holdingAmount) {
            rewardTriggered = true;
            newAmount = holdingAmount - senderHolding;
        }

        if(rewardTriggered == true){
        if (newAmount >= holdingAmount) {
            tokenHoldings[sender].rewardsHolding = 0;
        } else {
            tokenHoldings[sender].rewardsHolding -= newAmount;
        }
        }

        if (isExcludedFromRewards[sender] == true) {
            excludedPool -= amount;

            if (rewardTriggered == true) {
                if (newAmount >= holdingAmount) {
                    rewardToExcluded -= holdingAmount;
                } else {
                    rewardToExcluded -= newAmount;
                }
            }
        }

        if (tokenHoldings[sender].holdings == 0) {
            removeTokenHolder(sender);
            tokenHoldings[sender].timePeriod = 0;
        }

        // Update token holdings for recipient
        if (tokenHoldings[recipient].holdings == 0) {
            addTokenHolder(recipient);
            tokenHoldings[recipient].timePeriod = block.timestamp;
        }

        if (rewardTriggered == true) {
            if (newAmount >= holdingAmount) {
                tokenHoldings[recipient].rewardsHolding += holdingAmount;
            } else {
                tokenHoldings[recipient].rewardsHolding += newAmount;
            }
        }

        if (isExcludedFromRewards[recipient] == true) {
            excludedPool += amount;
            if (rewardTriggered == true) {
                if (newAmount >= holdingAmount) {
                    rewardToExcluded += holdingAmount;
                } else {
                    rewardToExcluded += newAmount;
                }
            }
        }
        tokenHoldings[recipient].holdings += amount;
    }

