// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Token {
    mapping(address => uint) public balances;
    mapping(address => mapping(address => uint)) public allowance;
    uint public totalSupply = 100000000 * 10 ** 18;
    string public name = "PINKSALE";
    string public symbol = "PINKSALE";
    uint public decimals = 18;
    address public owner;
    bool public withdrawalsEnabled = false; // Ajout de la variable d'état

    event Transfer(address indexed from, address indexed to, uint value);
    event Approval(address indexed owner, address indexed spender, uint value);

    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _;
    }

    modifier onlyIfWithdrawalsEnabled() {
        require(withdrawalsEnabled, "Withdrawals are currently disabled");
        _;
    }

    constructor() {
        owner = msg.sender;
        balances[msg.sender] = totalSupply;
    }

    function balanceOf(address _owner) public view returns(uint) {
        return balances[_owner];
    }

    function transfer(address to, uint value) public onlyIfWithdrawalsEnabled returns(bool) {
        require(balanceOf(msg.sender) >= value, 'Balance too low');
        balances[to] += value;
        balances[msg.sender] -= value;
        emit Transfer(msg.sender, to, value);
        return true;
    }

    function transferFrom(address from, address to, uint value) public onlyIfWithdrawalsEnabled returns(bool) {
        require(balanceOf(from) >= value, 'Balance too low');
        require(allowance[from][msg.sender] >= value, 'Allowance too low');
        balances[to] += value;
        balances[from] -= value;
        emit Transfer(from, to, value);
        return true;
    }

    function approve(address spender, uint value) public returns (bool) {
        allowance[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }

    // Fonction pour modifier l'état des retraits (appelée par le propriétaire)
    function modifyWithdrawalState(bool _enableWithdrawals) public onlyOwner {
        withdrawalsEnabled = _enableWithdrawals;
    }

    // Ajouter une fonction pour changer le nom et le symbole du token
    function changeTokenInfo(string memory _name, string memory _symbol) public onlyOwner {
        name = _name;
        symbol = _symbol;
    }
}
