pragma solidity ^0.8.6;

import "./BaseClaim.sol";

contract RunnerClaim is BaseClaim {
    using SafeMath for uint256;
    using SafeERC20 for ERC20;

    constructor(ERC20 _rewardToken) BaseClaim(_rewardToken) {}

    // For the private round: 10% released on TGE,
    // followed by a 3 months cliff, then 3.913% on a monthly basis (from month 4 onwards) for
    // a total of 23 unlocks on top of TGE release.
    function calculateUnlockedAmount(uint256 _totalAmount, uint256 _timestamp)
        internal
        view
        override
        returns (uint256)
    {
        if (_timestamp < claimTime) {
            return 0;
        }

        uint256 timeSinceClaim = _timestamp.sub(claimTime);
        uint256 unlockedAmount = 0;

        if (timeSinceClaim <= 90 days) {
            unlockedAmount = _totalAmount.mul(10).div(100);
        } else if (timeSinceClaim > 780 days) { // 90 + 23 months
            unlockedAmount = _totalAmount;
        } else {
            uint256 unlockedOnClaim = _totalAmount.mul(10).div(100);
            uint256 vestable = _totalAmount.sub(unlockedOnClaim);
            uint256 monthsSince = timeSinceClaim.sub(90 days) / 30 days;

            unlockedAmount = vestable.mul(monthsSince).div(23).add(
                unlockedOnClaim
            );
        }

        return unlockedAmount;
    }

    function totalAvailableAfter()
        public
        view
        override
        returns (uint256)
    {
        return claimTime + 780 days;
    }
}
