pragma solidity >=0.8.0 <0.9.0;
/// @title A title that should describe the contract/interface
/// @author Tamer Fouad
/// @notice NFT contract ERC721
/// @dev This is V1 logic contract of ExoWorlds NFT.
/// Implemented Upgradeable, Enumerable, URIStorage, Burnable, Pausable, Royalty feature.
contract PlanetNFTV3 is
	Initializable,
	ERC721EnumerableUpgradeable,
	ERC721URIStorageUpgradeable,
	ERC2981Upgradeable,
	PausableUpgradeable,
	OwnableUpgradeable
{
	using CountersUpgradeable for CountersUpgradeable.Counter;

	CountersUpgradeable.Counter private _tokenPendingIdCounter;

	// NFT collection item supply
	uint256 private MAX_PLANET;

	// Base token URI
	string private _baseTokenURI;

	// Available Tokens for minting
	uint256[] private _availableTokens;

	// Counter for initialization
	uint256 private _initCounter;

	// Address that can call with random seed
	address private _oracleRandom;

	// Pending mint counter array
	uint256[] private _indexPendingMints;

	// Max pending mint count
	uint256 private _maxPendingMintsToProcess;

	// Max mint count for give away
	uint256 private _totalMintsForGiveaway;

	// NFT price for each tier
	uint256[] private TIER_PRICE;

	// Array of Block Numbers for each tier range.
	uint256[] private TIER_BLOCK;

	// Limit the number of mints per tier
	uint8[] private TIER_MINT_LIMIT;

	// Giveaway address
	address private _giveawayAddress;

	// Mapping from address to white list flag
	mapping(address => uint8) private _whitelister;

	// Mapping from pending count to mint request address
	mapping(uint256 => address) private _pendingMint;

	// Mapping from tier number to minted amount of address
	mapping(uint8 => mapping(address => uint8)) private _mintCounter;
    mapping(address => uint256) private pendingAmount; // deprectaed but kept to avoid storage layout issues in proxy
	mapping (address => uint256) private lastClaimdate; // deprectaed but kept to avoid storage layout issues in proxy
	mapping (uint256 => uint256) private metadatas; // tokenid => 1,2,3. 1 == whitesale, 2 = public sale, 3, secodary sale
	mapping (uint256 => uint256 ) public rate; //value will be uint256. it means that value's decimals are same to exotoken's decimal.
	address public exoAddress;
	uint256 public startAirdropTime;
   


	/// @dev Modifier used internally to prevent minting until tokens are fully initialized.
	modifier onlyAfterFullInit() {
		require(
			_isFullyInitialized(),
			"Available tokens not fully initialized yet"
		);
		_;
	}

	/// @dev Modifier used internally to set variables before starting mint.
	modifier onlyBeforeMint() {
		require(totalSupply() == 0, "Minting tokens already started");
		_;
	}

	/// @dev Emitted when number of minting requests are added to pending array.
	event AddPendingMint(
		address indexed minter,
		uint8 indexed amount,
		uint256 pendingId
	);

	/// @dev Emitted when token minted with randomSeed
	event MintedWithRandomNumber(address indexed minter, uint256 randomSeed);
	event ClaimAirdrop(address to, uint256 amount);
	/// @custom:oz-upgrades-unsafe-allow constructor
	constructor() initializer {}

	function initialize() public initializer {
		__ERC721_init("PLANET", "PLN");
		__ERC721Enumerable_init();
		__ERC721URIStorage_init();
		__ERC2981_init();
		__Pausable_init();
		__Ownable_init();
		// Initialize variables
		_initCounter = 0;
		_maxPendingMintsToProcess = 100;
		MAX_PLANET = 10000;
		TIER_PRICE = [
			20_0000_0000_0000_0000_0000,
			21_0000_0000_0000_0000_0000,
			22_0000_0000_0000_0000_0000,
			23_0000_0000_0000_0000_0000,
			25_0000_0000_0000_0000_0000
		];
		TIER_BLOCK = [
			11381920,
			11407840,
			11390560,
			11416480,
			11399200,
			11425120,
			11407840,
			11433760,
			11416480
		];
		_oracleRandom = address(0);
	}


	/**
	 * @dev Sets the `_tokenURI` as the tokenURI of `tokenId`
	 *
	 * Requirements:
	 *
	 * - Only owner can call this function.
	 */
	function setTokenURI(uint256 tokenId, string memory _tokenURI)
		external
		onlyOwner
	{
		_setTokenURI(tokenId, _tokenURI);
	}

	/**
	 * @dev Sets the default royalty
	 *
	 * Requirements:
	 *
	 * - Only owner can call this function.
	 */
	function setDefaultRoyalty(address receiver, uint96 feeNumerator)
		external
		onlyOwner
	{
		_setDefaultRoyalty(receiver, feeNumerator);
	}

	/**
	 * @dev Removes default royalty
	 *
	 * Requirements:
	 *
	 * - Only owner can call this function.
	 */
	function deleteDefaultRoyalty() external onlyOwner {
		_deleteDefaultRoyalty();
	}

	/**
	 * @dev Sets the royalty for a specific NFT
	 *
	 * Requirements:
	 *
	 * - Caller must be approved or owner of token.
	 */
	function setTokenRoyalty(
		uint256 tokenId,
		address receiver,
		uint96 feeNumerator
	) external virtual {
		require(
			_isApprovedOrOwner(_msgSender(), tokenId),
			"ERC2981Royalty: caller is not owner nor approved"
		);
		_setTokenRoyalty(tokenId, receiver, feeNumerator);
	}

	/**
	 * @dev Resets royalty for the `tokenId` back to the default.
	 */
	function resetTokenRoyalty(uint256 tokenId) external virtual {
		require(
			_isApprovedOrOwner(_msgSender(), tokenId),
			"ERC2981Royalty: caller is not owner nor approved"
		);
		_resetTokenRoyalty(tokenId);
	}


	/**
	 * @dev Fetch token list of `_owner`
	 * @param _owner address for checking tokens
	 * @return Token list of `_owner` have
	 */
	function tokensOfOwner(address _owner)
		public
		view
		returns (uint256[] memory)
	{
		uint256 tokenCount = balanceOf(_owner);
		if (tokenCount == 0) {
			// Return an empty array
			return new uint256[](0);
		} else {
			uint256[] memory result = new uint256[](tokenCount);
			for (uint256 index = 0; index < tokenCount; index++) {
				result[index] = tokenOfOwnerByIndex(_owner, index);
			}
			return result;
		}
	}

	/// @dev Safely mints `tokenId` and transfers it to `to`
	function safeMint(address to, uint256 tokenId) public onlyOwner {
		_safeMint(to, tokenId);
	}

	/**
	 * @notice Explain to an end user what this does
	 * @dev Burn token and transfer it to null address
	 * Remove tokenURI of `tokenId`
	 *
	 * @param tokenId Token Id to burn
	 *
	 * Requirements:
	 *
	 * - Only owner of contract can call this function, not owner of tokenId
	 */
	function tokenBurn(uint256 tokenId) public virtual onlyOwner {
		_burn(tokenId);
	}

	/// @dev Pause all transfers
	function pause() public onlyOwner {
		_pause();
	}

	/// @dev Returns to normal state
	function unpause() public onlyOwner {
		_unpause();
	}

	// The trick to change the metadata if necessary and have a reveal moment
	function setBaseURI(string memory baseURI_) public onlyOwner {
		_setBaseURI(baseURI_);
	}

	function baseURI() public view returns (string memory) {
		return _baseTokenURI;
	}

	/// @dev Returns the tokenURI of `tokenId`
	function tokenURI(uint256 tokenId)
		public
		view
		override(ERC721Upgradeable, ERC721URIStorageUpgradeable)
		returns (string memory)
	{
		return super.tokenURI(tokenId);
	}

	/// @dev See {IERC165-supportsInterface}.
	function supportsInterface(bytes4 interfaceId)
		public
		view
		override(ERC721Upgradeable, ERC721EnumerableUpgradeable, ERC2981Upgradeable)
		returns (bool)
	{
		return super.supportsInterface(interfaceId);
	}

	/// @dev Sets the token base URI
	function _setBaseURI(string memory baseURI_) internal virtual onlyOwner {
		_baseTokenURI = baseURI_;
	}

	/// @dev Burn `tokenId`
	/// Includes remove token uri, reset royalty of token, transfer token to null address.
	function _burn(uint256 tokenId)
		internal
		override(ERC721Upgradeable, ERC721URIStorageUpgradeable)
	{
		super._burn(tokenId);
		_resetTokenRoyalty(tokenId);
	}

	function setAttrbutes(uint256 tokenId, uint256 attr) external onlyOwner {
		metadatas[tokenId] = attr;
	}
	function getAttribute(uint256 tokenId) public view returns(uint256) {
		return metadatas[tokenId];
	}
    function getAirdropAmount(address user) public view returns(uint256) {
        uint256[] memory userTokens = tokensOfOwner(user);
        uint256 currentAmount = 0;
        for(uint i = 0 ; i < userTokens.length; i ++) {
            uint256 claimedTime = lastClaimedTimestamp[userTokens[i]] == 0 ? startAirdropTime: lastClaimedTimestamp[userTokens[i]];
            if(claimedTime == 0) {
                continue;
            }
            uint256 _duration = block.timestamp - claimedTime;
            currentAmount += _duration * rate[metadatas[userTokens[i]]];
        }
        return currentAmount;
    }

	function claimAirdrop() external {
        uint256 claimAmount = getAirdropAmount(msg.sender);
        require(claimAmount != 0, "zero amount");
        uint256[] memory userTokens = tokensOfOwner(msg.sender);
        for(uint i = 0 ; i < userTokens.length; i ++) {
            lastClaimedTimestamp[userTokens[i]] = block.timestamp;
        }
        IEXOToken(exoAddress).mintForAirdrop(claimAmount, msg.sender);
        emit ClaimAirdrop(msg.sender, claimAmount);
    }

	function setExoAddress(address _exo) external onlyOwner {
		exoAddress = _exo;
	}
	function setRate(uint256[] memory _rates) external onlyOwner {
		rate[1] = _rates[0]; // whitesale
		rate[2] = _rates[1]; // public sale
		rate[3] = _rates[2]; // secondary sale
	}
	/// @dev Hook that is called before any token transfer.
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId,
        uint256 batchSize
    )
        internal
        override(ERC721Upgradeable, ERC721EnumerableUpgradeable)
        whenNotPaused
    {
        
        super._beforeTokenTransfer(from, to, tokenId, batchSize);
    }

	function setStartAirdrop(uint256 _startTime) external onlyOwner {
		startAirdropTime = _startTime;
	}
	/**
	 *
	 */
	function _isFullyInitialized() internal virtual returns (bool) {
		return _initCounter >= MAX_PLANET;
	}

	function batchTransferFrom(uint256[] memory tokenIds, address[] memory to) external {
		uint256 _length = tokenIds.length;
		for(uint i = 0 ; i < _length; i ++) {
			transferFrom(msg.sender, to[i], tokenIds[i]);
		}
	}

	/**
	* @dev Batch sets the `_tokenURI` for each `tokenId` in `tokenIds`
	*
	* Requirements:
	*
	* - Only owner can call this function.
	* - `tokenIds` and `tokenURIs` must have the same length.
	*/
	function batchSetTokenURI(uint256[] memory tokenIds, string[] memory tokenURIs) external onlyOwner {
		require(tokenIds.length == tokenURIs.length, "Input must have the same length");
		for (uint256 i = 0; i < tokenIds.length; i++) {
			_setTokenURI(tokenIds[i], tokenURIs[i]);
		}
	}

    mapping (uint256 => uint256) private lastClaimedTimestamp; //mapping the lastclaimed timestamp of each tokenid

    /**
     * @dev Claim airdrop for a specific token
     * @param tokenId ID of the token to claim airdrop for
     */
    function claimAirdropForToken(uint256 tokenId) external {
        require(ownerOf(tokenId) == msg.sender, "Not token owner");
        uint256 claimAmount = getAirdropAmountForToken(tokenId);
        require(claimAmount != 0, "zero amount");
        lastClaimedTimestamp[tokenId] = block.timestamp;
        IEXOToken(exoAddress).mintForAirdrop(claimAmount, msg.sender);
        emit ClaimAirdrop(msg.sender, claimAmount);
    }
    
    /**
     * @dev Claim airdrop for multiple tokens
     * @param tokenIds Array of token IDs to claim airdrop for
     */
    function claimAirdropForTokens(uint256[] memory tokenIds) external {
        uint256 totalClaimAmount = 0;
        for(uint i = 0 ; i < tokenIds.length; i ++) {
            require(ownerOf(tokenIds[i]) == msg.sender, "Not token owner");
            uint256 claimAmount = getAirdropAmountForToken(tokenIds[i]);
            totalClaimAmount += claimAmount;
            lastClaimedTimestamp[tokenIds[i]] = block.timestamp;
        }
        require(totalClaimAmount != 0, "zero amount");
        IEXOToken(exoAddress).mintForAirdrop(totalClaimAmount, msg.sender);
        emit ClaimAirdrop(msg.sender, totalClaimAmount);
    }
    
    /**
     * @dev Get airdrop amount for a specific token
     * @param tokenId ID of the token to get airdrop amount for
     * @return uint256 Airdrop amount for the token
     */
    function getAirdropAmountForToken(uint256 tokenId) public view returns(uint256) {
        uint256 claimedTime = lastClaimedTimestamp[tokenId] == 0 ? startAirdropTime: lastClaimedTimestamp[tokenId];
        if(claimedTime == 0) {
            return 0;
        }
        uint256 _duration = block.timestamp - claimedTime;
        return _duration * rate[metadatas[tokenId]];
    }

    /**
     * @dev Set last claimed time for a specific token
     * @param tokenId ID of the token to set last claimed time for
     * @param timestamp New last claimed time
     */
    function setLastClaimedTime(uint256 tokenId, uint256 timestamp) external onlyOwner {
        lastClaimedTimestamp[tokenId] = timestamp;
    }

    /**
    * @dev Get the last claim date for a specific token
    * @param tokenId ID of the token to get last claim date for
    * @return uint256 Last claim date for the token
    */
    function getLastClaimDate(uint256 tokenId) public view returns (uint256) {
        uint256 lastClaim = lastClaimedTimestamp[tokenId];
        if(lastClaim == 0) {
            return startAirdropTime;
        } else {
            return lastClaim;
        }
    }

}
