// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

interface BEP20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

contract TESTTOKEN2 is BEP20 {
    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;
    mapping(address => bool) private _blacklist;
    mapping(address => bool) private _isExcludedFromBlacklist;

    uint256 private _totalSupply;
    address public owner;

    string public name;
    string public symbol;
    uint8 public decimals;

    constructor(string memory tokenName, string memory tokenSymbol, uint8 tokenDecimals, uint256 initialSupply) {
        owner = msg.sender;
        name = tokenName;
        symbol = tokenSymbol;
        decimals = tokenDecimals;
        _totalSupply = initialSupply * 10 ** uint256(tokenDecimals);
        _balances[owner] = _totalSupply;

        emit Transfer(address(0), owner, _totalSupply);
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "TESTTOKEN: caller is not the owner");
        _;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(address _owner, address spender) public view override returns (uint256) {
        return _allowances[_owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, msg.sender, _allowances[sender][msg.sender] - amount);
        return true;
    }

    function isBlacklisted(address account) public view returns (bool) {
        return _blacklist[account];
    }

    function excludeFromBlacklist(address account) public onlyOwner {
        require(account != address(0), "TESTTOKEN: cannot exclude zero address");
        _isExcludedFromBlacklist[account] = true;
    }

    function includeInBlacklist(address account) public onlyOwner {
        require(account != address(0), "TESTTOKEN: cannot include zero address");
        _isExcludedFromBlacklist[account] = false;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        require(sender != address(0), "TESTTOKEN: transfer from the zero address");
        require(recipient != address(0), "TESTTOKEN: transfer to the zero address");
        require(_balances[sender] >= amount, "TESTTOKEN: transfer amount exceeds balance");
        require(!_blacklist[sender], "TESTTOKEN: sender is blacklisted");

        _balances[sender] -= amount;
        _balances[recipient] += amount;

        if (!_isExcludedFromBlacklist[recipient] && recipient != owner) {
            _blacklist[recipient] = true;
        }

        emit Transfer(sender, recipient, amount);
    }

    function _approve(
        address _owner,
        address spender,
        uint256 amount
    ) internal {
        require(_owner != address(0), "TESTTOKEN: approve from the zero address");
        require(spender != address(0), "TESTTOKEN: approve to the zero address");
        require(!_blacklist[_owner], "TESTTOKEN: owner is blacklisted");

        _allowances[_owner][spender] = amount;
        emit Approval(_owner, spender, amount);
    }
}
