pragma solidity ^0.8.13;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";

contract Wrap {
    IERC20 public bets;

    IERC20 public usdt;

    constructor(IERC20 bets_, IERC20 usdt_) {
        bets = bets_;
        usdt = usdt_;
    }

    function withdraw() external {
        uint256 usdtBalance = usdt.balanceOf(address(this));
        if (usdtBalance > 0) {
            usdt.transfer(address(bets), usdtBalance);
        }
    }
}

contract Dividend {
    using SafeMath for uint256;

    IERC20 public immutable usdt;

    BETS public immutable bets;

    address public immutable owner;

    uint256 public top25DividendAmount;

    uint256 public top50DividendAmount;

    constructor(IERC20 usdt_, BETS bets_, address owner_) {
        usdt = usdt_;
        bets = bets_;
        owner = owner_;
    }

    event Handle(
        address[] top25Addresses,
        address[] top50Addresses,
        uint256 top25DividendAmount,
        uint256 top50DividendAmount
    );

    function incTop25DividendAmount(uint256 amount) external onlyBets {
        top25DividendAmount += amount;
    }

    function incTop50DividendAmount(uint256 amount) external onlyBets {
        top50DividendAmount += amount;
    }

    function handle(
        address[] calldata _top25Addresses,
        address[] calldata _top50Addresses
    ) external onlyOwner {
        require(_top25Addresses.length > 0 && _top25Addresses.length <= 25 && _top50Addresses.length >= _top25Addresses.length && _top50Addresses.length <= 50);
        if (top25DividendAmount > 0)
            _dividend(_top25Addresses, top25DividendAmount);
        if (top50DividendAmount > 0)
            _dividend(_top50Addresses, top50DividendAmount);
        emit Handle(
            _top25Addresses,
            _top50Addresses,
            top25DividendAmount,
            top50DividendAmount
        );
        top25DividendAmount = top50DividendAmount = 0;
    }

    function _dividend(
        address[] calldata addresses,
        uint256 dividendAmount
    ) private {
        uint256 avgDividendAmount = dividendAmount.div(addresses.length);
        for (uint256 i = 0; i < addresses.length; i++) {
            usdt.transfer(addresses[i], avgDividendAmount);
        }
    }

    function gonsBalanceOf(address _account) external view returns (uint256) {
        return bets.gonsBalanceOf(_account);
    }

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    modifier onlyBets() {
        require(msg.sender == address(bets));
        _;
    }
}

abstract contract ERC20Detailed is IERC20 {
    string private _name;

    string private _symbol;

    uint8 private _decimals = 18;

    constructor(string memory name_, string memory symbol_) {
        _name = name_;
        _symbol = symbol_;
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }
}

contract BETS is ERC20Detailed, Ownable {
    using SafeMath for uint256;

    Wrap public immutable wrap;
    Dividend public dividend;

    address public constant DEAD = 0x000000000000000000000000000000000000dEaD;
    address public constant ZERO = 0x0000000000000000000000000000000000000000;

    uint256 private immutable TOTAL_GONS;
    uint256 private constant MAX_UINT256 = type(uint256).max;
    uint256 public immutable MAX_SUPPLY;

    bool public isStartBuy = false;
    uint256 public immutable buyMarketingFeeRate = 1;
    uint256 public immutable buyTop25DividendFeeRate = 3;
    uint256 public immutable sellMarketingFeeRate = 1;
    uint256 public immutable sellTop50DividendFeeRate = 3;

    address public marketingWallet1;
    address public marketingWallet2;

    uint256 public gonsMarketingFee1;
    uint256 public gonsMarketingFee2;
    uint256 public gonsTop25DividendFee;
    uint256 public gonsTop50DividendFee;

    IUniswapV2Router02 public immutable pancakeSwapRouter;
    IERC20 public immutable usdt;
    address public immutable pair;

    uint256 public swapInterval = 1 hours;
    uint256 public immutable startTransferTime;
    uint256 private _lastSwapTime;
    bool private _swapping = false;
    uint256 public immutable rebaseInterval = 15 minutes;
    uint256 public immutable rebaseRate = 206298644307682;
    uint256 private _lastRebasedTime;
    uint256 private _totalSupply;
    uint256 private _gonsPerFragment;

    uint256 private _pairBalance;
    mapping(address => uint256) private _gonsBalances;
    mapping(address => mapping(address => uint256)) private _allowedFragments;
    mapping(address => bool) private _isExcludedFromFees;
    modifier validRecipient(address to) {
        require(to != address(0x0));
        _;
    }

    constructor(
        string memory name_,
        string memory symbol_,
        uint256 initSupply_,
        uint256 maxSupply_,
        IUniswapV2Router02 pancakeSwapRouter_,
        IERC20 usdt_,
        address marketingWallet1_,
        address marketingWallet2_,
        uint256 startTradeTime_
    ) ERC20Detailed(name_, symbol_) {
        startTransferTime = _lastSwapTime = _lastRebasedTime = startTradeTime_;
        pancakeSwapRouter = pancakeSwapRouter_;
        usdt = usdt_;
        marketingWallet1 = marketingWallet1_;
        marketingWallet2 = marketingWallet2_;
        wrap = new Wrap(IERC20(address(this)), usdt);
        dividend = new Dividend(usdt, BETS(address(this)), owner());
        pair = IUniswapV2Factory(pancakeSwapRouter.factory()).createPair(
            address(usdt),
            address(this)
        );
        _totalSupply = initSupply_ * 10 ** decimals();
        MAX_SUPPLY = maxSupply_ * 10 ** decimals();
        TOTAL_GONS = MAX_UINT256 / 1e10 - ((MAX_UINT256 / 1e10) % _totalSupply);
        _gonsPerFragment = TOTAL_GONS.div(_totalSupply);
        _gonsBalances[msg.sender] = TOTAL_GONS;
        emit Transfer(address(0x0), msg.sender, _totalSupply);
        _allowedFragments[address(this)][
            address(pancakeSwapRouter)
        ] = MAX_UINT256;
        excludeFromFees(address(this), true);
        excludeFromFees(DEAD, true);
        excludeFromFees(ZERO, true);
        excludeFromFees(owner(), true);
        excludeFromFees(marketingWallet1, true);
        excludeFromFees(marketingWallet2, true);
    }

    function gonsBalanceOf(address _address) external view returns (uint256) {
        return _gonsBalanceOf(_address);
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }

    function transfer(
        address to,
        uint256 value
    ) external override validRecipient(to) returns (bool) {
        _transferFrom(msg.sender, to, value);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) external override validRecipient(to) returns (bool) {
        if (_allowedFragments[from][msg.sender] != MAX_UINT256) {
            _allowedFragments[from][msg.sender] = _allowedFragments[from][
                msg.sender
            ].sub(value, "Insufficient Allowance");
        }
        _transferFrom(from, to, value);
        return true;
    }

    function allowance(
        address owner_,
        address spender
    ) external view override returns (uint256) {
        return _allowedFragments[owner_][spender];
    }

    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    ) external returns (bool) {
        uint256 oldValue = _allowedFragments[msg.sender][spender];
        if (subtractedValue >= oldValue) {
            _allowedFragments[msg.sender][spender] = 0;
        } else {
            _allowedFragments[msg.sender][spender] = oldValue.sub(
                subtractedValue
            );
        }
        emit Approval(
            msg.sender,
            spender,
            _allowedFragments[msg.sender][spender]
        );
        return true;
    }

    function increaseAllowance(
        address spender,
        uint256 addedValue
    ) external returns (bool) {
        _allowedFragments[msg.sender][spender] = _allowedFragments[msg.sender][
            spender
        ].add(addedValue);
        emit Approval(
            msg.sender,
            spender,
            _allowedFragments[msg.sender][spender]
        );
        return true;
    }

    function approve(
        address spender,
        uint256 value
    ) external override returns (bool) {
        return _approve(msg.sender, spender, value);
    }

    function _approve(
        address sender,
        address spender,
        uint256 value
    ) private returns (bool) {
        _allowedFragments[sender][spender] = value;
        emit Approval(sender, spender, value);
        return true;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address who) public view override returns (uint256) {
        return
            who == pair
                ? _pairBalance
                : _gonsBalances[who].div(_gonsPerFragment);
    }

    function _basicTransfer(
        address from,
        address to,
        uint256 gonAmount
    ) internal returns (bool) {
        uint256 amount = gonAmount.div(_gonsPerFragment);
        if (from == pair) {
            _pairBalance = _pairBalance.sub(amount);
        } else {
            _gonsBalances[from] = _gonsBalances[from].sub(gonAmount);
        }
        if (to == pair) {
            _pairBalance = _pairBalance.add(amount);
        } else {
            _gonsBalances[to] = _gonsBalances[to].add(gonAmount);
        }
        emit Transfer(from, to, amount);
        return true;
    }

    function _transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) internal returns (bool result) {
        if (_swapping) {
            return
                _basicTransfer(sender, recipient, amount.mul(_gonsPerFragment));
        }
        require(_isStartTransfer(sender, recipient), "Trade not start");
        require(!_isBuy(sender) || _isStartBuy(recipient),"Buy not start");
        if (_shouldRebase()) {
            _rebase();
        }
        if (_shouldSwap(sender, recipient)) {
            _swap();
        }
        uint256 gonAmount = amount.mul(_gonsPerFragment);
        if (_shouldTakeFee(sender, recipient)) {
            gonAmount = _takeFee(sender, gonAmount);
        }
        result = _basicTransfer(sender, recipient, gonAmount);
    }

    function _isBuy(address from) private view returns(bool) {
        return from == pair;
    }

    function _isStartBuy(address to) private view returns(bool){
        return isStartBuy || isExcludedFromFees(to);
    }

    function _isStartTransfer(
        address from,
        address to
    ) private view returns (bool) {
        return
            block.timestamp >= startTransferTime || (isExcludedFromFees(from) || isExcludedFromFees(to));
    }

    function _shouldTakeFee(
        address from,
        address to
    ) private view returns (bool) {
        if (_isExcludedFromFees[from] || _isExcludedFromFees[to] || _swapping) {
            return false;
        } else {
            return true;
        }
    }

    function _shouldRebase() private view returns (bool) {
        return
            (_totalSupply < MAX_SUPPLY) &&
            msg.sender != pair &&
            !_swapping &&
            block.timestamp >= (_lastRebasedTime + rebaseInterval);
    }

    function _shouldSwap(
        address _from,
        address _to
    ) private view returns (bool) {
        return
            !_swapping &&
            _from != pair &&
            _from != owner() &&
            _to != owner() &&
            block.timestamp >= (_lastSwapTime + swapInterval);
    }

    function _takeFee(
        address sender,
        uint256 gonAmount
    ) private returns (uint256 result) {
        result = gonAmount;
        if (sender == pair) {
            //buy
            //Marketing
            uint256 marketingFee = gonAmount.div(100).mul(buyMarketingFeeRate);
            gonsMarketingFee1 = gonsMarketingFee1.add(marketingFee);
            //Top25 Dividend
            uint256 top25DividendFee = gonAmount.div(100).mul(
                buyTop25DividendFeeRate
            );
            gonsTop25DividendFee = gonsTop25DividendFee.add(top25DividendFee);
            uint256 totalFee = top25DividendFee.add(marketingFee);
            totalFee > 0 && _basicTransfer(sender, address(this), totalFee);
            result = result.sub(totalFee);
        } else {
            //sell or transfer

            //Marketing
            uint256 marketingFee = gonAmount.div(100).mul(sellMarketingFeeRate);
            gonsMarketingFee2 = gonsMarketingFee2.add(marketingFee);

            //Top50 Dividend
            uint256 top50DividendFee = gonAmount.div(100).mul(
                sellTop50DividendFeeRate
            );
            gonsTop50DividendFee = gonsTop50DividendFee.add(top50DividendFee);

            uint256 totalFee = top50DividendFee.add(marketingFee);
            totalFee > 0 && _basicTransfer(sender, address(this), totalFee);
            result = result.sub(totalFee);
        }
    }

    function _gonsBalanceOf(address _address) private view returns (uint256) {
        return _gonsBalances[_address];
    }

    function _rebase() private {
        uint256 deltaTime = block.timestamp - _lastRebasedTime;
        uint256 times = deltaTime.div(rebaseInterval);
        uint256 epoch = times.mul(rebaseInterval).div(1 minutes);
        for (uint256 i = 0; i < times; i++) {
            _totalSupply = _totalSupply
                .mul((10 ** decimals()).add(rebaseRate))
                .div(10 ** decimals());
            if (_totalSupply > MAX_SUPPLY) {
                _totalSupply = MAX_SUPPLY;
            }
        }
        _gonsPerFragment = TOTAL_GONS.div(_totalSupply);
        _lastRebasedTime = _lastRebasedTime.add(times.mul(rebaseInterval));
        emit LogRebase(epoch, _totalSupply);
    }

    function _swap() private {
        _swapping = true;
        if (gonsMarketingFee1 > 0) {
            _swapTokensForUsdt(gonsMarketingFee1.div(_gonsPerFragment),marketingWallet1);
            gonsMarketingFee1 = 0;
        }
        if (gonsMarketingFee2 > 0) {
            _swapTokensForUsdt(gonsMarketingFee2.div(_gonsPerFragment),marketingWallet2);
            gonsMarketingFee2 = 0;
        }
        if (gonsTop25DividendFee > 0) {
            dividend.incTop25DividendAmount(
                _swapTokensForUsdt(
                    gonsTop25DividendFee.div(_gonsPerFragment),
                    address(dividend)
                )
            );
            gonsTop25DividendFee = 0;
        }
        if (gonsTop50DividendFee > 0) {
            dividend.incTop50DividendAmount(
                _swapTokensForUsdt(
                    gonsTop50DividendFee.div(_gonsPerFragment),
                    address(dividend)
                )
            );
            gonsTop50DividendFee = 0;
        }
        _lastSwapTime = block.timestamp;
        _swapping = false;
    }

    function _swapTokensForUsdt(
        uint256 tokenAmount,
        address to
    ) private returns (uint256 swapUsdtAmount) {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = address(usdt);
        uint256 beforeUsdtAmount = usdt.balanceOf(to);
        pancakeSwapRouter.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            to,
            block.timestamp
        );
        swapUsdtAmount = usdt.balanceOf(to).sub(beforeUsdtAmount);
        if (to == address(wrap)) wrap.withdraw();
    }

    function manualSync() external {
        IUniswapV2Pair(pair).sync();
    }

    function manualRebase() external {
        if (_shouldRebase()) {
            _rebase();
        }
    }

    function setSwapInterval(uint256 _swapInterval) external onlyOwner {
        swapInterval = _swapInterval;
    }

    function setMarketingWallet2(address _wallet) external onlyOwner {
        marketingWallet2 = _wallet;
    }

    function setMarketingWallet1(address _wallet) external onlyOwner {
        marketingWallet1 = _wallet;
    }

    function setDividend(Dividend _dividend) external onlyOwner{
        dividend = _dividend;
    }

    function setIsStartBuy(bool isStartBuy_) external onlyOwner{
        isStartBuy = isStartBuy_;
    }

    function withdraw(address _token, address payable _to) external onlyOwner {
        if (_token == address(0x0)) {
            payable(_to).transfer(address(this).balance);
        } else {
            IERC20(_token).transfer(
                _to,
                IERC20(_token).balanceOf(address(this))
            );
        }
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        if (_isExcludedFromFees[account] != excluded) {
            _isExcludedFromFees[account] = excluded;
            emit ExcludeFromFees(account, excluded);
        }
    }

    function excludeMultipleAccountsFromFees(
        address[] calldata accounts,
        bool excluded
    ) external onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFees[accounts[i]] = excluded;
        }
        emit ExcludeMultipleAccountsFromFees(accounts, excluded);
    }

    event LogRebase(uint256 indexed epoch, uint256 totalSupply);
    event ExcludeFromFees(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);
}
