contract WST is Ownable,ReentrancyGuard,Pausable, MinterRole,BurnerRole,ERC20("Woonkly Sharing Token", "WST", 18) {
    using SafeMath for uint256;


    uint256 internal limitTotalSupply =450000 ether;

    address operation_wallet;
    IERC20 internal _token;
    uint32 _rate;



    event CrowdSaled(address indexed msgsender,uint256 realamount,uint256 daos  );
    event WalletOpChanged(address indexed oldw, address indexed neww);
    event RateChanged(uint32 oldr,uint32 newr);

    constructor(address op_wallet,address tokenwoop) public {
        operation_wallet = op_wallet;
        _token = IERC20(tokenwoop);
        _rate = 1000;
        _mint(address(this), limitTotalSupply);
    }

    function getToken() external view returns (address) {
        return address(_token);
    }


    function getOpWallet() external view returns(address){
         return  operation_wallet;
    }
    
    function setOpWallet(address new_wallet) public onlyOwner {
        require(new_wallet != address(0), "WST: account is the zero address");
        address old = operation_wallet;
        operation_wallet = new_wallet;
        emit WalletOpChanged(old, new_wallet);
    }

    function getRate() external view returns(uint32){
        return _rate;
    }

    function setRate(uint32 newrate) public onlyOwner {
        require(newrate > 0 ,"WST invalid rate");
        uint32 oldr = _rate;
        _rate = newrate;
        emit RateChanged( oldr,newrate);

    }


    function calcEstimatedWST(uint256 amount) public view returns(uint256,uint256){

        uint256 eamount=amount.div(1 ether);
        uint256 edao=eamount.div(_rate);
        
        if( edao < 1 ) return (0,0);


        uint256 remainder =  amount.sub(edao.mul(1 ether).mul(_rate));
        uint256 dao=edao.mul(1 ether);
         return (dao,remainder);
    }

    
    function crowdsale(uint256 amount) public
        whenNotPaused
        nonReentrant
     {
        require(_token.allowance(_msgSender(), address(this)) >= amount, "WST: Invalid allowance");
        require(amount > 0, "WST: 0 amount!");
        require(_token.balanceOf(_msgSender()) >= amount, "WST: Insufficient user funds");

        (uint256 daos,uint256 remainder) = calcEstimatedWST( amount);

        require( daos >= 1 ether , "WST: Only valid for values greater than or equal 1 WST  ");

        require(balanceOf(address(this)) >= daos, "WST: Insufficient contract funds");

        SafeERC20.safeTransferFrom(
            _token,
            _msgSender(),
            operation_wallet,
            amount.sub(remainder)
        );

        SafeERC20.safeTransfer(
            ERC20(address(this)),
            _msgSender(),
            daos
        );

        emit CrowdSaled(_msgSender(), amount.sub(remainder), daos  );
    }

    function pause() external onlyOwner whenNotPaused {
        _pause();
    }

    function unpause() external onlyOwner whenPaused {
        _unpause();
    }


    /// @notice Creates `_amount` token to `_to`. Must only be called by the owner.
    function mint(address _to, uint256 _amount) public onlyMinter {
        require(_totalSupply.add(_amount) <= limitTotalSupply,"WST:current totalSupply + amount exceeds limitTotalSupply" );
        _mint(_to, _amount);
    }

    /// @notice Bunrs `_amount` token fromo `_from`. Must only be called by the burner.
    function burn(address _from, uint256 _amount) public onlyBurner {
        _burn(_from, _amount);
    }

    /// @notice Presale `_amount` token to `_to`. Must only be called by the minter.
    function presale(address _to, uint256 _amount) public onlyMinter {

        _transfer(address(this), _to, _amount);
    }
    
    
    /**
     * @dev See {ERC20-totalSupply}.
     */
    function totalSupply() public view override returns (uint256) {
        return limitTotalSupply;
    }
    
    
    function getTotalMinted() public view returns(uint256) {
        
        return _totalSupply;
        
    }



}
