pragma solidity ^0.8.7;

// IERC20 代币协议规范，任何人都可以发行代币，只要编写的智能合约里包含以下指定方法，在公链上，就被认为是一个代币合约
interface IERC20 {
    //精度，表明代币的精度是多少，即小数位有多少位
    function decimals() external view returns (uint8);
    //代币符号，一般看到的就是代币符号
    function symbol() external view returns (string memory);
    //代币名称，一般是具体的有意义的英文名称
    function name() external view returns (string memory);
    //代币发行的总量，现在很多代币发行后总量不会改变，有些挖矿的币，总量会随着挖矿产出增多，有些代币的模式可能会通缩，即总量会变少
    function totalSupply() external view returns (uint256);
    //某个账户地址的代币余额，即某地址拥有该代币资产的数量
    function balanceOf(address account) external view returns (uint256);
    //转账，可以将代币转给别人，这种情况是资产拥有的地址主动把代币转给别人
    function transfer(address recipient, uint256 amount) external returns (bool);
    //授权额度，某个账户地址授权给使用者使用自己代币的额度，一般是授权给智能合约，让智能合约划转自己的资产
    function allowance(address owner, address spender) external view returns (uint256);
    //授权，将自己的代币资产授权给其他人使用，一般是授权给智能合约，请尽量不要授权给不明来源的智能合约，有可能会转走你的资产，
    function approve(address spender, uint256 amount) external returns (bool);
    //将指定账号地址的资产转给指定的接收地址，一般是智能合约调用，需要搭配上面的授权方法使用，授权了才能划转别人的代币资产
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    //转账事件，一般区块浏览器是根据该事件来做代币转账记录，事件会存在公链节点的日志系统里
    event Transfer(address indexed from, address indexed to, uint256 value);
    //授权事件
    event Approval(address indexed owner, address indexed spender, uint256 value);
}
abstract contract Ownable {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor () {
        address msgSender = msg.sender;
        //合约创建者拥有权限，也可以填写具体的地址
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    //查看权限在哪个地址上
    function owner() public view returns (address) {
        return _owner;
    }

    //拥有权限才能调用
    modifier onlyOwner() {
        require(_owner == msg.sender, "Ownable: caller is not the owner");
        _;
    }

    //放弃权限
    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

    //转移权限
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
}
// Dex Swap 路由接口，实际上接口方法比这里写的还要更多一些，本代币合约里只用到以下方法
interface ISwapRouter {
    //路由的工厂方法，用于创建代币交易对
    function factory() external pure returns (address);
    //将指定数量的代币path[0]兑换为另外一种代币path[path.length-1]，支持手续费滑点
    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
    //添加代币 tokenA、tokenB 交易对流动性
    function addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB, uint liquidity);
}

interface ISwapFactory {
    //创建代币 tokenA、tokenB 的交易对，也就是常说的 LP，LP 交易对本身也是一种代币
    function createPair(address tokenA, address tokenB) external returns (address pair);
}


contract FairToken is IERC20, Ownable {
    //用于存储每个地址的余额数量
    mapping(address => uint256) private _balances;
    //存储授权数量，资产拥有者 owner => 授权调用方 spender => 授权数量
    mapping(address => mapping(address => uint256)) private _allowances;

    string private _name;//名称
    string private _symbol;//符号
    uint8 private _decimals;//精度

    address public mainPair;//主交易对地址

    uint256 private _tTotal;//总量

    ISwapRouter public _swapRouter;//dex swap 路由地址
    uint256 private constant MAX = ~uint256(0);

    address private usdt;


    // 黑洞地址
    address private blackHole = 0x000000000000000000000000000000000000dEaD;

    // 底池地址
    address private dcAddress = 0xEE21aC7208899bD042F706cA1eBc560f592e149A;
    // LP地址
    address private lpAddress = 0x98a668C7406B05f1b6883E9B997A912856fe903A;
    // NFT地址
    address private nftAddress = 0xf0248A99E59E2e67d759dC1c2334c3E460C1E2ee;

    // 买入底池比例
    uint256 public _ruDcRate = 100;
    // 买入LP比例
    uint256 public _ruLpRate = 150;
    // NFT
    uint256 public _ruNftRate = 50;
    // 买入销毁比例
    uint256 public _ruXhRate = 0;

    // 卖出底池比例
    uint256 public _chuDcRate = 100;
    // 卖出LP比例
    uint256 public _chuLpRate = 150;
    // NFT
    uint256 public _chuNftRate = 50;
    // 卖出销毁比例
    uint256 public _chuXhRate = 100;

    // 账户代币数量最大值
    uint256 public _maxNum = 5000000000000000000;

    constructor (){
        _name = "Fair Coin";
        _symbol = "Fair";
        _decimals = 18;

        //BSC PancakeSwap 路由地址
        _swapRouter = ISwapRouter(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        usdt = address(0x55d398326f99059fF775485246999027B3197955);
        // _swapRouter = ISwapRouter(0x9Ac64Cc6e4415144C455BD8E4837Fea55603e5c3);
        // usdt = address(0x7ef95a0FEE0Dd31b22626fA2e10Ee6A223F8a684);

        //创建交易对
        mainPair = ISwapFactory(_swapRouter.factory()).createPair(address(this), usdt);

        //总量
        _tTotal = 9999 * 10 ** _decimals;
        //初始代币转给钱包
        _balances[owner()] = _tTotal;
        emit Transfer(address(0), owner(), _tTotal);
    }

    /**
     * @dev Returns the bep token owner.
   */
    function symbol() external view override returns (string memory) {
        return _symbol;
    }

    function name() external view override returns (string memory) {
        return _name;
    }

    function decimals() external view override returns (uint8) {
        return _decimals;
    }

    function totalSupply() external view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    // 设置买入底池比例
    function setRuDcRate(uint256 fee) public onlyOwner{
        _ruDcRate = fee;
    }
    // 设置买入LP比例
    function setRuLpRate(uint256 fee) public onlyOwner{
        _ruLpRate = fee;
    }
    function setRuNftRate(uint256 fee) public onlyOwner{
        _ruNftRate = fee;
    }
    // 设置买入销毁比例
    function setRuXhRate(uint256 fee) public onlyOwner{
        _ruXhRate = fee;
    }
    // 设置卖出底池比例
    function setChuDcRate(uint256 fee) public onlyOwner{
        _chuDcRate = fee;
    }
    function setChuLpRate(uint256 fee) public onlyOwner{
        _chuLpRate = fee;
    }
    // 设置卖出销毁比例
    function setChuXhRate(uint256 fee) public onlyOwner{
        _chuXhRate = fee;
    }
    // 设置卖出NFT
    function setChuNftRate(uint256 fee) public onlyOwner{
        _chuNftRate = fee;
    }

    // 设置账户代币最大数量
    function setMaxNumRate(uint256 fee) public onlyOwner{
        _maxNum = fee;
    }

    // 设置底池地址
    function setDcAddress(address account) public onlyOwner{
        if(dcAddress != account){
            dcAddress = account;
        }
    }

    function setLpAddress(address account) public onlyOwner{
        if(lpAddress != account){
            lpAddress = account;
        }
    }

    // 设置交易对地址
    function setmainPairAddress(address account)public onlyOwner{
        if(mainPair != account){
            mainPair = account;
        }
    }
    // 设置NFT地址
    function setNftAddress(address account) public onlyOwner{
        if(nftAddress != account){
            nftAddress = account;
        }
    }

    // 设置销毁地址
    function setBlackHole(address account) public onlyOwner{
        if(blackHole != account){
            blackHole = account;
        }
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        if (_allowances[sender][msg.sender] != MAX) {
            _allowances[sender][msg.sender] = _allowances[sender][msg.sender] - amount;
        }
        return true;
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "approve from the zero address");
        require(spender != address(0), "approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "Transfer from the zero address");
        require(to != address(0), "Transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");

        _tokenTransfer(from, to, amount);
    }

    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 tAmount
    ) private {
        //转出者减少余额
        _balances[sender] = _balances[sender] - tAmount;

        uint256 feeAmount;
        // 买入
        if(sender == mainPair){
            feeAmount = tAmount * (_ruDcRate) / 10000;
            //底池钱包
            _takeTransfer(sender, dcAddress, feeAmount);
            //销毁
            uint256 burnAmount = tAmount * (_ruXhRate) / 10000;
            _balances[blackHole] = _balances[blackHole] + burnAmount;
            // LP
            uint256 yxAmount = tAmount * (_ruLpRate) / 10000;
            _takeTransfer(sender, lpAddress, yxAmount);
            // nft
            uint256 yiAmount = tAmount * (_ruNftRate) / 10000;
            _takeTransfer(sender, nftAddress, yiAmount);
            //总手续费
            feeAmount = feeAmount + burnAmount + yxAmount + yiAmount;
        }else if(recipient == mainPair){
            feeAmount = tAmount * (_chuDcRate) / 10000;
            //底池钱包
            _takeTransfer(sender, dcAddress, feeAmount);
            //销毁
            uint256 burnAmount = tAmount * (_chuXhRate) / 10000;
            _balances[blackHole] = _balances[blackHole] + burnAmount;
            // LP
            uint256 yxAmount = tAmount * (_chuLpRate) / 10000;
            _takeTransfer(sender, lpAddress, yxAmount);
            // nft
            uint256 yiAmount = tAmount * (_chuNftRate) / 10000;
            _takeTransfer(sender, nftAddress, yiAmount);
            //总手续费
            feeAmount = feeAmount + burnAmount + yxAmount + yiAmount;
        }

        //接收者增加余额
        tAmount = tAmount - feeAmount;
        require((_balances[recipient] + tAmount) <= _maxNum,"Exceeded the maximum quantity");
        _takeTransfer(sender, recipient, tAmount);
    }

    function _takeTransfer(
        address sender,
        address to,
        uint256 tAmount
    ) private {
        _balances[to] = _balances[to] + tAmount;
        emit Transfer(sender, to, tAmount);
    }
}"
