    "contracts/ethregistrar/SpacePiRegistrarFreeController.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import {BaseRegistrarImplementation} from "./BaseRegistrarImplementation.sol";
import {StringUtils} from "../utils/StringUtils.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {EIP712, ECDSA} from "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import {SafeERC20, IERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {RegInfo} from "../struct/PidStruct.sol";
import "../price-oracle/IPriceOracle.sol";
import "../resolvers/Resolver.sol";
import "../utils/BytesUtils.sol";

contract SpacePiRegistrarFreeController is Ownable, EIP712, ReentrancyGuard {
    using SafeERC20 for IERC20;
    using StringUtils for *;
    using BytesUtils for *;
    // state variable
    bytes32 private constant _REGISTRAR_TYPE_HASH =
        keccak256(
            "Registrar(bytes32 secret,address receiver,bytes32 label,uint256 signedAt)"
        );
    uint public constant MIN_REGISTRATION_DURATION = 365 days;

    uint256 public constant SIGN_EXPIRATION = 30 minutes;
    IERC20 public immutable SpacePiToken =
        IERC20(0x69b14e8D3CEBfDD8196Bfe530954A0C226E5008E);
    address public signer;
    IPriceOracle prices;
    uint public minCommitmentAge;
    uint public maxCommitmentAge;
    BaseRegistrarImplementation public base;

    mapping(bytes32 => uint) public commitments;

    event NameRegistered(
        string name,
        bytes32 indexed label,
        address indexed owner,
        uint cost,
        uint expires
    );
    event NameRenewed(
        string name,
        bytes32 indexed label,
        uint cost,
        uint expires
    );
    event NewPriceOracle(address indexed oracle);

    constructor(
        BaseRegistrarImplementation _base,
        IPriceOracle _prices,
        address _signer,
        uint _minCommitmentAge,
        uint _maxCommitmentAge
    ) EIP712("SpacePiRegistrarFreeController", "1") {
        require(_signer != address(0), "signer cannot be zero address");
        signer = _signer;
        base = _base;
        prices = _prices;
        minCommitmentAge = _minCommitmentAge;
        maxCommitmentAge = _maxCommitmentAge;
    }

    function _hash(
        bytes32 secret,
        address receiver,
        bytes32 label,
        uint256 signedAt
    ) public view returns (bytes32) {
        return
            _hashTypedDataV4(
                keccak256(
                    abi.encode(
                        _REGISTRAR_TYPE_HASH,
                        secret,
                        receiver,
                        label,
                        signedAt
                    )
                )
            );
    }

    function _verifySignature(
        bytes32 hash,
        bytes calldata signature
    ) private view returns (bool) {
        return ECDSA.recover(hash, signature) == signer;
    }

    function verifyCertificate(
        bytes32 secret,
        address receiver,
        bytes32 label,
        uint256 signedAt,
        bytes calldata signature
    ) public view returns (bool) {
        return
            block.timestamp < signedAt + SIGN_EXPIRATION &&
            _verifySignature(
                _hash(secret, receiver, label, signedAt),
                signature
            );
    }

    function valid(string calldata name) public pure returns (bool) {
        if (name.strlen() < 3) return false;
        bytes memory nb = bytes(name);
        for (uint i; i < nb.length; ++i) {
            if (bytes1(nb[i]) < 0x30 || bytes1(nb[i]) > 0x39) return false;
        }
        return true;
    }

    function changeSigner(address _signer) public onlyOwner {
        require(msg.sender == signer, "only Owner can change signer");
        signer = _signer;
    }

    function available(string calldata name) public view returns (bool) {
        bytes32 label = keccak256(bytes(name));
        return valid(name) && base.available(uint256(label));
    }

    // @param name is the name to register
    // @param owner is the owner of the name
    // @param secret is the secret to commit to, in order to prevent front running
    function makeCommitment(
        string calldata name,
        address owner,
        bytes32 secret
    ) public pure returns (bytes32) {
        bytes32 label = keccak256(bytes(name));
        return keccak256(abi.encodePacked(label, owner, secret));
    }

    // @dev 提交一个注册请求
    function commit(bytes32 commitment) public {
        require(commitments[commitment] + maxCommitmentAge < block.timestamp);
        commitments[commitment] = block.timestamp;
    }

    // because this function returns fund based on msg.value
    // it MUST be an external function to avoid accidental call that
    // returns incorrect amount, e.g., bulk register.
    function register(
        string calldata name,
        address owner,
        uint signAt,
        bytes32 secret,
        bytes calldata signature
    ) external {
        require(
            verifyCertificate(
                secret,
                owner,
                keccak256(bytes(name)),
                signAt,
                signature
            ),
            "Invalid signature"
        );
        _register(
            name,
            RegInfo(
                owner,
                MIN_REGISTRATION_DURATION,
                secret,
                address(0),
                false,
                0
            )
        );
    }

    // because this function returns fund based on msg.value
    // it MUST be an external function to avoid accidental call that
    // returns incorrect amount, e.g., bulk register.
    function registerWithConfig(
        string calldata name,
        address owner,
        uint signAt,
        bytes32 secret,
        address resolver,
        bytes calldata signature
    ) external {
        require(
            verifyCertificate(
                secret,
                owner,
                keccak256(bytes(name)),
                signAt,
                signature
            ),
            "Invalid referral signature"
        );
        _register(
            name,
            RegInfo(
                owner,
                MIN_REGISTRATION_DURATION,
                secret,
                resolver,
                false,
                0
            )
        );
    }

    function _register(
        string calldata name,
        RegInfo memory regInfo
    ) internal nonReentrant returns (uint256 cost) {
        bytes32 commitment = makeCommitment(
            name,
            regInfo.owner,
            regInfo.secret
        );
        cost = _consumeCommitment(name, regInfo.duration, commitment);

        bytes32 label = keccak256(bytes(name));
        uint256 tokenId = uint256(label);

        uint expires;
        if (regInfo.resolver != address(0)) {
            // Set this contract as the (temporary) owner, giving it
            // permission to set up the resolver.
            expires = base.register(tokenId, address(this), regInfo.duration);

            // The nodeHash of this label
            bytes32 nodeHash = keccak256(
                abi.encodePacked(base.baseNode(), label)
            );

            // Set the resolver
            base.pid().setResolver(nodeHash, regInfo.resolver);
            Resolver(regInfo.resolver).setName(nodeHash, name);
            Resolver(regInfo.resolver).setAddr(nodeHash, regInfo.owner);

            // Now transfer full ownership to the expected owner
            base.reclaim(tokenId, regInfo.owner);
            base.transferFrom(address(this), regInfo.owner, tokenId);
        } else {
            expires = base.register(tokenId, regInfo.owner, regInfo.duration);
        }

        emit NameRegistered(name, label, regInfo.owner, cost, expires);

        return cost;
    }

    // because this function returns fund based on msg.value
    // it MUST be an external function to avoid accidental call that
    // returns incorrect amount, e.g., bulk register.
    function renew(
        string calldata name,
        uint duration,
        uint256 paid
    ) external payable {
        bool sent = SpacePiToken.transferFrom(msg.sender, address(this), paid);
        require(sent, "Failed to send SpacePiToken");

        uint256 cost = _renew(name, duration, paid);
        require(paid >= cost, "Insufficient fund");
        // Refund any extra payment
        if (paid > cost) {
            sent = SpacePiToken.transferFrom(
                msg.sender,
                address(this),
                paid - cost
            );
            require(sent, "Failed to send SpacePiToken back");
        }
    }

    function _renew(
        string calldata name,
        uint duration,
        uint256 paid
    ) internal nonReentrant returns (uint256 cost) {
        IPriceOracle.Price memory price = rentPrice(name, duration);
        cost = (price.base + price.premium);
        require(paid >= cost);
        bytes32 label = keccak256(bytes(name));
        uint expires = base.renew(uint256(label), duration);
        emit NameRenewed(name, label, cost, expires);
        return cost;
    }

    function rentPrice(
        string calldata name,
        uint256 duration
    ) public view returns (IPriceOracle.Price memory price) {
        bytes32 label = keccak256(bytes(name));
        price = prices.price(name, base.nameExpires(uint256(label)), duration);
    }

    function _consumeCommitment(
        string calldata name,
        uint duration,
        bytes32 commitment
    ) internal returns (uint256) {
        // Require a valid commitment
        require(
            commitments[commitment] + minCommitmentAge <= block.timestamp,
            "Commitment too young"
        );
        // If the commitment is too old, or the name is registered, stop
        require(
            commitments[commitment] + maxCommitmentAge > block.timestamp,
            "Commitment too old"
        );
        require(available(name), "Name already registered");
        delete (commitments[commitment]);
        require(duration >= MIN_REGISTRATION_DURATION, "Invalid duration");
        return 0;
    }
}
"
    },
    "contracts/price-oracle/IPriceOracle.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity >=0.8.17 <0.9.0;

interface IPriceOracle {
    struct Price {
        uint256 base;
        uint256 premium;
    }

    /**
     * @dev Returns the price to register or renew a name.
     * @param name The name being registered or renewed.
     * @param expires When the name presently expires (0 if this is a new registration).
     * @param duration How long the name is being registered or extended for, in seconds.
     * @return base premium tuple of base price + premium price
     */
    function price(
        string calldata name,
        uint256 expires,
        uint256 duration
    ) external view returns (Price calldata);
}
