      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.14;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./interfaces/IORVote.sol";

/// @title Staking contract to earn Voting Power
/// @notice Bet GQ token and earn ORVP token for using governance system
/// @author Ibai -Nexxyo Labs
contract StakingVote is Ownable, ReentrancyGuard {
    //============== LIBRARIES ==============
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using SafeERC20 for IORVote;

    //============== STRUCTS ==============
    struct UserInfo {
        uint256 amount;
        uint256 rewardDebt;
        uint256 depositTimestamp;
    }

    struct PoolInfo {
        IERC20 stakeToken;
        uint256 allocPoint;
        uint256 lastRewardBlock;
        uint256 accVoteTokenPerShare;
        uint256 timeLock;
        uint256 multiplier;
        uint256 totalStaked;
    }

    //============== VARIABLES ==============
    // Token variable declaration
    IORVote public voteToken;
    IERC20 public stakeToken;
    // Array of pools info
    PoolInfo[] public poolInfo;
    // User info by stake
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;

    // Vote token per block
    uint256 public voteTokenPerBlock;
    // Number that determines the total allocation points
    uint256 public totalAllocPoint;
    // Block to set the reward start block
    uint256 public startBlock;

    //============== EVENTS ==============
    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Claim(address indexed user, uint256 indexed pid);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(
        address indexed user,
        uint256 indexed pid,
        uint256 amount
    );

    //============== CONSTRUCTOR ==============
    constructor(
        IORVote _voteToken,
        IERC20 _stakeToken,
        uint256 _voteTokenPerBlock,
        uint256 _startBlock
    ) {
        voteToken = _voteToken;
        stakeToken = _stakeToken;
        voteTokenPerBlock = _voteTokenPerBlock;
        startBlock = _startBlock;
    }

    //============== EXTERNAL FUNCTIONS ==============

    /// @notice Add a new pool with different configuration
    /// @param allocPoint allocation points assigned to this new pool
    /// @param token the stake token added to the pool
    /// @param timeLock the time for locking tokens
    function add(
        uint256 allocPoint,
        IERC20 token,
        uint256 timeLock,
        uint256 multiplier
    ) external onlyOwner {
        require(token == stakeToken, "add: token != stakeToken");
        massUpdatePools();
        uint256 lastRewardBlock = block.number > startBlock
            ? block.number
            : startBlock;
        totalAllocPoint = totalAllocPoint + allocPoint;
        poolInfo.push(
            PoolInfo({
                stakeToken: token,
                allocPoint: allocPoint,
                lastRewardBlock: lastRewardBlock,
                accVoteTokenPerShare: 0,
                timeLock: timeLock,
                multiplier: multiplier,
                totalStaked: 0
            })
        );
    }

    /// @notice Update alloc points for the given pool
    /// @param pid The pool identifier
    /// @param allocPoint The new alloc point quantity
    function set(uint256 pid, uint256 allocPoint) external onlyOwner {
        massUpdatePools();
        totalAllocPoint = totalAllocPoint.sub(poolInfo[pid].allocPoint).add(
            allocPoint
        );
        poolInfo[pid].allocPoint = allocPoint;
    }

    /// @notice Update token per block
    /// @param _newVoteTokenPerBlock The new token per block amount
    function updateVoteTokenPerBlock(uint256 _newVoteTokenPerBlock)
        external
        onlyOwner
    {
        voteTokenPerBlock = _newVoteTokenPerBlock;
    }

    /// @notice Return reward multiplier over the given _from to _to block
    /// @param from From block
    /// @param to To block
    /// @param poolMultiplier The pool multiplier parameter
    /// @return Multiplier value
    function getMultiplier(
        uint256 from,
        uint256 to,
        uint256 poolMultiplier
    ) public pure returns (uint256) {
        return to.sub(from).mul(poolMultiplier);
    }

    /// @notice Deposit tokens to the contract to receive rewards
    /// @param pid The pool identifier where user deposit tokens
    /// @param amount The amount to deposit
    function deposit(uint256 pid, uint256 amount) external nonReentrant {
        require(pid < poolInfo.length, "deposit: pool exists?");
        PoolInfo storage pool = poolInfo[pid];
        UserInfo storage user = userInfo[pid][msg.sender];
        updatePool(pid);
        if (user.amount > 0) {
            uint256 pending = user
                .amount
                .mul(pool.accVoteTokenPerShare)
                .div(1e12)
                .sub(user.rewardDebt);
            if (pending > 0) {
                safeVoteTokenTransfer(msg.sender, pending);
            }
        }
        if (amount > 0) {
            pool.stakeToken.safeTransferFrom(
                address(msg.sender),
                address(this),
                amount
            );
            user.amount = user.amount.add(amount);
            user.depositTimestamp = block.timestamp;
            pool.totalStaked = pool.totalStaked.add(amount);
        }
        user.rewardDebt = user.amount.mul(pool.accVoteTokenPerShare).div(1e12);
        emit Deposit(msg.sender, pid, amount);
    }

    /// @notice Deposit tokens to the contract to receive rewards
    /// @param pid The pool identifier where user deposit tokens
    function claim(uint256 pid) external nonReentrant {
        require(pid < poolInfo.length, "deposit: pool exists?");
        PoolInfo storage pool = poolInfo[pid];
        UserInfo storage user = userInfo[pid][msg.sender];
        updatePool(pid);
        if (user.amount > 0) {
            uint256 pending = user
                .amount
                .mul(pool.accVoteTokenPerShare)
                .div(1e12)
                .sub(user.rewardDebt);
            if (pending > 0) {
                safeVoteTokenTransfer(msg.sender, pending);
            }
        }
        user.rewardDebt = user.amount.mul(pool.accVoteTokenPerShare).div(1e12);
        emit Claim(msg.sender, pid);
    }

    /// @notice Withdraw tokens from the contract
    /// @param pid The pool identifier where user withdraw tokens
    /// @param amount The amount to withdraw
    function withdraw(uint256 pid, uint256 amount) external nonReentrant {
        require(pid < poolInfo.length, "withdraw: pool exists?");
        PoolInfo storage pool = poolInfo[pid];
        UserInfo storage user = userInfo[pid][msg.sender];
        require(
            block.timestamp > (user.depositTimestamp + pool.timeLock),
            "withdraw: locked"
        );
        require(user.amount >= amount, "withdraw: not good amount");

        updatePool(pid);
        uint256 pending = user
            .amount
            .mul(pool.accVoteTokenPerShare)
            .div(1e12)
            .sub(user.rewardDebt);
        if (pending > 0) {
            safeVoteTokenTransfer(msg.sender, pending);
        }
        if (amount > 0) {
            pool.stakeToken.safeTransfer(address(msg.sender), amount);
            user.amount = user.amount.sub(amount);
            pool.totalStaked = pool.totalStaked.sub(amount);
        }
        user.rewardDebt = user.amount.mul(pool.accVoteTokenPerShare).div(1e12);
        emit Withdraw(msg.sender, pid, amount);
    }

    /// @notice Withdraw without caring about rewards. EMERGENCY ONLY.
    /// @param pid The pool identifier where user withdraws
    function emergencyWithdraw(uint256 pid) external nonReentrant {
        PoolInfo storage pool = poolInfo[pid];
        UserInfo storage user = userInfo[pid][msg.sender];
        require(
            block.timestamp > (user.depositTimestamp + pool.timeLock),
            "withdraw: locked"
        );
        pool.stakeToken.safeTransfer(address(msg.sender), user.amount);
        pool.totalStaked = pool.totalStaked.sub(user.amount);
        emit EmergencyWithdraw(msg.sender, pid, user.amount);
        user.amount = 0;
        user.rewardDebt = 0;
    }

    /// @notice Function to see pending tokens
    /// @param pid Identifier of the pool in which we consult the pending rewards
    /// @param user User that consult the rewards
    function pendingVoteToken(uint256 pid, address user)
        external
        view
        returns (uint256)
    {
        PoolInfo storage pool = poolInfo[pid];
        UserInfo storage _user = userInfo[pid][user];
        uint256 accVoteTokenPerShare = pool.accVoteTokenPerShare;
        uint256 supply = pool.totalStaked;
        if (block.number > pool.lastRewardBlock && supply != 0) {
            uint256 multiplier = getMultiplier(
                pool.lastRewardBlock,
                block.number,
                pool.multiplier
            );
            uint256 resourceReward = multiplier
                .mul(voteTokenPerBlock)
                .mul(pool.allocPoint)
                .div(totalAllocPoint);
            accVoteTokenPerShare = accVoteTokenPerShare.add(
                resourceReward.mul(1e12).div(supply)
            );
        }
        return
            _user.amount.mul(accVoteTokenPerShare).div(1e12).sub(
                _user.rewardDebt
            );
    }

    /// @notice Gets the length for the pool info array
    /// @return poolInfo length
    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    //============== PUBLIC FUNCTIONS ==============

    /// @notice Update reward variables
    /// @param pid The pool identifier to update
    function updatePool(uint256 pid) public {
        PoolInfo storage pool = poolInfo[pid];
        if (block.number <= pool.lastRewardBlock) return;
        uint256 supply = pool.totalStaked;
        if (supply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(
            pool.lastRewardBlock,
            block.number,
            pool.multiplier
        );
        uint256 voteTokenReward = multiplier
            .mul(voteTokenPerBlock)
            .mul(pool.allocPoint)
            .div(totalAllocPoint);

        voteToken.mint(address(this), voteTokenReward);
        pool.accVoteTokenPerShare = pool.accVoteTokenPerShare.add(
            voteTokenReward.mul(1e12).div(supply)
        );
        pool.lastRewardBlock = block.number;
    }

    /// @notice Update reward variables for all pools
    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    //============== INTERNAL FUNCTIONS ==============

    /// @notice Safe token transfer function, just in case if rounding error causes pool to not have enough Vote tokens
    /// @param to address for token receiver
    /// @param amount Tokens that receiver will receive
    function safeVoteTokenTransfer(address to, uint256 amount) internal {
        uint256 voteTokenBal = voteToken.balanceOf(address(this));
        if (amount > voteTokenBal) {
            voteToken.safeTransfer(to, voteTokenBal);
        } else {
            voteToken.safeTransfer(to, amount);
        }
    }
}
