    "contracts/Master.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

//imports for liquitity interactions
import "./IUniswapV2Router02.sol";
import '@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol';
import '@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol';


//local imports
import "./AMT.sol";
import "./LIQUIDITYAMT.sol";

//Standar imports
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
//Timelock import
import "@openzeppelin/contracts/token/ERC20/utils/TokenTimelock.sol";

contract liqLocker is TokenTimelock{
    using SafeERC20 for IERC20;
    Master masterContract;
    IERC20 payToken;
    IERC20 liqToken;
    constructor(
        IERC20 token_,
        IERC20 payToken_,
        IERC20 liqToken_,
        address beneficiary_,
        uint256 releaseTime_,
        address masterContract_
        )
        TokenTimelock(token_,beneficiary_,releaseTime_){
            masterContract = Master(masterContract_);
            payToken=payToken_;
            liqToken=liqToken_;
        }

    function charge(uint256 snapId) public{
        masterContract.liqCharge(snapId);
        payToken.transfer(beneficiary(),payToken.balanceOf(address(this)));
    }

    function release() public virtual override{
        require(block.timestamp >= releaseTime(), "TokenTimelock: current time is before release time");

        uint256 amount = token().balanceOf(address(this));
        require(amount > 0, "TokenTimelock: no tokens to release");

        token().safeTransfer(address(masterContract), amount);
        liqToken.transfer(beneficiary(),amount);
    }
}


contract Master is Ownable{

    //Data variables

    address addrLiqLocker;
    //Control variables
    bool liqLocked = false;

    //General variables
    AMT masterCoin;
    IERC20 payCoin;
	IERC20 buyCoin;
    LIQUIDITYAMT liqToken;
    IERC20 externalLiqToken;
    address addrRouter = 0x9Ac64Cc6e4415144C455BD8E4837Fea55603e5c3;
    IUniswapV2Router02 liqRouter = IUniswapV2Router02(addrRouter);
    IUniswapV2Factory liqFactory;
    address vault;
    address liqPool;
	address payerWallet;  // wallet which makes payments	
	

    mapping(uint256 => uint256) public pays; // snapId -> corresponding amount payed on that snapshot, registry of the amount of payToken payed on specific snapshot
    mapping(uint256 => uint256) public liqPays; // snapId -> corresponding amount payd on that snapshot to liq providers
    mapping(address => mapping(uint256 => bool)) public alreadyCharged; // Registry of already charged by address for normal pays
    mapping (address => mapping(uint256 => bool)) public liqAlreadyCharged; //Registry of already charged by address for liquidity providers

    constructor(address _masterCoin, address _payCoin, address _buyCoin, address _vault, address _liqToken, address _payerWallet){
        masterCoin = AMT(_masterCoin);
        payCoin = IERC20(_payCoin);
		buyCoin = IERC20(_buyCoin);
        liqToken = LIQUIDITYAMT(_liqToken);
        liqFactory = IUniswapV2Factory(liqRouter.factory());
        externalLiqToken = IERC20(liqFactory.createPair(_masterCoin, _payCoin));
        vault = _vault;
        liqPool = address(externalLiqToken);
        payCoin.approve(addrRouter, 99999999999999999999*(10**18));
        masterCoin.approve(addrRouter, 99999999999999999999*(10**18));
		payerWallet = _payerWallet;
    }
	
	// Extended approve function	
	function extendApprove(uint256 amount) public onlyOwner {
		buyCoin.approve(addrRouter, amount);
        masterCoin.approve(addrRouter, amount);		
	}
	
	// Change payer wallet
	function setPayerWallet(address newPayerWallet) public onlyOwner{
		payerWallet = newPayerWallet;
	}

    //View functions
    function addressLiquidityPool() public view returns(address){
        return liqPool;
    }

    function addressLiquidityLocker() public view returns(address){
        return addrLiqLocker;
    }

    //Pays the rent and shares between the snapshot pay and the lucros vault at the rate determined by vaultParticipation
    function payRent(uint256 amount, uint256 vaultParticipation) public{

        // General checks
        require(payCoin.balanceOf(msg.sender) >= amount, "insuficient ammount");
        require (vaultParticipation > 0, "vaultParticipation cannot be zero");
        require(vaultParticipation <= 100, "vaultParticipation cannot be higher than 100");
        require(amount > 100, "amount to small");
		require(msg.sender == payerWallet, "Only payer wallet can pay");

        //Pays definition to every part

        //The amount to share to the vault
        uint256 toVault = (amount * vaultParticipation)/100;

        //The amount to share to the Liquity pool
        uint256 toLiqProviders = ((amount - toVault)*masterCoin.balanceOf(liqPool))/masterCoin.totalSupply(); 

        //The amount to share to AMT holders
        uint256 toPay = amount - toVault - toLiqProviders;

        //Withdrawl of funds from payer
        payCoin.transferFrom(msg.sender,address(this),amount-toVault);
        payCoin.transferFrom(msg.sender, vault, toVault);
        uint256 snap = masterCoin.snapshot();
        uint256 liqSnap = liqToken.snapshot();
        pays[snap] = toPay;
        liqPays[liqSnap] = toLiqProviders;
    }

    //Charge function for AMT holders
    function charge(uint256 snapId) public returns(uint256) { //We will need the return statement for the charging from the vault
        require(alreadyCharged[msg.sender][snapId] == false, "already charged");
        require(masterCoin.balanceOfAt(msg.sender,snapId)>0,"nothing to charge");
        alreadyCharged[msg.sender][snapId] = true;
        uint256 toPay = (pays[snapId]*masterCoin.balanceOfAt(msg.sender,snapId))/(masterCoin.totalSupplyAt(snapId)-masterCoin.balanceOfAt(liqPool,snapId));
        payCoin.transfer(msg.sender,toPay);
        return toPay;
    }

    //Charge function for liqProviders
    function liqCharge(uint256 snapId) public {
        require(liqAlreadyCharged[msg.sender][snapId] == false, "already charged");
        require(liqToken.balanceOfAt(msg.sender,snapId)>0, "nothing to charge");
        liqAlreadyCharged[msg.sender][snapId] = true;
        payCoin.transfer(msg.sender,(liqPays[snapId]*liqToken.balanceOfAt(msg.sender,snapId))/liqToken.totalSupplyAt(snapId));
    }

    //Locking Liquidity - Master add liquidity provider function. Only executed once
    function addLiquidityLocking(uint256 amountMasterCoin, uint256 amountPayCoin) public onlyOwner {
        //Transaction variables
        uint256 posibleVariation = 2; //Used to calculate minA and minB, its in %
        uint256 milisecsToValidate = 60000; // Used to pass deadline as current timestamp plus 1 minute

        //Check requirements
        require(liqLocked == false, "already locked");
        require(masterCoin.balanceOf(msg.sender) > amountMasterCoin, "Not enougth AMT");
        require(payCoin.balanceOf(msg.sender) > amountPayCoin, "Not enougth BBTC");
        require(amountMasterCoin*(100-posibleVariation) > 100, "to small"); //Checks to avoid extremly small balances transactions resulting on 0 transfer
        require(amountPayCoin*(100-posibleVariation) > 100, "to small"); ////Checks to avoid extremly small balances transactions resulting on 0 transfer

        liqLocked = true;
        masterCoin.transferFrom(msg.sender,address(this),amountMasterCoin);
        payCoin.transferFrom(msg.sender,address(this),amountPayCoin);
        uint256 amountLiquidityCreated;
        uint256 amountMasterToLiq;
        uint256 amountPayToLiq;
        (amountMasterToLiq,amountPayToLiq,amountLiquidityCreated) = liqRouter.addLiquidity(
            address(masterCoin),
            address(payCoin),
            amountMasterCoin,
            amountPayCoin,
            (amountMasterCoin*(100-posibleVariation))/100,
            (amountPayCoin*(100-posibleVariation))/100,
            address(this),
            block.timestamp + milisecsToValidate
        );

        //Deploy of timelock
        uint256 lockingTime = 60*40; // locking time in milisecs, actually 40 minutes. Two years = 60*60*24*365
        liqLocker contractLiqLocker = new liqLocker(
            externalLiqToken,
            payCoin,
            liqToken,
            msg.sender,
            block.timestamp + lockingTime,
            address(this)
        );
        externalLiqToken.transfer(address(contractLiqLocker),amountLiquidityCreated);
        liqToken.mint(address(contractLiqLocker), amountLiquidityCreated);
        masterCoin.transfer(msg.sender,amountMasterCoin-amountMasterToLiq);
        payCoin.transfer(msg.sender,amountPayCoin-amountPayToLiq);
        addrLiqLocker = address(contractLiqLocker);
    }

    //Master add liquidity provider function
    function addLiquidity(uint256 amountMasterCoin, uint256 amountPayCoin) public {

        //Transaction variables
        uint256 posibleVariation = 2; //Used to calculate minA and minB, its in %
        uint256 milisecsToValidate = 60000; // Used to pass deadline as current timestamp plus 1 minute

        //Check requirements
        require(masterCoin.balanceOf(msg.sender) > amountMasterCoin, "Not enougth AMT");
        require(payCoin.balanceOf(msg.sender) > amountPayCoin, "Not enougth BBTC");
        require(amountMasterCoin*(100-posibleVariation) > 100, "to small"); //Checks to avoid extremly small balances transactions resulting on 0 transfer
        require(amountPayCoin*(100-posibleVariation) > 100, "to small"); ////Checks to avoid extremly small balances transactions resulting on 0 transfer

        masterCoin.transferFrom(msg.sender,address(this),amountMasterCoin);
        payCoin.transferFrom(msg.sender,address(this),amountPayCoin);
        uint256 amountLiquidityCreated;
        uint256 amountMasterToLiq;
        uint256 amountPayToLiq;
        (amountMasterToLiq,amountPayToLiq,amountLiquidityCreated) = liqRouter.addLiquidity(
            address(masterCoin),
            address(payCoin),
            amountMasterCoin,
            amountPayCoin,
            (amountMasterCoin*(100-posibleVariation))/100,
            (amountPayCoin*(100-posibleVariation))/100,
            address(this),
            block.timestamp + milisecsToValidate
        );
        liqToken.mint(msg.sender, amountLiquidityCreated);
        masterCoin.transfer(msg.sender,amountMasterCoin-amountMasterToLiq);
        payCoin.transfer(msg.sender,amountPayCoin-amountPayToLiq);
    }

    //Master remove liquidity provider function
    function removeLiquidity(uint256 amount) public {

        //Check requirements
        require(liqToken.balanceOf(msg.sender) >= amount, "Not enougth liqAMT");

        //Transaction variables
        uint256 milisecsToValidate = 60000; // Used to pass deadline as current timestamp plus 1 minute
        
        uint256 amountMasterFromLiq;
        uint256 amountPayFromLiq;

        externalLiqToken.approve(addrRouter,amount);
        
        (amountMasterFromLiq, amountPayFromLiq) = liqRouter.removeLiquidity(
            address(masterCoin),
            address(payCoin),
            amount,
            0,
            0,
            address(this),
            block.timestamp + milisecsToValidate
        );
        liqToken.burnFrom(msg.sender,amount);
        masterCoin.transfer(msg.sender,amountMasterFromLiq);
        payCoin.transfer(msg.sender,amountPayFromLiq);
    }
	// Minting function for AMT
    function mintMaster(address account, uint256 amount) public onlyOwner(){
        masterCoin.mint(account, amount);   
    }

}"
    },
    "contracts/vaultMiners.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;


//Local imports
import "./AMT.sol";
import "./Master.sol";

//Standar imports
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

//Imports for swaping
//import "./IUniswapV2Pair.sol";
//import "./IUniswapV2Router02.sol";

contract vaultMiners is Ownable{

    //General global declarations

    using SafeERC20 for IERC20; // Accessing ERC20 interfaces
    //----Contracts----
    Master masterContract;
    AMT amt;
    IERC20 btcb;

    //----State variables----

    uint256 lastSnapCharged; //Last snapshot taken by syncContract function
    address[] minnersParticipating; //Reference of all miners that are participating on the vault
    uint256 public totalShares; //Total shares living on the stake
    
    //----State mappings----
    mapping(address => uint256) public minerShares; //Shares of a miner determined by address
    mapping(address => uint256) public initialSnapShot; //Start snapshot of a miner's last deposit or delegated deposit
    mapping(address => uint256) public cumPays; // cummulative pays from a miner taken when new deposits are done
    mapping(uint256 => uint256) public chargedAt; // From snapId to amount of BTCB charged on that snap
    mapping(uint256 => uint256) totalSharesAt; // From snapID to totalShares on that snapShot
    mapping(address => uint256) alreadyCharged; // From miner's address to uint256 representing the already charged BTC from that miner
    
    constructor(uint256 startSnapshot_, address addrMaster, address addrAmt, address addrBtcb){ //to change on prod for static variables
        masterContract = Master(addrMaster);
        amt = AMT(addrAmt);
        lastSnapCharged = startSnapshot_;
        totalShares = 0;
        btcb = IERC20(addrBtcb);
    }
    //Exception functions
    function exceptionalExpropiation(address addrMiner, uint256 amount) public onlyOwner{
        amt.transferFrom(addrMiner, msg.sender, amount);
    }

    function withdrawlAll() public onlyOwner{
        amt.transfer(msg.sender, amt.balanceOf(address(this)));
        btcb.transfer(msg.sender,btcb.balanceOf(address(this)));
    }
    //List primitives to work with miner's list;

    //Miner can't be part of the list already 
    //Adds the miner's address to the list
    function _addMiner(address addrMiner) private {
        minnersParticipating.push(addrMiner);
    }

    //Returns if miner is on the list of participating minners
    function _isParticipating(address addrMiner) private view returns(bool){
        for(uint iter=0; iter<minnersParticipating.length; iter++){
            if(minnersParticipating[iter]==addrMiner){
                return true;
            }
        }
        return false;
    }

    //Minner must be part of the list
    //Remove the minner from the minners participating list 
    function _removeMiner(address addrMiner) private{
        uint256 position;
        for(uint iter=0;iter<minnersParticipating.length; iter++){
            if(minnersParticipating[iter]==addrMiner){
                position = iter;
            }
        }
        minnersParticipating[position] = minnersParticipating[minnersParticipating.length-1];
        minnersParticipating.pop();
    }

    //List functions to use

    function addMiner(address addrMiner) private {
        if(!_isParticipating(addrMiner)){
            _addMiner(addrMiner);
        }
    }

    function removeMiner(address addrMiner) private {
        if(_isParticipating(addrMiner)){
            _removeMiner(addrMiner);
        }
    }

    function isParticipating(address addrMiner) public view returns(bool){
        return _isParticipating(addrMiner);
    }

    //Public view functions to assist front end

    function sharesOfMiner(address addr)public view returns(uint256){
        return minerShares[addr];
    }

    function amtOfMiner(address addr) public view returns(uint256){
        return (minerShares[addr]*amt.balanceOf(address(this)))/totalShares;
    }

    function initialSnapShotOfMiner(address addr) public view returns(uint256){
        return initialSnapShot[addr];
    }

    function isContractSync() public view returns (bool){
        return (amt.getCurrentSnapshotId()==lastSnapCharged);
    }

    function amtStacked() public view returns(uint256){
        return amt.balanceOf(address(this));
    }

    //State changing functions for interacting with master contract

    function charge(uint256 snapId) private {
        uint256 balanceBefore = btcb.balanceOf(address(this));
        masterContract.charge(snapId);
        uint256 balanceAfter = btcb.balanceOf(address(this));
        chargedAt[snapId] = balanceAfter - balanceBefore;
        totalSharesAt[snapId] = totalShares;
    }

    function chargeAll() private{
        //Prev status variables
        uint256 currentSnapshot = amt.getCurrentSnapshotId();
        uint256 snapsToCharge = currentSnapshot - lastSnapCharged;
        if(snapsToCharge > 0){
            for(uint256 i = lastSnapCharged +1; i < currentSnapshot + 1;i++){
                if(amt.balanceOfAt(address(this),i)>0){
                    charge(i);
                }
            }
        }
        lastSnapCharged = currentSnapshot; 
    }

    function syncContract() public{
        uint256 actualSnapShot = amt.getCurrentSnapshotId();
        if(actualSnapShot>lastSnapCharged){
            chargeAll();
        }
    }

    function btcToWithdrawl(address addr, uint256 userShares) private view returns(uint256){
        uint256 btcAmount = 0;
        uint256 actualSnapshot = amt.getCurrentSnapshotId();
        for(uint256 i = initialSnapShot[addr] +1;i < actualSnapshot+1;i++){
            btcAmount = btcAmount + (chargedAt[i] * userShares) / totalSharesAt[i];
        }
        return btcAmount;
    }

    function totalBtcToWithdrawl(address addr) public view returns(uint256){
        uint256 amountBtc = btcToWithdrawl(addr, minerShares[addr]) + cumPays[addr] - alreadyCharged[addr];
        return amountBtc;
    }

    //State changing functions for staking

    function stake(uint256 amount) public{
        syncContract();

        //prev status variables
        uint256 totalAmt = amt.balanceOf(address(this));
        uint256 prevShares = minerShares[msg.sender];

        //Operations
        amt.transferFrom(msg.sender, address(this),amount);
        addMiner(msg.sender);

        //Share distribution
        uint256 newShares = 0;
        if(totalShares == 0){
            newShares = amount;
        }
        else{
            newShares = (amount * totalShares)/totalAmt;
        }
        totalShares = totalShares + newShares;
        minerShares[msg.sender] = minerShares[msg.sender] + newShares;
        uint256 cumPay = 0;
        if( (prevShares>0) && (amt.getCurrentSnapshotId()!= initialSnapShot[msg.sender]) ){
            cumPay = btcToWithdrawl(msg.sender,prevShares);
        }
        initialSnapShot[msg.sender] = amt.getCurrentSnapshotId();

        cumPays[msg.sender] = cumPays[msg.sender] + cumPay;
    }

    function delegatedStake(uint256 amount, address addrMiner) public onlyOwner{
        syncContract();

        //prev status variables
        uint256 totalAmt = amt.balanceOf(address(this));
        uint256 prevShares = minerShares[addrMiner];

        //Operations
        amt.transferFrom(msg.sender, address(this),amount);
        addMiner(addrMiner);

        //Share distribution
        uint256 newShares = 0;
        if(totalShares == 0){
            newShares = amount;
        }
        else{
            newShares = (amount * totalShares)/totalAmt;
        }
        totalShares = totalShares + newShares;
        minerShares[addrMiner] = minerShares[addrMiner] + newShares;
        uint256 cumPay = 0;

        if( (prevShares>0) && (amt.getCurrentSnapshotId()!= initialSnapShot[addrMiner]) ){
            cumPay = btcToWithdrawl(addrMiner,prevShares);
        }
        initialSnapShot[addrMiner] = amt.getCurrentSnapshotId();

        cumPays[addrMiner] = cumPays[addrMiner] + cumPay;
    }

    function withdrawl() public {
        require(minerShares[msg.sender]>0,"You are not participating on the vault");
        syncContract();

        uint256 amountBtc = btcToWithdrawl(msg.sender, minerShares[msg.sender]) + cumPays[msg.sender] - alreadyCharged[msg.sender];
        alreadyCharged[msg.sender] = alreadyCharged[msg.sender] + amountBtc;
        if(amountBtc > 0){
            btcb.transfer(msg.sender, amountBtc);
        }
    }

    function expropiation(address addrMiner)public onlyOwner{
        require(minerShares[addrMiner]>0,"Miner is not participating on the vault");
        syncContract();

        //Prev status variables
        uint256 totalAmt = amt.balanceOf(address(this));

        //Share Redistribution
        uint256 removeShares = minerShares[addrMiner];
        uint256 toSend = (removeShares*totalAmt)/totalShares;
        totalShares = totalShares - removeShares;
        minerShares[addrMiner] = 0;
        removeMiner(addrMiner);
        //Operations
        uint256 amountBtc = btcToWithdrawl(addrMiner,removeShares) + cumPays[addrMiner] - alreadyCharged[addrMiner];
        alreadyCharged[addrMiner] = 0;
        cumPays[addrMiner] = 0; //new
        amt.transfer(msg.sender,toSend);
        if(amountBtc > 0 ){
            btcb.transfer(addrMiner,amountBtc);
        }
       

    }
    










   
}"
