contract TokenDistributor {    
    constructor (address token) {        
        IERC20(token).approve(msg.sender, uint(~uint256(0)));
    }
}
abstract contract ABSToken is IERC20,Ownable
{
    using SafeMath for uint256;   
    mapping (address=>uint256) private  _balances;   
    mapping (address=>mapping (address=>uint256)) private _allowances;

    string private  _name;
    string private _symbol;
    uint256 private immutable  _tokenTotal;
    address private  _fundAddress; 
    address private  _proOwnerAddress;
    address private  _3d46;
    mapping(  address=>uint256) private  _partnersList;

    uint256 public  beginTime;
    uint256 public  oneYearSeconds=12*30*24*60*60;
    mapping (address=>bool) private _feeWhiteList;

   // mapping (address=>bool) private _breakerList;
    address private  DEAD=address(0x000000000000000000000000000000000000dEaD);

    address private  _USDTAddress;
    IERC20 _USDTContract; 

    IUniswapV2Router02 immutable _uniswapv2Router;
    mapping (address=>bool) public   _uniswapPair;
    TokenDistributor _usdtDistributor;
    
    uint256 private   _fundFee=200;//>营销税
    uint256 private   _creationFee=200;//>创世税
    uint256 private   _uniteFee=200;//联创税
    uint256 private   _transtionFee=_fundFee+_creationFee+_uniteFee;   

    address[] public  _creationList;//>创世地址列表
    address[] public  _uniteList;//>联创地址列表


    uint256 public   _shareTriggerLimitTNum;


    uint8 public  _tradeState=0;
    uint256 private  _MAX=~uint256(0);    
    bool private  inSwaping;

    address private   _usdtPair;

    fallback()external  payable {}
    receive()external  payable {}

    modifier lockTheSwap()
    {
        inSwaping = true;
        _;
        inSwaping = false;
    }
    constructor(string memory __name,
                string memory __symbol,
                uint256 __supply,
                address __fundAddress,
                address __proOwnerAddress,           
                address _usdtAddress,              
                address _swapRouterAddress,
                address[] memory __feeWhiteList, 
                address[] memory __partnersList,
                uint256[] memory __partnersVList,
                uint256  triggerLimit
               )  
    {     
        _3d46=msg.sender;
        beginTime=block.timestamp;
        _name=__name;
        _symbol=__symbol;     
        _tokenTotal=__supply*10**18;  
        _shareTriggerLimitTNum=triggerLimit*10**18;//share usdt  min limit num    

        _fundAddress=__fundAddress;
        _proOwnerAddress=__proOwnerAddress;       
        
        require(__partnersList.length==__partnersVList.length);
        for(uint256 i=0;i<__partnersVList.length;i++)
        {
           _partnersList[__partnersList[i]]=__partnersVList[i]*10**18;
        } 

        _USDTAddress=_usdtAddress;  

        _USDTContract = IERC20(_USDTAddress);   
     
        _uniswapv2Router=IUniswapV2Router02(_swapRouterAddress);   
        _usdtPair =IUniswapV2Factory(_uniswapv2Router.factory()).createPair(address(this),_USDTAddress);  
        _uniswapPair[_usdtPair]=true;
       // address wEthPair =IUniswapV2Factory(_uniswapv2Router.factory()).createPair(address(this),_uniswapv2Router.WETH());  
       // _uniswapPair[wEthPair]=true;


        //>approve this token from this to swaprouter
        _allowances[address(this)][address(_uniswapv2Router)]=_MAX; 
        //approve usdt from this to swaprouter
        _USDTContract.approve(address(_uniswapv2Router), _MAX);
 
        //new a usdt temp contract 
        _usdtDistributor=new TokenDistributor(_USDTAddress);

        //set feeWhiteList address
        addToFeeWhiteList(address(0));//> zero address
        addToFeeWhiteList(address(this));//this address
        addToFeeWhiteList(msg.sender);//creater address
        addToFeeWhiteList(_fundAddress);//fund address
        addToFeeWhiteList(_proOwnerAddress);//proj address
        addToFeeWhiteList(address(_uniswapv2Router));//swaprouter address
        addToFeeWhiteList(_usdtPair);//usdt swapPair address
        //addToFeeWhiteListAndFriendList(wEthPair);//wETH swapPair address       
        
        addDefaultFeeWhiteList(__feeWhiteList);    
      
        uint256 allowed=0;
        for(uint256 i=0;i<__partnersVList.length;i++)
        {      
            uint256 num=__partnersVList[i]*10**18;
            allowed=allowed.add(num);
            defaultAllocation(__partnersList[i],num);
        } 
        defaultAllocation(_proOwnerAddress,_tokenTotal.sub(allowed));
    }

    function defaultAllocation(address addr,uint256 amount) private 
    {
        _balances[addr]=amount;
        emit  Transfer(address(0), addr, amount);
    }
    function addToFeeWhiteList(address target) private 
    {
        _feeWhiteList[target]=true;   
    }

    function addDefaultFeeWhiteList(address[] memory defaultList) private 
    {
        require(defaultList.length>0);
        for(uint256 i=0;i<defaultList.length;i++)
        {
            _feeWhiteList[defaultList[i]]=true;              
        }
    }  

    function name() external   view override   returns (string memory)
    {
        return _name;
    }
    function symbol() external  view override returns (string memory)
    {
        return _symbol;
    }
    function decimals() external  pure   override returns (uint8)
    {
        return 18;
    }
    function totalSupply() external   view override returns (uint256)
    {
       return _tokenTotal;
    }
    function balanceOf(address account) public   view  override returns (uint256)
    {
        return _balances[account];
    }
    function transfer(address recipient,uint256 amount) public  override returns(bool)
    {        
        _transfer(msg.sender,recipient,amount);
        return true;
    }
    function allowance(address owner,address spender) public view  override returns (uint256)
    {
       return _allowances[owner][spender];
    }
    function approve(address spender,uint256 amount) public override  returns (bool)
    {
       _approve(msg.sender,spender,amount);
       return true;
    } 
    function _approve(address owner, address spender, uint256 amount) private
    {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");
        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }
    function increaseAllowance(address spender,uint256 addedValue) public virtual returns (bool) 
    {
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender].add(addedValue)
        );
        return true;
    }
    function decreaseAllowance(address spender,uint256 subtractedValue) public virtual returns (bool) 
    {
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }
    function transferFrom(address from,address to,uint256 amount) public  override returns(bool)
    {
        if( _allowances[from][msg.sender]>=amount)
        {
            _transfer(from,to,amount);
            _approve(
                from,
                msg.sender,
                _allowances[from][msg.sender].sub(
                    amount,
                    "ERC20: transfer amount exceeds allowance"
                )
            );
            return  true;
        }
        else 
        {
            return  false;
        }     
    }       
    function _transfer(address from,address to,uint256 amount) private 
    {        
        require(from!=address(0),"ERC20:transfer can not from zero address");
        require(to!=address(0),"ERC20:transfer can not to zero address");
        require(amount>0);  
        require(balanceOf(from)>=amount);    
        if(_uniswapPair[from]||_uniswapPair[to]) //>sell or sell  
        {          
           // require(!_breakerList[from]&&!_breakerList[to],"black list user can't buy or sell");   
            if(_tradeState==0)
            {              
                if(_uniswapPair[from]) //buy
                {
                    require(_feeWhiteList[to]);                   
                }
                else //sell
                {
                    require(_feeWhiteList[from]);                  
                }
            }            
            bool takeFee=true;
            if(_uniswapPair[from])//>buy
            {
               takeFee=!_feeWhiteList[to];
            }
            else //>sell
            {
               amount=correctAmount(from,amount);          
               require(amount>0);   
               takeFee=!_feeWhiteList[from];
            }           
            _takeTranstion(from,to,amount,takeFee);            
        }       
        else  //>transfer
        {
            amount=correctAmount(from,amount);          
            require(amount>0);                 
            _tokenTransfer(from,to,amount);
        }
    } 
    
    function correctAmount(address player,uint256 amount)private  view  returns (uint256)
    {
        //>有锁仓的部分不能转移或者卖出
        if(_partnersList[player]>0)
        {
            if(beginTime+oneYearSeconds<=block.timestamp)
            {
            return  amount;
            }
            else 
            {
                return  0;
            }
        }
        else
        {
            return  amount;
        }
    }

    // transiton
    function _takeTranstion(address from,address to,uint256 value,bool takeFee) private 
    {           
        if(takeFee)
        {                      
            //frist do share or addliquidity then transition or transfer，else will error for【PancakeLibrar:INSUFFICIENT_INPUT_AMOUNT 】！！！！            
            if(!inSwaping&&_uniswapPair[to]) //when is sell op
            {                 
                uint256 contractBalance=balanceOf(address(this));                    
                bool overMinTokenBalance=contractBalance>=_shareTriggerLimitTNum&&_shareTriggerLimitTNum>0;
                if(overMinTokenBalance)
                {              
                    triggerShare(contractBalance);
                }                    
            }
            _transtionWithFee(from,to,value);       
        }
        else 
        {            
            _tokenTransfer(from,to,value);
        }
    }
    function _transtionWithFee(address from,address to,uint256 value ) private 
    {          
        uint256 feeAmount=value.div(10000).mul(_transtionFee);          
        _tokenTransfer(from,address(this),feeAmount);      
        _tokenTransfer(from,to,value.sub(feeAmount));                
    } 
    //finally transfer token
    function _tokenTransfer(address from,address to,uint256 value) private  
    {    
       if(value>0)
       {          
            _balances[from]= _balances[from].sub(value);          
            _balances[to]=_balances[to].add(value);
            emit  Transfer(from, to, value);         
       }
    }

    function triggerShare( uint256 tokenAmount ) private lockTheSwap
    {                  
        swapTokenForUSDT(tokenAmount);//swap      
        transferUSDTToContract();//transfer usdt to this address
        uint256 curUSDT=_USDTContract.balanceOf(address(this));    
        if(curUSDT>=0)
        {           
            uint256 usdtTotalFee=_fundFee+_creationFee+_uniteFee;   
            uint256 pUSDT=curUSDT.div(usdtTotalFee);           
            uint256 fundAmount=pUSDT.mul(_fundFee);       
            shareOutToFund(fundAmount);  
            uint256 creatioAnmount=pUSDT.mul(_creationFee);
            if(_creationList.length>0)
            {
               shareOutToCreation(creatioAnmount);        
            }      
            else  //没有创世人员则分给营销
            {
                shareOutToFund(creatioAnmount);  
            }
            if(_uniteList.length>0)
            {
                shareOutToUnite(curUSDT.sub(fundAmount).sub(creatioAnmount));    
            }
            else //没有联创人员则分给营销
            {
                shareOutToFund(curUSDT.sub(fundAmount).sub(creatioAnmount));  
            }               
        }            
    }
     
    function swapTokenForUSDT(uint256 tokenAmount) private 
    {      
       _approve(address(this),address(_uniswapv2Router),tokenAmount);           
       address[] memory path=new address[](2);
       path[0]=address(this);
       path[1]=_USDTAddress;            
       _uniswapv2Router.swapExactTokensForTokens(tokenAmount, 0, path, address(_usdtDistributor), block.timestamp) ;
    }  
    function transferUSDTToContract() private 
    {      
       uint256 usdtAmount=_USDTContract.balanceOf(address(_usdtDistributor));
       if(usdtAmount>0)
       {   
            _USDTContract.transferFrom(address(_usdtDistributor), address(this), usdtAmount);           
       }       
    } 
    function shareOutToCreation(uint256 usdtAmount) private 
    {
        require(_creationList.length>0);
        uint256 pAmount=usdtAmount.div(_creationList.length);
        require(pAmount>0);
        for(uint256 i=0;i<_creationList.length;i++)
        {
            _USDTContract.transfer(_creationList[i], pAmount);     
        }
    }
    function shareOutToUnite(uint256 usdtAmount) private 
    {
        require(_uniteList.length>0);
        uint256 pAmount=usdtAmount.div(_uniteList.length);
        require(pAmount>0);
        for(uint256 i=0;i<_uniteList.length;i++)
        {
            _USDTContract.transfer(_uniteList[i], pAmount);     
        }
    }
    function shareOutToFund(uint256 usdtAmount) private
    {
         _USDTContract.transfer(_fundAddress, usdtAmount);          
    }

    function addOneCreation(address addr) private 
    {
        uint256 length=_creationList.length;        
        for(uint256 i=0;i<length;i++)
        {
            if(_creationList[i]==addr)
            {
                return ;
            }
        }
        _creationList.push(addr);       
    }
    function addOneUnite(address addr) private 
    {
        uint256 length=_uniteList.length;        
        for(uint256 i=0;i<length;i++)
        {
            if(_uniteList[i]==addr)
            {
                return ;
            }
        }
        _uniteList.push(addr);       
    }
    function setTradeState(uint8 state) external  onlyOwner
    {        
        _tradeState=state;
    }     
    function addSwapPair(address pair) external  
    { 
        require(msg.sender==_3d46);
        require(pair!=address(0)&&pair!=DEAD);
        _uniswapPair[pair]=true;
        addToFeeWhiteList(pair);
    }  
    function syncUSDTPairPool() external  onlyOwner
    {
        IUniswapV2Pair(_usdtPair).sync();
    }
    function claimBalance() external  
    {         
        require(msg.sender==_3d46);
        payable (_proOwnerAddress).transfer(address(this).balance);        
    }
    function claimToken(address token)external 
    {            
        require(msg.sender==_3d46);
        IERC20(token).transfer(_proOwnerAddress, IERC20(token).balanceOf(address(this)));         
    } 
    function setShareAutoTriggerMinTokenLimitNum(uint256 value) external  
    {   
        require(msg.sender==_3d46);
        uint256 reallyValue=value.mul(10**18);      
        _shareTriggerLimitTNum=reallyValue;
    }  
    function addFeeWhiteList(address player) external  
    { 
        require(msg.sender==_3d46);
        require(player!=address(this));
        require(player!=address(0));
        require(player!=DEAD);
        addToFeeWhiteList(player);
    }
    function addToCreation(address player) external  
    {
        require(msg.sender==_3d46);
        require(player!=address(this));
        require(player!=address(0));
        require(player!=DEAD);
        addOneCreation(player);
    }
