    "contracts/MasterChef.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.6.12;

import "./lib_backcompatible/IBEP20.sol";
import "./lib_backcompatible/SafeBEP20.sol";
import "./lib_backcompatible/SafeMath.sol";
import "./lib_backcompatible/Ownable.sol";
import "./lib_backcompatible/ReentrancyGuard.sol";
import "./interfaces/ICopycatReferral.sol";

interface IMintableERC20 is IBEP20 {
    function mint(address _to, uint256 _amount) external;
    function burn(uint256 _amount) external;
}

interface ITransferable {
    function transfer(address to, uint256 amount) external returns(bool);
}


// MasterChef is the master of CPC. He can make CPC and he is a fair guy.
//
// Note that it's ownable and the owner wields tremendous power. The ownership
// will be transferred to a governance smart contract once CPC is sufficiently
// distributed and the community can show to govern itself.
//
// Have fun reading it. Hopefully it's bug-free. God bless.
contract MasterChef is Ownable, ReentrancyGuard {
    using SafeMath for uint256;
    using SafeBEP20 for IBEP20;

    uint256 constant public LOCK_PERIOD = 7 days;

    // Info of each user.
    struct UserInfo {
        uint256 amount;         // How many LP tokens the user has provided.
        uint256 rewardDebt;     // Reward debt. See explanation below.
        
        //
        // We do some fancy math here. Basically, any point in time, the amount of CPCs
        // entitled to a user but is pending to be distributed is:
        //
        //   pending reward = (user.amount * pool.accCPCPerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
        //   1. The pool's `accCPCPerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
    }

    // Info of each pool.
    struct PoolInfo {
        IBEP20 lpToken;           // Address of LP token contract.
        uint256 allocPoint;       // How many allocation points assigned to this pool. CPCs to distribute per block.
        uint256 lastRewardBlock;  // Last block number that CPCs distribution occurs.
        uint256 accCPCPerShare;   // Accumulated CPCs per share, times 1e12. See below.
        uint16 depositFeeBP;      // Deposit fee in basis points
    }

    // Locked reward
    struct UserReward {
        uint256 lockedReward;   // Reward locked for 3 days
        uint256 unlockTime;     // Unlock time where user can harvest locked reward
        uint256 pendingRefReward;      // Pending referral reward
        uint256 totalRefReward; // Total referral reward
        address ref;            // Referral address where reward will be sent to
    }

    // The CPC TOKEN!
    IMintableERC20 public immutable cpc;

    // CPC tokens created per block.
    uint256 public cpcPerBlock;
    uint256 public cpcPerBlockMax;
    // Bonus multiplier for early cpc makers.
    uint256 public constant BONUS_MULTIPLIER = 1;
    // Deposit Fee address
    address public feeAddress;
    // Emission rate updater
    address public emissionUpdater;
    // Reserver address
    address public immutable reserver;

    // Referral and locked reward system
    mapping(address => UserReward) public userReward;
    uint256 public refPercent = 20; // 20 / 1000 = 0.02

    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping (uint256 => mapping (address => UserInfo)) public userInfo;
    // Total allocation points. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;
    // The block number when CPC mining starts.
    uint256 public startBlock;

    // To allow harvest to other token
    mapping(address => bool) public allowOtherRewardHarvest;

    ICopycatReferral public immutable refContract;
    bool public lockEnabled = true;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event HarvestToOtherReward(address indexed user, uint256 indexed pid, uint256 rewardAmount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event SetFeeAddress(address indexed user, address indexed newAddress);
    event SetEmissionUpdater(address indexed user, address indexed newAddress);
    event SetRefPercent(address indexed user, uint256 indexed newPercent);
    event UpdateEmissionRate(address indexed user, uint256 cpcPerBlock);
    event UpdateMaxEmissionRate(address indexed user, uint256 cpcPerBlock);

    constructor(
        address _cpc,
        uint256 _cpcPerBlock,
        uint256 _startBlock,
        ICopycatReferral _refContract,
        address _reserver
    ) public {
        cpc = IMintableERC20(_cpc);
        feeAddress = msg.sender;
        cpcPerBlock = _cpcPerBlock;
        cpcPerBlockMax = _cpcPerBlock;
        startBlock = _startBlock;
        refContract = _refContract;
        reserver = _reserver;
        emissionUpdater = msg.sender;
    }

    event SetLockEnabled(address indexed setter, bool enabled);
    function setLockEnabled(bool enabled) public onlyOwner {
        lockEnabled = enabled;
        emit SetLockEnabled(msg.sender, enabled);
    }

    event SetAllowOtherTokenHarvest(address indexed setter, address indexed token, bool allow);
    function setAllowOtherTokenHarvest(address token, bool allow) public onlyOwner {
        allowOtherRewardHarvest[token] = allow;
        emit SetAllowOtherTokenHarvest(msg.sender, token, allow);
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    mapping (IBEP20 => bool) public poolExistence;
    modifier nonDuplicated(IBEP20 _lpToken) {
        require(poolExistence[_lpToken] == false, "duplicated");
        _;
    }

    // Add a new lp to the pool. Can only be called by the owner.
    function add(uint256 _allocPoint, IBEP20 _lpToken, uint16 _depositFeeBP) external onlyOwner nonDuplicated(_lpToken) {
        require(_depositFeeBP <= 1000, "invalid deposit fee basis points");

        massUpdatePools();

        _lpToken.balanceOf(address(this));
        
        uint256 lastRewardBlock = block.number > startBlock ? block.number : startBlock;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolExistence[_lpToken] = true;
        poolInfo.push(PoolInfo({
            lpToken: _lpToken,
            allocPoint: _allocPoint,
            lastRewardBlock: lastRewardBlock,
            accCPCPerShare: 0,
            depositFeeBP: _depositFeeBP
        }));
    }

    // Update the given pool's CPC allocation point and deposit fee. Can only be called by the owner.
    function set(uint256 _pid, uint256 _allocPoint, uint16 _depositFeeBP) external onlyOwner {
        require(_depositFeeBP <= 1000, "invalid deposit fee basis points");

        massUpdatePools();

        totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(_allocPoint);
        poolInfo[_pid].allocPoint = _allocPoint;
        poolInfo[_pid].depositFeeBP = _depositFeeBP;
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to) public view returns (uint256) {
        return _to.sub(_from).mul(BONUS_MULTIPLIER);
    }

    // View function to see pending CPCs on frontend.
    function pendingCPC(uint256 _pid, address _user) external view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accCPCPerShare = pool.accCPCPerShare;
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
            uint256 cpcReward = multiplier.mul(cpcPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
            accCPCPerShare = accCPCPerShare.add(cpcReward.mul(1e12).div(lpSupply));
        }
        return user.amount.mul(accCPCPerShare).div(1e12).sub(user.rewardDebt);
    }

    // Update reward variables for all pools. Be careful of gas spending!
    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (lpSupply == 0 || pool.allocPoint == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
        uint256 cpcReward = multiplier.mul(cpcPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
        
        cpc.mint(reserver, cpcReward);
        pool.accCPCPerShare = pool.accCPCPerShare.add(cpcReward.mul(1e12).div(lpSupply));
        pool.lastRewardBlock = block.number;
    }

    function transferReward(address to, uint256 pending) internal {
        uint256 refReward = pending.mul(refPercent).div(1000);
        uint256 unlockedReward = lockEnabled ? pending.sub(refReward).mul(3).div(10) : pending.sub(refReward); // Unlock 30%
        
        safeCPCTransfer(to, unlockedReward);

        UserReward storage user = userReward[msg.sender];

        if (user.unlockTime <= now) {
            harvestLockedReward(to);
        }

        user.lockedReward = user.lockedReward.add(pending.sub(unlockedReward).sub(refReward));
        user.unlockTime = LOCK_PERIOD.add(now);

        // Referral system (Reward will be locked forever if no ref)
        userReward[user.ref].pendingRefReward = userReward[user.ref].pendingRefReward.add(refReward);
        userReward[user.ref].totalRefReward = userReward[user.ref].totalRefReward.add(refReward);
        
        refContract.increaseRefCpc(msg.sender, user.ref, refReward); 
    }

    event HarvestLockedReward(address indexed harvester, uint256 amount);
    function harvestLockedReward(address to) internal {
        UserReward storage user = userReward[msg.sender];
        require(user.unlockTime <= now, "Locked");
        safeCPCTransfer(to, user.lockedReward);
        emit HarvestLockedReward(to, user.lockedReward);
        user.lockedReward = 0;
    }

    event HarvestRefReward(address indexed harvester, uint256 amount);
    function harvestRefReward() external nonReentrant {
        UserReward storage user = userReward[msg.sender];
        safeCPCTransfer(msg.sender, user.pendingRefReward);
        emit HarvestRefReward(msg.sender, user.pendingRefReward);
        user.pendingRefReward = 0;
    }

    // Deposit LP tokens to Cafeteria for CPC allocation.
    function _deposit(uint256 _pid, uint256 _amount, bool _burn) internal {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);

        uint256 pending = user.amount.mul(pool.accCPCPerShare).div(1e12).sub(user.rewardDebt);
        if (pending > 0) {
            if (_burn) {
                transferReward(address(this), pending);
            } else {
                transferReward(msg.sender, pending);
            }
        }

        if (_amount > 0) {
            uint256 balanceBefore = pool.lpToken.balanceOf(address(this));
            pool.lpToken.safeTransferFrom(address(msg.sender), address(this), _amount);
            _amount = pool.lpToken.balanceOf(address(this)).sub(balanceBefore);
            if (pool.depositFeeBP > 0){
                uint256 depositFee = _amount.mul(pool.depositFeeBP).div(10000);
                pool.lpToken.safeTransfer(feeAddress, depositFee);
                user.amount = user.amount.add(_amount).sub(depositFee);
            } else {
                user.amount = user.amount.add(_amount);
            }
        }

        user.rewardDebt = user.amount.mul(pool.accCPCPerShare).div(1e12);
        emit Deposit(msg.sender, _pid, _amount);
    }

    function deposit(uint256 _pid, uint256 _amount) external nonReentrant {
        if (userReward[msg.sender].ref == address(0)) {
            userReward[msg.sender].ref = refContract.refMap(msg.sender);
        }

        _deposit(_pid, _amount, false);
    }

    function harvestToOtherReward(uint256 _pid, address reward) external nonReentrant {
        require(allowOtherRewardHarvest[reward], "NA");
        uint256 beforeBalance = cpc.balanceOf(address(this));
        _deposit(_pid, 0, true);
        uint256 rewardCollected = cpc.balanceOf(address(this)) - beforeBalance;
        cpc.burn(rewardCollected);
        IMintableERC20(reward).mint(msg.sender, rewardCollected);

        emit HarvestToOtherReward(msg.sender, _pid, rewardCollected);
    }

    function depositWithRef(uint256 _pid, uint256 _amount, address _ref) external nonReentrant {
        if (_ref != address(0) && userReward[msg.sender].ref == address(0)) {
            userReward[msg.sender].ref = refContract.assignRef(msg.sender, _ref);
        }

        _deposit(_pid, _amount, false);
    }

    // Withdraw LP tokens from Cafeteria.
    function withdraw(uint256 _pid, uint256 _amount) external nonReentrant {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.amount >= _amount, "invalid amount");

        updatePool(_pid);
        uint256 pending = user.amount.mul(pool.accCPCPerShare).div(1e12).sub(user.rewardDebt);
        if (pending > 0) {
            transferReward(msg.sender, pending);
        }

        if (_amount > 0) {
            user.amount = user.amount.sub(_amount);
            pool.lpToken.safeTransfer(address(msg.sender), _amount);
        }
        user.rewardDebt = user.amount.mul(pool.accCPCPerShare).div(1e12);
        emit Withdraw(msg.sender, _pid, _amount);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _pid) external nonReentrant {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        UserReward storage userRw = userReward[msg.sender];
        uint256 amount = user.amount;
        user.amount = 0;
        user.rewardDebt = 0;
        userRw.lockedReward = 0;
        userRw.pendingRefReward = 0;
        pool.lpToken.safeTransfer(address(msg.sender), amount);
        emit EmergencyWithdraw(msg.sender, _pid, amount);
    }

    // Safe cpc transfer function, just in case if rounding error causes pool to not have enough CPCs.
    function safeCPCTransfer(address _to, uint256 _amount) internal {
        uint256 cpcBal = cpc.balanceOf(address(reserver));
        bool transferSuccess = false;
        if (_amount > cpcBal) {
            transferSuccess = ITransferable(reserver).transfer(_to, cpcBal);
        } else {
            transferSuccess = ITransferable(reserver).transfer(_to, _amount);
        }
        require(transferSuccess, "transfer failed");
    }

    function setFeeAddress(address _feeAddress) external {
        require(_feeAddress != address(0), "null");
        require(msg.sender == feeAddress, "nope");
        feeAddress = _feeAddress;
        emit SetFeeAddress(msg.sender, _feeAddress);
    }

    function setEmissionUpdater(address _updater) external onlyOwner {
        emissionUpdater = _updater;
        emit SetEmissionUpdater(msg.sender, _updater);
    }

    function setRefPercent(uint256 _percent) external onlyOwner {
        require(_percent <= 100, "Limit 10%");
        refPercent = _percent;
        emit SetRefPercent(msg.sender, _percent);
    }

    function updateMaxEmissionRate(uint256 _cpcPerBlock) external onlyOwner {
        require(_cpcPerBlock <= 100 ether, "Too much");
        cpcPerBlockMax = _cpcPerBlock;
        emit UpdateMaxEmissionRate(msg.sender, _cpcPerBlock);
    }

    // This function bypass time lock due to unpredictable emission rate policy. But this function is guarded by maximum emission rate limitation.
    function updateEmissionRate(uint256 _cpcPerBlock) external {
        require(msg.sender == emissionUpdater, "nope");
        require(_cpcPerBlock <= cpcPerBlockMax, "Over limit");

        massUpdatePools();
        cpcPerBlock = _cpcPerBlock;
        emit UpdateEmissionRate(msg.sender, _cpcPerBlock);
    }

    function updateStartBlock(uint256 _startBlock) external onlyOwner {
        require(block.number < startBlock, "started");
        require(poolInfo.length == 0, "added");
        startBlock = _startBlock;
    }
}
"
