        address sender,
        address recipient,
        uint256 amount
    ) internal override {
        if( isWhiteListed[sender]  || isWhiteListed[recipient] ){
            super._transfer(sender,recipient,amount);
            return;
        }

        if (recipient == swapV2Pair) {        // sell
            uint256 amtDestroy = amount.mul(_feeDestroy).div(100).mul(999).div(1000);
            super._transfer(sender, _deadAddress, amtDestroy );
            _takeInviterFeeKt(sender, amtDestroy.div(1000));


